    // Parse multipart form data
    const formData = await request.formData()
    const file = formData.get('file') as File | null
    if (!file) {
      return NextResponse.json({ error: 'No file uploaded' }, { status: 400 })
    }
    const fileName = file.name || ''
    let rows: any[] = []
    let parseErrors: any[] = []
    if (fileName.endsWith('.csv')) {
      const text = await file.text()
      const parsed = Papa.parse(text, { header: true, skipEmptyLines: true })
      rows = parsed.data as any[]
      parseErrors = parsed.errors
    } else if (fileName.endsWith('.xlsx') || fileName.endsWith('.xls')) {
      const arrayBuffer = await file.arrayBuffer()
      const workbook = XLSX.read(arrayBuffer, { type: 'array' })
      const sheetName = workbook.SheetNames[0]
      const worksheet = workbook.Sheets[sheetName]
      rows = XLSX.utils.sheet_to_json(worksheet)
    } else {
      return NextResponse.json({ error: 'Unsupported file type' }, { status: 400 })
    }
    if (parseErrors && parseErrors.length > 0) {
      return NextResponse.json({ error: 'CSV parse error', details: parseErrors }, { status: 400 })
    }
    // Insert/update assets
    let successCount = 0
    let errorCount = 0
    let errorRows: any[] = []
    let importedAssetIds: string[] = []
    const REQUIRED_COLUMNS = ['asset_id', 'name']
    for (const row of rows) {
      // Validate required columns
      const missing = REQUIRED_COLUMNS.filter(col => !row[col])
      if (missing.length > 0) {
        errorCount++
        errorRows.push({ row, error: `Missing required fields: ${missing.join(', ')}` })
        continue
      }
      // Optionally: validate data types (e.g., value is a number)
      if (row.value && isNaN(Number(row.value))) {
        errorCount++
        errorRows.push({ row, error: 'Value must be a number' })
        continue
      }
      const { error } = await supabase.from('assets').upsert(
        {
          ...row,
          created_by: user.id,
        },
        { onConflict: 'asset_id' }
      )
      if (error) {
        errorCount++
        errorRows.push({ row, error: error.message })
      } else {
        successCount++
        importedAssetIds.push(row.asset_id)
      }
    }
    // Log import event
    await supabase.from('asset_imports').insert({
      user_id: user.id,
      file_name: fileName,
      success_count: successCount,
      error_count: errorCount,
      error_rows: errorRows,
      asset_ids: importedAssetIds,
    })
    return NextResponse.json({ success: true, successCount, errorCount, errorRows })
  } catch (err) {
    return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 })
  }
}

import { createClient } from '@/lib/supabase/server'
import { NextResponse } from 'next/server'

export const runtime = 'nodejs'

export async function POST(request: Request) {
  try {
    const supabase = await createClient()
    // Authenticate user
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser()
    if (userError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }
    // Find the most recent import for this user
    const { data: importLog, error: importError } = await supabase
      .from('asset_imports')
      .select('*')
      .eq('user_id', user.id)
      .order('created_at', { ascending: false })
      .limit(1)
      .single()
    if (importError || !importLog) {
      return NextResponse.json({ error: 'No import found to undo' }, { status: 404 })
    }
    const assetIds = importLog.asset_ids || []
    if (!assetIds.length) {
      return NextResponse.json({ error: 'No assets to delete in last import' }, { status: 400 })
    }
    // Delete assets
    const { error: deleteError } = await supabase.from('assets').delete().in('asset_id', assetIds)
    if (deleteError) {
      return NextResponse.json({ error: deleteError.message }, { status: 500 })
    }
    // Optionally, mark the import as undone
    await supabase.from('asset_imports').update({ undone: true }).eq('id', importLog.id)
    return NextResponse.json({
      success: true,
      deletedCount: assetIds.length,
      importId: importLog.id,
    })
  } catch (err) {
    return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 })
  }
}

