// API endpoints for role management

import { NextRequest, NextResponse } from 'next/server'
import { RoleService } from '@/lib/services/role-service'
import { withPermissions, PERMISSIONS } from '@/lib/middleware/role-validation'
import { createClient } from '@/lib/supabase/server'

const roleService = new RoleService()

// GET /api/roles - Get all roles for tenant
export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const tenantId = searchParams.get('tenant_id')
    const includeInactive = searchParams.get('include_inactive') === 'true'

    if (!tenantId) {
      return NextResponse.json({ error: 'Tenant ID is required' }, { status: 400 })
    }

    // Verify user has permission to read roles
    const supabase = await createClient()
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const roles = await roleService.getRoles(tenantId, includeInactive)

    return NextResponse.json({
      success: true,
      roles,
    })
  } catch (error: any) {
    console.error('Error getting roles:', error)
    return NextResponse.json({ error: error.message || 'Failed to get roles' }, { status: 500 })
  }
}

// POST /api/roles - Create new role
export const POST = withPermissions([PERMISSIONS.ROLES.CREATE])(async (request: NextRequest) => {
  try {
    const body = await request.json()
    const { tenant_id, ...roleData } = body

    if (!tenant_id) {
      return NextResponse.json({ error: 'Tenant ID is required' }, { status: 400 })
    }

    // Get user context from middleware
    const userContext = (request as any).userContext
    if (!userContext) {
      return NextResponse.json({ error: 'User context not found' }, { status: 401 })
    }

    const role = await roleService.createRole(tenant_id, roleData, userContext.userId)

    return NextResponse.json({
      success: true,
      role,
    })
  } catch (error: any) {
    console.error('Error creating role:', error)
    return NextResponse.json({ error: error.message || 'Failed to create role' }, { status: 500 })
  }
})

import { storeApiKey } from '@/lib/api-key'
import { createClient } from '@/lib/supabase/server'
import { NextResponse } from 'next/server'
import { isAuthorized } from '@/lib/rbac/utils'
import { Permission } from '@/lib/rbac/types'

export async function GET() {
  const supabase = await createClient()
  const {
    data: { user },
  } = await supabase.auth.getUser()
  if (!user) return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
  // Get tenant_id from user profile
  const { data: profile } = await supabase
    .from('profiles')
    .select('tenant_id')
    .eq('id', user.id)
    .single()
  if (!profile?.tenant_id) return NextResponse.json({ error: 'No tenant' }, { status: 400 })
  const { data, error } = await supabase
    .from('api_keys')
    .select('id, name, created_at, revoked, user_id')
    .eq('tenant_id', profile.tenant_id)
    .order('created_at', { ascending: false })
  if (error) return NextResponse.json({ error: error.message }, { status: 500 })
  return NextResponse.json({ keys: data })
}

export async function POST(request: Request) {
  const supabase = await createClient()
  const {
    data: { user },
  } = await supabase.auth.getUser()
  if (!user) return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
  // RBAC permission check
  const authorized = await isAuthorized(user.id, 'manage:settings' as Permission)
  if (!authorized) {
    return NextResponse.json({ error: 'Insufficient permissions' }, { status: 403 })
  }
  const { name } = await request.json()
  // Get tenant_id from user profile
  const { data: profile } = await supabase
    .from('profiles')
    .select('tenant_id')
    .eq('id', user.id)
    .single()
  if (!profile?.tenant_id) return NextResponse.json({ error: 'No tenant' }, { status: 400 })
  try {
    const { apiKey } = await storeApiKey({ tenant_id: profile.tenant_id, user_id: user.id, name })
    return NextResponse.json({ apiKey })
  } catch (error: any) {
    return NextResponse.json(
      { error: error.message || 'Failed to create API key' },
      { status: 500 }
    )
  }
}

import { NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { randomBytes } from 'crypto'
import { isAuthorized } from '@/lib/rbac/utils'
import { logAuditEvent } from '@/lib/audit-log'
import { sendInvitationEmail } from '@/lib/notifications'

export async function POST(request: Request) {
  const supabase = await createClient()
  const { email, role = 'member', team_id } = await request.json()
  if (!email || !team_id) {
    return NextResponse.json({ error: 'Missing email or team_id' }, { status: 400 })
  }
  // Get the inviter (current user)
  const {
    data: { user },
  } = await supabase.auth.getUser()
  if (!user) return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
  // Check inviter's role in the team
  const { data: member } = await supabase
    .from('team_members')
    .select('role')
    .eq('team_id', team_id)
    .eq('user_id', user.id)
    .single()
  if (!member || !(await isAuthorized(user.id, 'manage:users'))) {
    return NextResponse.json({ error: 'Insufficient permissions' }, { status: 403 })
  }
  // Generate a unique token
  const token = randomBytes(32).toString('hex')
  // Insert invitation
  const { error, data: invitation } = await supabase
    .from('team_invitations')
    .insert({
      team_id,
      email,
      role,
      token,
      invited_by: user.id,
      status: 'pending',
    })
    .select()
    .single()
  if (error) {
    await logAuditEvent({
      user_id: user.id,
      action: 'invite_failed',
      entity: 'team_invitation',
      entity_id: undefined,
      details: { email, team_id, error: error.message },
      tenant_id: undefined,
    })
    return NextResponse.json({ error: error.message }, { status: 500 })
  }
  // Send invitation email
  try {
    // Get team name for email
    const { data: team } = await supabase.from('teams').select('name').eq('id', team_id).single()
    const inviteLink = `${process.env.APP_URL || 'http://localhost:3000'}/accept-invite?token=${token}`
    await sendInvitationEmail({
      to: email,
      inviteLink,
      teamName: team?.name || 'your team',
      inviterName: user.email || 'A team member',
    })
  } catch (emailError: any) {
    await logAuditEvent({
      user_id: user.id,
      action: 'invite_email_failed',
      entity: 'team_invitation',
      entity_id: invitation?.id,
      details: { email, team_id, error: emailError?.message },
      tenant_id: undefined,
    })
    return NextResponse.json({ error: 'Failed to send invitation email' }, { status: 500 })
  }
  // Audit log: invitation sent
  await logAuditEvent({
    user_id: user.id,
    action: 'invite_sent',
    entity: 'team_invitation',
    entity_id: invitation.id,
    details: { email, team_id, role },
    tenant_id: undefined,
  })
  return NextResponse.json({ success: true, token })
}

