              <div className="flex items-center justify-between">
                <CardTitle>Asset Details</CardTitle>
                <Button variant="ghost" size="sm" onClick={() => setSelectedAsset(null)}>

                </Button>
              </div>
            </CardHeader>
            <CardContent>
              {(() => {
                const asset = mockAssets.find(a => a.id === selectedAsset)
                if (!asset) return null
                return (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="text-sm font-medium text-gray-600">Asset ID</label>
                        <p className="text-lg font-semibold">{asset.asset_id}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Name</label>
                        <p className="text-lg">{asset.name}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Category</label>
                        <p className="text-lg capitalize">{asset.category.replace('-', ' ')}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Status</label>
                        <Badge variant={getStatusVariant(asset.status)} className="capitalize">
                          {asset.status}
                        </Badge>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Location</label>
                        <p className="text-lg">{asset.location}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Value</label>
                        <p className="text-lg font-semibold">{formatCurrency(asset.value)}</p>
                      </div>
                    </div>
                    <div className="flex space-x-2 pt-4">
                      <Button className="flex-1">
                        <Edit className="h-4 w-4 mr-2" />
                        Edit Asset
                      </Button>
                      <Button variant="outline">
                        <QrCode className="h-4 w-4 mr-2" />
                        Generate QR
                      </Button>
                      <Button variant="outline">
                        <Download className="h-4 w-4 mr-2" />
                        Export
                      </Button>
                    </div>
                  </div>
                )
              })()}
            </CardContent>
          </Card>
        </div>
      )}

      <Dialog
        open={deleteDialog.open}
        onOpenChange={open => setDeleteDialog({ open, asset: deleteDialog.asset })}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Delete Asset</DialogTitle>
          </DialogHeader>
          <div>
            Are you sure you want to delete <b>{deleteDialog.asset?.name}</b>?
          </div>
          <DialogFooter>
            <Button
              variant="destructive"
              onClick={() => deleteDialog.asset && handleDelete(deleteDialog.asset)}
            >
              Delete
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { useParams, useRouter } from 'next/navigation'
import { useAuth } from '@/components/auth/auth-provider'
import { Input } from '@/components/ui/input'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { useToast } from '@/hooks/use-toast'
import { Asset, getAsset } from '@/lib/asset-actions'

export default function EditAssetPage() {
  const params = useParams()
  const router = useRouter()
  const { user, loading: authLoading } = useAuth()
  const { toast } = useToast()
  const assetId = params.assetId as string

  const [asset, setAsset] = useState<Asset | null>(null)
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [form, setForm] = useState({
    name: '',
    description: '',
    category: '',
    status: 'active',
    location: '',
    purchase_value: '',
    purchase_date: '',
    manufacturer: '',
    model: '',
    serial_number: '',
    warranty_expiry: '',
    notes: '',
  })

  useEffect(() => {
    if (!authLoading && user && assetId) {
      fetchAsset()
    }
    // eslint-disable-next-line
  }, [user, authLoading, assetId])

  const fetchAsset = async () => {
    setLoading(true)
    setError(null)
    try {
      const result = await getAsset(assetId)
      if (result.error) {
        setError(result.error)
        toast({
          title: 'Error Loading Asset',
          description: result.error,
          variant: 'destructive',
        })
      } else {
        setAsset(result.data)
        setForm({
          name: result.data.name || '',
          description: result.data.description || '',
          category: result.data.category || '',
          status: result.data.status || 'active',
          location: result.data.location || '',
          purchase_value: result.data.purchase_value?.toString() || '',
          purchase_date: result.data.purchase_date || '',
          manufacturer: result.data.manufacturer || '',
          model: result.data.model || '',
          serial_number: result.data.serial_number || '',
          warranty_expiry: result.data.warranty_expiry || '',
          notes: result.data.notes || '',
        })
      }
    } catch (e) {
      setError('Failed to load asset')
      toast({
        title: 'Error Loading Asset',
        description: 'An unexpected error occurred while loading the asset',
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target
    setForm(prev => ({ ...prev, [name]: value }))
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setSaving(true)
    setError(null)
    try {
      const res = await fetch(`/api/assets/${assetId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...form,
          purchase_value: form.purchase_value ? Number(form.purchase_value) : null,
        }),
      })
      const data = await res.json()
      if (!res.ok) {
        setError(data.error || 'Failed to update asset')
        toast({
          title: 'Update Failed',
          description: data.error || 'Failed to update asset',
          variant: 'destructive',
        })
      } else {
        toast({
          title: 'Asset Updated',
          description: 'The asset has been updated successfully.',
        })
        router.push(`/asset/${assetId}`)
      }
    } catch (e) {
      setError('Failed to update asset')
      toast({
        title: 'Update Failed',
        description: 'An unexpected error occurred while updating the asset',
        variant: 'destructive',
      })
    } finally {
      setSaving(false)
    }
  }

  if (loading) return <div className="p-8">Loading...</div>
  if (error) return <div className="p-8 text-red-600">{error}</div>
  if (!asset) return <div className="p-8">Asset not found.</div>

  return (
    <div className="max-w-2xl mx-auto py-8">
      <Card>
        <CardHeader>
          <CardTitle>Edit Asset</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="name" className="block text-sm font-medium mb-1">
                Name
              </label>
              <Input
                id="name"
                name="name"
                placeholder="Asset Name"
                value={form.name}
                onChange={handleChange}
                required
              />
            </div>
            <div>
              <label htmlFor="description" className="block text-sm font-medium mb-1">
                Description
              </label>
              <Input
                id="description"
                name="description"
                placeholder="Description"
                value={form.description}
                onChange={handleChange}
              />
            </div>
            <div>
              <label htmlFor="category" className="block text-sm font-medium mb-1">
                Category
              </label>
              <Input
                id="category"
                name="category"
                placeholder="Category"
                value={form.category}
                onChange={handleChange}
              />
            </div>
            <div>
              <label htmlFor="status" className="block text-sm font-medium mb-1">
                Status
              </label>
              <Input
                id="status"
                name="status"
                placeholder="Status"
                value={form.status}
                onChange={handleChange}
              />
            </div>
            <div>
              <label htmlFor="location" className="block text-sm font-medium mb-1">
                Location
              </label>
              <Input
                id="location"
                name="location"
                placeholder="Location"
                value={form.location}
                onChange={handleChange}
              />
            </div>
            <div>
              <label htmlFor="purchase_value" className="block text-sm font-medium mb-1">
                Purchase Value
              </label>
              <Input
                id="purchase_value"
                name="purchase_value"
                placeholder="Purchase Value"
                value={form.purchase_value}
                onChange={handleChange}
                type="number"
              />
            </div>
            <div>
              <label htmlFor="purchase_date" className="block text-sm font-medium mb-1">
                Purchase Date
              </label>
              <Input
                id="purchase_date"
                name="purchase_date"
                placeholder="YYYY-MM-DD"
                value={form.purchase_date}
                onChange={handleChange}
                type="date"
              />
            </div>
            <div>
              <label htmlFor="manufacturer" className="block text-sm font-medium mb-1">
                Manufacturer
              </label>
              <Input
                id="manufacturer"
                name="manufacturer"
                placeholder="Manufacturer"
                value={form.manufacturer}
                onChange={handleChange}
              />
            </div>
            <div>
              <label htmlFor="model" className="block text-sm font-medium mb-1">
                Model
              </label>
              <Input
                id="model"
                name="model"
                placeholder="Model"
                value={form.model}
                onChange={handleChange}
              />
            </div>
            <div>
              <label htmlFor="serial_number" className="block text-sm font-medium mb-1">
                Serial Number
              </label>
              <Input
                id="serial_number"
                name="serial_number"
                placeholder="Serial Number"
                value={form.serial_number}
                onChange={handleChange}
              />
            </div>
            <div>
              <label htmlFor="warranty_expiry" className="block text-sm font-medium mb-1">
                Warranty Expiry
              </label>
              <Input
                id="warranty_expiry"
                name="warranty_expiry"
                placeholder="YYYY-MM-DD"
                value={form.warranty_expiry}
                onChange={handleChange}
                type="date"
              />
            </div>
            <div>
              <label htmlFor="notes" className="block text-sm font-medium mb-1">
                Notes
              </label>
              <Input
                id="notes"
                name="notes"
                placeholder="Notes"
                value={form.notes}
                onChange={handleChange}
              />
            </div>
            <div className="flex justify-end">
              <Button type="submit" disabled={saving}>
                {saving ? 'Saving...' : 'Save Changes'}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { useParams, useRouter } from 'next/navigation'
import { useAuth } from '@/components/auth/auth-provider'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
  DialogTrigger,
} from '@/components/ui/dialog'
import { Input } from '@/components/ui/input'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'
import { Calendar } from 'lucide-react'
import {
  Package,
  Loader2,
  AlertTriangle,
  Edit,
  Trash2,
  ArrowLeft,
  QrCode,
  MapPin,
  DollarSign,
  User,
  Settings,
  FileText,
  Tag,
  Building,
  Hash,
} from 'lucide-react'
import { useToast } from '@/hooks/use-toast'
import { Asset, getAsset, deleteAsset } from '@/lib/asset-actions'
import Link from 'next/link'
import MaintenanceCalendar from '@/components/maintenance-calendar'

export default function AssetDetailPage() {
  const params = useParams()
  const router = useRouter()
  const { user, loading: authLoading } = useAuth()
  const [asset, setAsset] = useState<Asset | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [deleting, setDeleting] = useState(false)
  const { toast } = useToast()
  const [customFields, setCustomFields] = useState<any[]>([])
  const [maintenance, setMaintenance] = useState<any[]>([])
  const [maintenanceLoading, setMaintenanceLoading] = useState(true)
  const [showMaintDialog, setShowMaintDialog] = useState(false)
  const [editingMaint, setEditingMaint] = useState<any | null>(null)
  const [maintForm, setMaintForm] = useState<any>({
    type: 'inspection',
    interval: 'monthly',
    next_due: '',
    notes: '',
  })
  const [maintenanceHistory, setMaintenanceHistory] = useState<any[]>([])
  const [historyLoading, setHistoryLoading] = useState(true)
  const [showHistoryDialog, setShowHistoryDialog] = useState(false)
  const [historyForm, setHistoryForm] = useState<any>({
    schedule_id: '',
    performed_at: '',
    notes: '',
  })

  const assetId = params.assetId as string

  useEffect(() => {
    if (!authLoading && user && assetId) {
      fetchAsset()
      fetchCustomFields()
      fetchMaintenance()
      fetchMaintenanceHistory()
    }
  }, [user, authLoading, assetId])

  const fetchAsset = async () => {
    try {
      setError(null)
      const result = await getAsset(assetId)

      if (result.error) {
        setError(result.error)
        toast({
          title: 'Error Loading Asset',
          description: result.error,
          variant: 'destructive',
        })
      } else {
        setAsset(result.data)
      }
    } catch (error) {
      console.error('Error fetching asset:', error)
      setError('Failed to load asset')
      toast({
        title: 'Error Loading Asset',
        description: 'An unexpected error occurred while loading the asset',
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const fetchCustomFields = async () => {
    try {
      const res = await fetch(`/api/assets/${assetId}/custom-fields`)
      const json = await res.json()
      setCustomFields(json.data || [])
    } catch (err) {
      // ignore
    }
  }

  const fetchMaintenance = async () => {
    setMaintenanceLoading(true)
    const res = await fetch(`/api/assets/${assetId}/maintenance`)
    const json = await res.json()
    setMaintenance(json.data || [])
    setMaintenanceLoading(false)
  }

  const fetchMaintenanceHistory = async () => {
    setHistoryLoading(true)
    const res = await fetch(`/api/assets/${assetId}/maintenance/history`)
    const json = await res.json()
    setMaintenanceHistory(json.data || [])
    setHistoryLoading(false)
  }

  const openAddMaint = () => {
    setEditingMaint(null)
    setMaintForm({ type: 'inspection', interval: 'monthly', next_due: '', notes: '' })
    setShowMaintDialog(true)
  }

  const openEditMaint = (m: any) => {
    setEditingMaint(m)
    setMaintForm({ type: m.type, interval: m.interval, next_due: m.next_due, notes: m.notes })
    setShowMaintDialog(true)
  }

  const handleSaveMaint = async () => {
    let res
    if (editingMaint) {
      res = await fetch(`/api/assets/${assetId}/maintenance`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: editingMaint.id, ...maintForm }),
      })
    } else {
      res = await fetch(`/api/assets/${assetId}/maintenance`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(maintForm),
      })
    }
    const json = await res.json()
    if (json.error) {
      toast({ title: 'Error', description: json.error, variant: 'destructive' })
    } else {
      setShowMaintDialog(false)
      fetchMaintenance()
      toast({ title: 'Saved', description: 'Maintenance schedule saved.' })
    }
  }

  const handleDeleteMaint = async (m: any) => {
    if (!window.confirm('Delete this maintenance schedule?')) return
    const res = await fetch(`/api/assets/${assetId}/maintenance`, {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id: m.id }),
    })
    const json = await res.json()
    if (json.error) {
      toast({ title: 'Error', description: json.error, variant: 'destructive' })
    } else {
      fetchMaintenance()
      toast({ title: 'Deleted', description: 'Maintenance schedule deleted.' })
    }
  }

  const openAddHistory = () => {
    setHistoryForm({ schedule_id: maintenance[0]?.id || '', performed_at: '', notes: '' })
    setShowHistoryDialog(true)
  }

  const handleSaveHistory = async () => {
    const res = await fetch(`/api/assets/${assetId}/maintenance/history`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...historyForm, performed_by: user?.id }),
    })
    const json = await res.json()
    if (json.error) {
      toast({ title: 'Error', description: json.error, variant: 'destructive' })
    } else {
      setShowHistoryDialog(false)
      fetchMaintenanceHistory()
      toast({ title: 'Logged', description: 'Maintenance event logged.' })
    }
  }

  const handleDelete = async () => {
    if (!asset) return

    if (
      !confirm(`Are you sure you want to delete "${asset.name}"? This action cannot be undone.`)
    ) {
      return
    }

    setDeleting(true)
    try {
      const result = await deleteAsset(asset.id!)

      if (result.error) {
        toast({
          title: 'Error Deleting Asset',
          description: result.error,
          variant: 'destructive',
        })
      } else {
        toast({
          title: 'Asset Deleted',
          description: 'The asset has been successfully deleted.',
        })
        router.push('/assets')
      }
    } catch (error) {
      toast({
        title: 'Error Deleting Asset',
        description: 'An unexpected error occurred while deleting the asset',
        variant: 'destructive',
      })
    } finally {
      setDeleting(false)
    }
  }

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      active: { color: 'bg-green-50 text-green-700 border-green-200', label: 'Active' },
      maintenance: { color: 'bg-amber-50 text-amber-700 border-amber-200', label: 'Maintenance' },
      retired: { color: 'bg-gray-50 text-gray-700 border-gray-200', label: 'Retired' },
      lost: { color: 'bg-red-50 text-red-700 border-red-200', label: 'Lost' },
      damaged: { color: 'bg-orange-50 text-orange-700 border-orange-200', label: 'Damaged' },
    }

    const config = statusConfig[status as keyof typeof statusConfig] || {
      color: 'bg-blue-50 text-blue-700 border-blue-200',
      label: status,
    }

    return (
      <Badge variant="outline" className={config.color}>
        {config.label}
      </Badge>
    )
  }

  const formatCurrency = (value?: number | null) => {
    if (!value) return 'N/A'
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(value)
  }

  const formatDate = (dateString?: string | null) => {
    if (!dateString) return 'N/A'
    return new Date(dateString).toLocaleDateString()
  }

  if (authLoading || loading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 py-8">
          <div className="flex items-center justify-center py-12">
            <div className="text-center">
              <Loader2 className="h-12 w-12 animate-spin text-blue-600 mx-auto mb-4" />
              <h2 className="text-xl font-semibold text-gray-900 mb-2">Loading Asset</h2>
              <p className="text-gray-600">Please wait while we fetch the asset details...</p>
            </div>
          </div>
        </div>
      </div>
    )
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 py-8">
          <Card className="w-full max-w-md mx-auto">
            <CardHeader>
              <CardTitle className="flex items-center">
                <AlertTriangle className="h-5 w-5 mr-2 text-red-600" />
                Authentication Required
              </CardTitle>
              <CardDescription>Please log in to view asset details.</CardDescription>
            </CardHeader>
            <CardContent>
              <Button asChild className="w-full">
                <Link href="/login">Log In</Link>
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    )
  }

  if (error || !asset) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 py-8">
          <Card className="w-full max-w-md mx-auto">
            <CardHeader>
              <CardTitle className="flex items-center">
                <AlertTriangle className="h-5 w-5 mr-2 text-red-600" />
                Asset Not Found
              </CardTitle>
              <CardDescription>
                {error || 'The requested asset could not be found.'}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Button asChild className="w-full">
                <Link href="/assets">Back to Assets</Link>
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-2">
              <Package className="h-8 w-8 text-blue-600" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">{asset.name}</h1>
                <p className="text-sm text-gray-600">Asset ID: {asset.asset_id}</p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <Button asChild variant="outline" size="sm">
                <Link href="/assets">
                  <ArrowLeft className="h-4 w-4 mr-2" />
                  Back to Assets
                </Link>
              </Button>
              <Button asChild size="sm">
                <Link href={`/asset/${asset.id}/edit`}>
                  <Edit className="h-4 w-4 mr-2" />
                  Edit
                </Link>
              </Button>
              {asset.qr_code && (
                <Button asChild size="sm" variant="outline">
                  <Link href={`/qr-test?asset=${asset.id}`}>
                    <QrCode className="h-4 w-4 mr-2" />
                    QR Code
                  </Link>
                </Button>
              )}
              <Button size="sm" variant="destructive" onClick={handleDelete} disabled={deleting}>
                {deleting ? (
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                ) : (
                  <Trash2 className="h-4 w-4 mr-2" />
                )}
                Delete
              </Button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Asset Overview */}
          <div className="lg:col-span-2">
            <Tabs defaultValue="overview" className="w-full">
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="overview">Overview</TabsTrigger>
                <TabsTrigger value="details">Details</TabsTrigger>
                <TabsTrigger value="history">History</TabsTrigger>
                <TabsTrigger value="maintenance">Maintenance</TabsTrigger>
              </TabsList>

              <TabsContent value="overview" className="space-y-6">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <Package className="h-5 w-5 mr-2" />
                      Asset Information
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
