                {asset.qr_code && (
                  <Button asChild className="w-full" size="sm" variant="outline">
                    <Link href={`/qr-test?asset=${asset.id}`}>
                      <QrCode className="h-4 w-4 mr-2" />
                      View QR Code
                    </Link>
