import { createClient } from '@/lib/supabase/server'
import { NextResponse } from 'next/server'
import { cookies } from 'next/headers'
import { redirect } from 'next/navigation'

export async function GET(request: Request) {
  const requestUrl = new URL(request.url)
  const code = requestUrl.searchParams.get('code')
  const error = requestUrl.searchParams.get('error')
  const error_description = requestUrl.searchParams.get('error_description')
  const provider = requestUrl.searchParams.get('provider')

  console.log('Auth callback received:', { code: !!code, error, provider })

  // if "next" is in param, use it as the redirect URL
  const next = requestUrl.searchParams.get('next') || '/dashboard'

  // Handle error cases
  if (error) {
    console.error('Auth callback error:', error, error_description)
    return redirect('/auth/auth-code-error')
  }

  // Check if we have Supabase configuration
  const hasSupabaseConfig =
    process.env.NEXT_PUBLIC_SUPABASE_URL && process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY

  if (!hasSupabaseConfig) {
    console.log('No Supabase configuration found, redirecting to demo')
    return redirect('/demo')
  }

  if (code) {
    const cookieStore = cookies()
    const supabase = await createClient()

    console.log('Exchanging code for session...')
    const { data, error: exchangeError } = await supabase.auth.exchangeCodeForSession(code)

    if (exchangeError) {
      console.error('Code exchange error:', exchangeError)
      return redirect('/auth/auth-code-error')
    }

    console.log('Session created successfully, user:', data.user?.id)

    if (data.user) {
      // Get user data
      const {
        data: { user },
      } = await supabase.auth.getUser()
      if (!user) {
        console.error('No user found after code exchange')
        return redirect('/auth/auth-code-error')
      }

      // Get full name from user metadata
      const fullName = user.user_metadata?.full_name || user.user_metadata?.name || null
      const avatarUrl = user.user_metadata?.avatar_url || null

      // Get organization name from metadata or cookie
      let orgName = user.user_metadata?.org_name
      if (!orgName) {
        // Try to get from cookie for GitHub signups
        const signupOrgName = cookieStore.get('signup_org_name')
        if (signupOrgName) {
          orgName = decodeURIComponent(signupOrgName.value)
          // Clear the cookie
          cookieStore.delete('signup_org_name')
        }
      }

      // Create profile if it doesn't exist
      const { data: existingProfile } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', user.id)
        .single()

      if (!existingProfile) {
        const { error: insertError } = await supabase.from('profiles').insert({
          id: user.id,
          full_name: fullName,
          avatar_url: avatarUrl,
          role: 'admin', // First user is admin
          email: user.email,
        })

        if (insertError) {
          console.error('Profile creation error:', insertError)
        } else {
          console.log('Profile created successfully')

          // Create default tenant for the user
          const { data: tenant, error: tenantError } = await supabase
            .from('tenants')
            .insert({
              name: orgName || `${fullName}'s Organization`,
              plan: 'free',
              status: 'active',
              max_users: 5,
              max_assets: 100,
              features: {
                qrCodes: true,
                analytics: false,
                api: false,
                customBranding: false,
                multipleLocations: false,
                advancedReports: false,
              },
            })
            .select()
            .single()

          if (tenantError) {
            console.error('Tenant creation error:', tenantError)
          } else if (tenant) {
            // Update profile with tenant info and make user the owner
            const { error: updateError } = await supabase
              .from('profiles')
              .update({
                tenant_id: tenant.id,
                role: 'owner',
              })
              .eq('id', user.id)

            if (updateError) {
              console.error('Profile update error:', updateError)
            }
          }
        }
      }

      // Successful authentication - redirect to dashboard
      console.log('Redirecting to dashboard after successful authentication')
      return redirect(next)
    }
  }

  // No code provided - redirect to login
  console.log('No auth code provided, redirecting to login')
  return redirect('/login')
}

'use client'

import type React from 'react'

import { useState, useEffect } from 'react'
import { useRouter, useSearchParams } from 'next/navigation'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { createClient } from '@/lib/supabase/client'
import Link from 'next/link'

export default function ResetPasswordPage() {
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [loading, setLoading] = useState(false)
  const [message, setMessage] = useState('')
  const [error, setError] = useState('')
  const [mounted, setMounted] = useState(false)

  const router = useRouter()
  const searchParams = useSearchParams()
  const accessToken = searchParams.get('access_token')
  const refreshToken = searchParams.get('refresh_token')

  useEffect(() => {
    setMounted(true)
  }, [])

  const handleResetPassword = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!mounted) return

    if (password !== confirmPassword) {
      setError('Passwords do not match')
      return
    }

    if (password.length < 6) {
      setError('Password must be at least 6 characters long')
      return
    }

    setLoading(true)
    setError('')
    setMessage('')

    try {
      const supabase = createClient()

      if (accessToken && refreshToken) {
        // Set the session first
        const { error: sessionError } = await supabase.auth.setSession({
          access_token: accessToken,
          refresh_token: refreshToken,
        })

        if (sessionError) {
          throw sessionError
        }
      }

      // Update the password
      const { error: updateError } = await supabase.auth.updateUser({
        password: password,
      })

      if (updateError) {
        throw updateError
      }

      setMessage('Password updated successfully! Redirecting to login...')

      setTimeout(() => {
        router.push('/login')
      }, 2000)
    } catch (error: any) {
      console.error('Password reset error:', error)
      setError(error.message || 'An error occurred while resetting your password')
    } finally {
      setLoading(false)
    }
  }

  if (!mounted) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    )
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1">
          <CardTitle className="text-2xl font-bold text-center">Reset Password</CardTitle>
          <CardDescription className="text-center">Enter your new password below</CardDescription>
        </CardHeader>
        <CardContent>
          {error && (
            <Alert className="mb-4 border-red-200 bg-red-50">
              <AlertDescription className="text-red-800">{error}</AlertDescription>
            </Alert>
          )}

          {message && (
            <Alert className="mb-4 border-green-200 bg-green-50">
              <AlertDescription className="text-green-800">{message}</AlertDescription>
            </Alert>
          )}

          <form onSubmit={handleResetPassword} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="password">New Password</Label>
              <Input
                id="password"
                type="password"
                value={password}
                onChange={e => setPassword(e.target.value)}
                required
                minLength={6}
                placeholder="Enter your new password"
                disabled={loading}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="confirmPassword">Confirm New Password</Label>
              <Input
                id="confirmPassword"
                type="password"
                value={confirmPassword}
                onChange={e => setConfirmPassword(e.target.value)}
                required
                minLength={6}
                placeholder="Confirm your new password"
                disabled={loading}
              />
            </div>

            <Button
              type="submit"
              className="w-full"
              disabled={loading || !password || !confirmPassword}
            >
              {loading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Updating Password...
                </>
              ) : (
                'Update Password'
              )}
            </Button>
          </form>

          <div className="mt-6 text-center">
            <Link href="/login" className="text-sm text-blue-600 hover:text-blue-500">
              Back to Login
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

