'use client'

import { useEffect, useState } from 'react'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog'
import { Loader2, Plus, Edit, Trash2 } from 'lucide-react'
import { useToast } from '@/hooks/use-toast'

const RULE_TYPES = [
  { value: 'retire', label: 'Retire' },
  { value: 'archive', label: 'Archive' },
  { value: 'depreciate', label: 'Depreciate' },
]
const TRIGGER_FIELDS = [
  { value: 'purchase_date', label: 'Purchase Date' },
  { value: 'warranty_expiry', label: 'Warranty Expiry' },
]

export default function LifecycleRulesPage() {
  const [rules, setRules] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [showDialog, setShowDialog] = useState(false)
  const [editing, setEditing] = useState<any | null>(null)
  const [form, setForm] = useState<any>({
    asset_id: '',
    type: 'retire',
    trigger_field: 'purchase_date',
    interval: '',
    trigger_date: '',
    status: 'active',
  })
  const { toast } = useToast()

  const fetchRules = async () => {
    setLoading(true)
    const res = await fetch('/api/lifecycle-rules')
    const json = await res.json()
    setRules(json.data || [])
    setLoading(false)
  }

  useEffect(() => {
    fetchRules()
  }, [])

  const openAdd = () => {
    setEditing(null)
    setForm({
      asset_id: '',
      type: 'retire',
      trigger_field: 'purchase_date',
      interval: '',
      trigger_date: '',
      status: 'active',
    })
    setShowDialog(true)
  }

  const openEdit = (rule: any) => {
    setEditing(rule)
    setForm({
      asset_id: rule.asset_id || '',
      type: rule.type,
      trigger_field: rule.trigger_field,
      interval: rule.interval || '',
      trigger_date: rule.trigger_date || '',
      status: rule.status,
    })
    setShowDialog(true)
  }

  const handleSave = async () => {
    let res
    if (editing) {
      res = await fetch('/api/lifecycle-rules', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: editing.id, ...form }),
      })
    } else {
      res = await fetch('/api/lifecycle-rules', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      })
    }
    const json = await res.json()
    if (json.error) {
      toast({ title: 'Error', description: json.error, variant: 'destructive' })
    } else {
      setShowDialog(false)
      fetchRules()
      toast({ title: 'Saved', description: 'Lifecycle rule saved.' })
    }
  }

  const handleDelete = async (rule: any) => {
    if (!window.confirm('Delete this lifecycle rule?')) return
    const res = await fetch('/api/lifecycle-rules', {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id: rule.id }),
    })
    const json = await res.json()
    if (json.error) {
      toast({ title: 'Error', description: json.error, variant: 'destructive' })
    } else {
      fetchRules()
      toast({ title: 'Deleted', description: 'Lifecycle rule deleted.' })
    }
  }

  return (
    <div className="max-w-4xl mx-auto py-8">
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle>Asset Lifecycle Rules</CardTitle>
          <Button onClick={openAdd} size="sm">
            <Plus className="h-4 w-4 mr-1" /> Add Rule
          </Button>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="animate-spin h-6 w-6 mr-2" /> Loading...
            </div>
          ) : rules.length === 0 ? (
            <div className="text-center text-gray-500 py-8">No lifecycle rules defined yet.</div>
          ) : (
            <table className="w-full text-sm border">
              <thead>
                <tr className="bg-gray-50">
                  <th className="p-2 text-left">Type</th>
                  <th className="p-2 text-left">Trigger Field</th>
                  <th className="p-2 text-left">Interval</th>
                  <th className="p-2 text-left">Trigger Date</th>
                  <th className="p-2 text-left">Status</th>
                  <th className="p-2 text-left">Asset</th>
                  <th className="p-2 text-left">Actions</th>
                </tr>
              </thead>
              <tbody>
                {rules.map(rule => (
                  <tr key={rule.id} className="border-t">
                    <td className="p-2 capitalize">{rule.type}</td>
                    <td className="p-2">{rule.trigger_field}</td>
                    <td className="p-2">{rule.interval}</td>
                    <td className="p-2">{rule.trigger_date}</td>
                    <td className="p-2">{rule.status}</td>
                    <td className="p-2">
                      {rule.asset_name ||
                        (rule.asset_id ? (
                          rule.asset_id
                        ) : (
                          <span className="text-gray-400">Global</span>
                        ))}
                    </td>
                    <td className="p-2">
                      <Button
                        variant="outline"
                        size="icon"
                        className="mr-1"
                        onClick={() => openEdit(rule)}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button variant="destructive" size="icon" onClick={() => handleDelete(rule)}>
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </CardContent>
      </Card>

      <Dialog open={showDialog} onOpenChange={setShowDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>{editing ? 'Edit Lifecycle Rule' : 'Add Lifecycle Rule'}</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-2">
            <Select
              value={form.type}
              onValueChange={val => setForm((f: any) => ({ ...f, type: val }))}
            >
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {RULE_TYPES.map(rt => (
                  <SelectItem key={rt.value} value={rt.value}>
                    {rt.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Select
              value={form.trigger_field}
              onValueChange={val => setForm((f: any) => ({ ...f, trigger_field: val }))}
            >
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {TRIGGER_FIELDS.map(tf => (
                  <SelectItem key={tf.value} value={tf.value}>
                    {tf.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Input
              placeholder="Interval (e.g. 5 years)"
              value={form.interval}
              onChange={e => setForm((f: any) => ({ ...f, interval: e.target.value }))}
            />
            <Input
              type="date"
              placeholder="Trigger Date (optional)"
              value={form.trigger_date}
              onChange={e => setForm((f: any) => ({ ...f, trigger_date: e.target.value }))}
            />
            <Select
              value={form.status}
              onValueChange={val => setForm((f: any) => ({ ...f, status: val }))}
            >
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="active">Active</SelectItem>
                <SelectItem value="inactive">Inactive</SelectItem>
                <SelectItem value="completed">Completed</SelectItem>
              </SelectContent>
            </Select>
            <Input
              placeholder="Asset ID (leave blank for global rule)"
              value={form.asset_id}
              onChange={e => setForm((f: any) => ({ ...f, asset_id: e.target.value }))}
            />
          </div>
          <DialogFooter>
            <Button onClick={handleSave}>{editing ? 'Save Changes' : 'Add Rule'}</Button>
            <Button variant="outline" onClick={() => setShowDialog(false)}>
              Cancel
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

'use client'
import { useState } from 'react'
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { useAuth } from '@/components/auth/auth-provider'

export default function SecuritySettingsPage() {
  const { user, signOut } = useAuth()
  const [currentPassword, setCurrentPassword] = useState('')
  const [newPassword, setNewPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [loading, setLoading] = useState(false)
  const [success, setSuccess] = useState('')
  const [error, setError] = useState('')

  const handlePasswordChange = async (e: React.FormEvent) => {
    e.preventDefault()
    setSuccess('')
    setError('')
    if (newPassword !== confirmPassword) {
      setError('New passwords do not match.')
      return
    }
    setLoading(true)
    try {
      const { createClient } = await import('@/lib/supabase/client')
      const supabase = createClient()
      const { error } = await supabase.auth.updateUser({ password: newPassword })
      if (error) setError(error.message)
      else setSuccess('Password updated successfully.')
    } catch (err) {
      setError('Failed to update password.')
    } finally {
      setLoading(false)
      setCurrentPassword('')
      setNewPassword('')
      setConfirmPassword('')
    }
  }

  const handleSignOutAll = async () => {
    setLoading(true)
    try {
      await signOut()
      window.location.href = '/login'
    } catch {
      setError('Failed to sign out.')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="max-w-2xl mx-auto py-8 px-4 space-y-8">
      <h1 className="text-2xl font-bold mb-4">Security Settings</h1>

      {/* Password Change */}
      <Card>
        <CardHeader>
          <CardTitle>Change Password</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handlePasswordChange} className="space-y-4">
            <Input
              type="password"
              placeholder="New password"
              value={newPassword}
              onChange={e => setNewPassword(e.target.value)}
              required
            />
            <Input
              type="password"
              placeholder="Confirm new password"
              value={confirmPassword}
              onChange={e => setConfirmPassword(e.target.value)}
              required
            />
            <Button type="submit" disabled={loading} className="w-full">
              {loading ? 'Updating...' : 'Change Password'}
            </Button>
            {success && <div className="text-green-600 text-sm mt-2">{success}</div>}
            {error && <div className="text-red-600 text-sm mt-2">{error}</div>}
          </form>
        </CardContent>
      </Card>

      {/* 2FA Section */}
      <Card>
        <CardHeader>
          <CardTitle>Two-Factor Authentication (2FA)</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-gray-600">2FA setup and management coming soon.</div>
        </CardContent>
      </Card>

      {/* Active Sessions/Devices */}
      <Card>
        <CardHeader>
          <CardTitle>Active Sessions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="mb-4">
            <div className="text-sm text-gray-700">Current session:</div>
            <div className="text-xs text-gray-500">User ID: {user?.id}</div>
            <div className="text-xs text-gray-500">Email: {user?.email}</div>
          </div>
          <Button variant="outline" onClick={handleSignOutAll} disabled={loading}>
            Sign out of all devices
          </Button>
        </CardContent>
      </Card>
    </div>
  )
}

'use client'
import { useEffect, useState } from 'react'
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'

const INTEGRATION_TYPES = [
  { value: 'slack', label: 'Slack' },
  { value: 'teams', label: 'Microsoft Teams' },
  { value: 'custom', label: 'Custom Webhook' },
]

export default function IntegrationManagementPage() {
  const [integrations, setIntegrations] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [form, setForm] = useState({ type: 'slack', webhook_url: '' })
  const [editingId, setEditingId] = useState<string | null>(null)
  const [error, setError] = useState<string | null>(null)
  const [testMsg, setTestMsg] = useState('')
  const [testStatus, setTestStatus] = useState<string | null>(null)

  const fetchIntegrations = async () => {
    setLoading(true)
    const res = await fetch('/api/integrations')
    const data = await res.json()
    setIntegrations(data.integrations || [])
    setLoading(false)
  }

  useEffect(() => {
    fetchIntegrations()
  }, [])

  const handleChange = (e: any) => {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  const handleSubmit = async (e: any) => {
    e.preventDefault()
    setError(null)
    const method = editingId ? 'PATCH' : 'POST'
    const url = editingId ? `/api/integrations/${editingId}` : '/api/integrations'
    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    })
    if (!res.ok) {
      const data = await res.json()
      setError(data.error || 'Failed to save integration')
      return
    }
    setForm({ type: 'slack', webhook_url: '' })
    setEditingId(null)
    fetchIntegrations()
  }

  const handleEdit = (integration: any) => {
    setForm({ type: integration.type, webhook_url: integration.webhook_url })
    setEditingId(integration.id)
  }

  const handleDelete = async (id: string) => {
    if (!confirm('Delete this integration?')) return
    await fetch(`/api/integrations/${id}`, { method: 'DELETE' })
    fetchIntegrations()
  }

  const handleTest = async (integration: any) => {
    setTestStatus('Sending...')
    const res = await fetch(integration.webhook_url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ text: testMsg || 'Test notification from AssetPro' }),
    })
    if (res.ok) setTestStatus('Success!')
    else setTestStatus('Failed to send')
    setTimeout(() => setTestStatus(null), 2000)
  }

  return (
    <div className="max-w-3xl mx-auto py-8 space-y-8">
      <Card>
        <CardHeader>
          <CardTitle>{editingId ? 'Edit Integration' : 'Register New Integration'}</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block mb-1 font-medium">Integration Type</label>
              <select
                name="type"
                value={form.type}
                onChange={handleChange}
                className="border rounded px-2 py-1"
              >
                {INTEGRATION_TYPES.map(opt => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block mb-1 font-medium">Webhook URL</label>
              <Input name="webhook_url" value={form.webhook_url} onChange={handleChange} required />
            </div>
            {error && <div className="text-red-600">{error}</div>}
            <Button type="submit">
              {editingId ? 'Update Integration' : 'Register Integration'}
            </Button>
            {editingId && (
              <Button
                type="button"
                variant="outline"
                onClick={() => {
                  setEditingId(null)
                  setForm({ type: 'slack', webhook_url: '' })
                }}
              >
                Cancel
              </Button>
            )}
          </form>
        </CardContent>
      </Card>
      <Card>
        <CardHeader>
          <CardTitle>Registered Integrations</CardTitle>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div>Loading...</div>
          ) : integrations.length === 0 ? (
            <div>No integrations registered.</div>
          ) : (
            <table className="w-full text-sm border">
              <thead>
                <tr>
                  <th className="p-2 border">Type</th>
                  <th className="p-2 border">Webhook URL</th>
                  <th className="p-2 border">Status</th>
                  <th className="p-2 border">Actions</th>
                </tr>
              </thead>
              <tbody>
                {integrations.map(integration => (
                  <tr key={integration.id}>
                    <td className="p-2 border">{integration.type}</td>
                    <td className="p-2 border break-all">{integration.webhook_url}</td>
                    <td className="p-2 border">{integration.status}</td>
                    <td className="p-2 border space-x-2">
                      <Button size="sm" variant="outline" onClick={() => handleEdit(integration)}>
                        Edit
                      </Button>
                      <Button
                        size="sm"
                        variant="destructive"
                        onClick={() => handleDelete(integration.id)}
                      >
                        Delete
                      </Button>
                      <Button size="sm" variant="secondary" onClick={() => handleTest(integration)}>
                        Test
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
          <div className="mt-4">
            <label className="block mb-1 font-medium">Test Message</label>
            <Input
              value={testMsg}
              onChange={e => setTestMsg(e.target.value)}
              placeholder="Enter a test message..."
            />
            {testStatus && <div className="mt-2 text-sm text-blue-600">{testStatus}</div>}
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

'use client'
