interface AssetManagementProps {
  assets: Asset[]
  loading?: boolean
  onRefresh?: () => void
}

export default function AssetManagement({
  assets,
  loading = false,
  onRefresh,
}: AssetManagementProps) {
  const [selectedAssets, setSelectedAssets] = useState<string[]>([])
  const [searchTerm, setSearchTerm] = useState('')
  const [filters, setFilters] = useState({ status: 'all', category: 'all' })
  const [showFilters, setShowFilters] = useState(false)
  const [bulkOperation, setBulkOperation] = useState({
    type: 'update_status' as const,
    value: '',
    open: false,
  })
  const [processing, setProcessing] = useState(false)
  const [deleteDialog, setDeleteDialog] = useState<{ open: boolean; asset: Asset | null }>({
    open: false,
    asset: null,
  })
  const [bulkDeleteDialog, setBulkDeleteDialog] = useState(false)
  const [bulkUpdateDialog, setBulkUpdateDialog] = useState(false)
  const [bulkField, setBulkField] = useState<'status' | 'category' | 'location'>('status')
  const [bulkValue, setBulkValue] = useState('')
  const [undoData, setUndoData] = useState<{ asset_ids: string[]; prevValues: any[] } | null>(null)
  const [undoBulkUpdate, setUndoBulkUpdate] = useState<{
    asset_ids: string[]
    prevValues: any[]
    field: string
  } | null>(null)
  const [undoBulkDelete, setUndoBulkDelete] = useState<{ assets: any[] } | null>(null)
  const [customFieldDefs, setCustomFieldDefs] = useState<any[]>([])
  const [customFieldValues, setCustomFieldValues] = useState<{
    [assetId: string]: { [fieldId: string]: string }
  }>({})
  const [categories, setCategories] = useState<any[]>([])
  const { toast } = useToast()

  useEffect(() => {
    async function fetchCustomFields() {
      const res = await fetch('/api/custom-fields')
      const json = await res.json()
      setCustomFieldDefs(json.data || [])
    }
    fetchCustomFields()
  }, [])

  useEffect(() => {
    async function fetchCategories() {
      const res = await fetch('/api/categories')
      const json = await res.json()
      setCategories(json.data || [])
    }
    fetchCategories()
  }, [])

  useEffect(() => {
    async function fetchAllCustomFieldValues() {
      if (!assets || assets.length === 0) return
      const all: { [assetId: string]: { [fieldId: string]: string } } = {}
      await Promise.all(
        assets.map(async asset => {
          const res = await fetch(`/api/assets/${asset.id}/custom-fields`)
          const json = await res.json()
          all[asset.id!] = {}
          if (json.data) {
            for (const cf of json.data) {
              all[asset.id!][cf.field_id] = cf.value
            }
          }
        })
      )
      setCustomFieldValues(all)
    }
    fetchAllCustomFieldValues()
  }, [assets])

  // Helper to build category options (flat with indentation for subcategories)
  const buildCategoryOptions = (list: any[], parentId: string | null = null, level = 0): any[] =>
    list
      .filter(c => (c.parent_id || '') === (parentId || ''))
      .flatMap(c => [{ ...c, indent: level }, ...buildCategoryOptions(list, c.id, level + 1)])

  const filteredAssets = assets.filter(asset => {
    const matchesSearch =
      asset.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      asset.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      asset.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
      asset.location?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      asset.asset_id.toLowerCase().includes(searchTerm.toLowerCase())

    const matchesStatus = filters.status === 'all' || asset.status === filters.status
    const matchesCategory = filters.category === 'all' || asset.category === filters.category

    return matchesSearch && matchesStatus && matchesCategory
  })

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedAssets(filteredAssets.map(asset => asset.id!))
    } else {
      setSelectedAssets([])
    }
  }

  const handleSelectAsset = (assetId: string, checked: boolean) => {
    if (checked) {
      setSelectedAssets(prev => [...prev, assetId])
    } else {
      setSelectedAssets(prev => prev.filter(id => id !== assetId))
    }
  }

  const handleBulkOperation = async () => {
    if (selectedAssets.length === 0) {
      toast({
        title: 'No Assets Selected',
        description: 'Please select at least one asset to perform bulk operations.',
        variant: 'destructive',
      })
      return
    }

    setProcessing(true)

    try {
      const response = await fetch('/api/assets/bulk', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          asset_ids: selectedAssets,
          operation: bulkOperation.type,
          value: bulkOperation.value,
        }),
      })

      const result = await response.json()

      if (result.success) {
        toast({
          title: 'Bulk Update Successful',
          description: `${result.updatedCount} assets updated successfully.`,
        })
        setSelectedAssets([])
        onRefresh?.()
      } else {
        toast({
          title: 'Bulk Update Failed',
          description: result.error || 'Failed to update assets',
          variant: 'destructive',
        })
      }
    } catch (error) {
      toast({
        title: 'Operation Failed',
        description: 'An unexpected error occurred',
        variant: 'destructive',
      })
    } finally {
      setProcessing(false)
      setBulkOperation(prev => ({ ...prev, open: false, value: '' }))
    }
  }

  const handleDelete = async (asset: Asset) => {
    setDeleteDialog({ open: false, asset: null })
    try {
      const res = await fetch(`/api/assets/${asset.id}`, { method: 'DELETE' })
      const data = await res.json()
      if (!res.ok) {
        toast({
          title: 'Delete Failed',
          description: data.error || 'Failed to delete asset',
          variant: 'destructive',
        })
      } else {
        toast({ title: 'Asset Deleted', description: `${asset.name} has been deleted.` })
        onRefresh?.()
      }
    } catch (e) {
      toast({
        title: 'Delete Failed',
        description: 'An unexpected error occurred',
        variant: 'destructive',
      })
    }
  }

  const handleBulkDelete = async () => {
    setBulkDeleteDialog(false)
    // Fetch asset data for undo
    let prevAssets: any[] = []
    try {
      const resPrev = await fetch('/api/assets/bulk-fetch', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ asset_ids: selectedAssets }),
      })
      prevAssets = await resPrev.json()
    } catch {}
    try {
      const res = await fetch('/api/assets/bulk', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ asset_ids: selectedAssets }),
      })
      const data = await res.json()
      if (!res.ok) {
        toast({
          title: 'Bulk Delete Failed',
          description: data.error || 'Failed to delete assets',
          variant: 'destructive',
        })
      } else {
        setUndoBulkDelete({ assets: prevAssets })
        toast({
          title: 'Assets Deleted',
          description: `${selectedAssets.length} assets deleted.`,
          action: (
            <ToastAction altText="Undo" onClick={handleUndoBulkDelete}>
              Undo
            </ToastAction>
          ),
        })
        setSelectedAssets([])
        onRefresh?.()
      }
    } catch (e) {
      toast({
        title: 'Bulk Delete Failed',
        description: 'An unexpected error occurred',
        variant: 'destructive',
      })
    }
  }

  const handleBulkUpdate = async () => {
    setBulkUpdateDialog(false)
    // Fetch previous values for undo
    let prevValues: any[] = []
    try {
      const resPrev = await fetch('/api/assets/bulk-fetch', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ asset_ids: selectedAssets, field: bulkField }),
      })
      prevValues = await resPrev.json()
    } catch {}
    try {
      const res = await fetch('/api/assets/bulk', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          asset_ids: selectedAssets,
          operation: `update_${bulkField}`,
          value: bulkValue,
        }),
      })
      const data = await res.json()
      if (!res.ok) {
        toast({
          title: 'Bulk Update Failed',
          description: data.error || 'Failed to update assets',
          variant: 'destructive',
        })
      } else {
        setUndoBulkUpdate({ asset_ids: selectedAssets, prevValues, field: bulkField })
        toast({
          title: 'Assets Updated',
          description: `${selectedAssets.length} assets updated.`,
          action: (
            <ToastAction altText="Undo" onClick={handleUndoBulkUpdate}>
              Undo
            </ToastAction>
          ),
        })
        setSelectedAssets([])
        onRefresh?.()
      }
    } catch (e) {
      toast({
        title: 'Bulk Update Failed',
        description: 'An unexpected error occurred',
        variant: 'destructive',
      })
    }
  }

  const handleUndoBulkUpdate = async () => {
    if (!undoBulkUpdate) return
    try {
      await fetch('/api/assets/bulk', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          asset_ids: undoBulkUpdate.asset_ids,
          operation: `update_${undoBulkUpdate.field}`,
          value: null, // Will be set per asset below
          prevValues: undoBulkUpdate.prevValues,
        }),
      })
      toast({ title: 'Undo Successful', description: 'Bulk update has been undone.' })
      onRefresh?.()
    } catch {
      toast({
        title: 'Undo Failed',
        description: 'Could not undo bulk update.',
        variant: 'destructive',
      })
    } finally {
      setUndoBulkUpdate(null)
    }
  }

  const handleUndoBulkDelete = async () => {
    if (!undoBulkDelete) return
    try {
      await fetch('/api/assets/bulk-restore', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ assets: undoBulkDelete.assets }),
      })
      toast({ title: 'Undo Successful', description: 'Bulk delete has been undone.' })
      onRefresh?.()
    } catch {
      toast({
        title: 'Undo Failed',
        description: 'Could not undo bulk delete.',
        variant: 'destructive',
      })
    } finally {
      setUndoBulkDelete(null)
    }
  }

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      active: { color: 'bg-green-50 text-green-700 border-green-200', label: 'Active' },
      maintenance: { color: 'bg-amber-50 text-amber-700 border-amber-200', label: 'Maintenance' },
      retired: { color: 'bg-gray-50 text-gray-700 border-gray-200', label: 'Retired' },
      lost: { color: 'bg-red-50 text-red-700 border-red-200', label: 'Lost' },
      damaged: { color: 'bg-orange-50 text-orange-700 border-orange-200', label: 'Damaged' },
    }

    const config = statusConfig[status as keyof typeof statusConfig] || {
      color: 'bg-blue-50 text-blue-700 border-blue-200',
      label: status,
    }

    return (
      <Badge variant="outline" className={config.color}>
        {config.label}
      </Badge>
    )
  }

  const formatCurrency = (value?: number | null) => {
    if (!value) return 'N/A'
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(value)
  }

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'N/A'
    return new Date(dateString).toLocaleDateString()
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Asset Management</h2>
          <p className="text-gray-600">Manage your asset inventory with advanced features</p>
        </div>
        <div className="flex items-center space-x-2">
          <Button asChild>
            <Link href="/add-asset">
              <Plus className="h-4 w-4 mr-2" />
              Add Asset
            </Link>
          </Button>
        </div>
      </div>

      {/* Search and Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center">
              <Search className="h-5 w-5 mr-2" />
              Search & Filters
            </div>
            <Button variant="outline" size="sm" onClick={() => setShowFilters(!showFilters)}>
              <Filter className="h-4 w-4 mr-2" />
              {showFilters ? 'Hide' : 'Show'} Filters
            </Button>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center space-x-4">
            <div className="flex-1">
              <Input
                placeholder="Search assets by name, description, category, location, or ID..."
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
              />
            </div>
          </div>

          {showFilters && (
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 pt-4 border-t">
              <div>
                <label className="text-sm font-medium">Status</label>
                <Select
                  value={filters.status}
                  onValueChange={value => setFilters(prev => ({ ...prev, status: value }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Statuses</SelectItem>
                    <SelectItem value="active">Active</SelectItem>
                    <SelectItem value="maintenance">Maintenance</SelectItem>
                    <SelectItem value="retired">Retired</SelectItem>
                    <SelectItem value="lost">Lost</SelectItem>
                    <SelectItem value="damaged">Damaged</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium">Category</label>
                <Select
                  value={filters.category}
                  onValueChange={value => setFilters(prev => ({ ...prev, category: value }))}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="All Categories" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Categories</SelectItem>
                    {buildCategoryOptions(categories).map(cat => (
                      <SelectItem key={cat.id} value={cat.id}>
                        {'\u00A0'.repeat(cat.indent * 4)}
                        {cat.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="flex items-end space-x-2">
                <Button
                  variant="outline"
                  onClick={() => setFilters({ status: 'all', category: 'all' })}
                  size="sm"
                >
                  Clear Filters
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Bulk Operations */}
      {selectedAssets.length > 0 && (
        <Card className="border-blue-200 bg-blue-50">
          <CardHeader>
            <CardTitle className="text-blue-800">
              Bulk Operations ({selectedAssets.length} assets selected)
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center space-x-4">
              <Select
                value={bulkOperation.type}
                onValueChange={(value: any) => setBulkOperation(prev => ({ ...prev, type: value }))}
              >
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="update_status">Update Status</SelectItem>
                  <SelectItem value="update_location">Update Location</SelectItem>
                  <SelectItem value="update_category">Update Category</SelectItem>
                </SelectContent>
              </Select>

              <Input
                placeholder={
                  bulkOperation.type === 'update_status'
                    ? 'Enter new status'
                    : bulkOperation.type === 'update_location'
                      ? 'Enter new location'
                      : 'Enter new category'
                }
                value={bulkOperation.value}
                onChange={e => setBulkOperation(prev => ({ ...prev, value: e.target.value }))}
                className="w-64"
              />

              <Dialog
                open={bulkOperation.open}
                onOpenChange={open => setBulkOperation(prev => ({ ...prev, open }))}
              >
                <DialogTrigger asChild>
                  <Button disabled={processing || !bulkOperation.value}>
                    {processing ? (
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    ) : (
                      <Settings className="h-4 w-4 mr-2" />
                    )}
                    Update Selected
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Confirm Update</DialogTitle>
                    <DialogDescription>
                      Are you sure you want to update {selectedAssets.length} selected assets?
                    </DialogDescription>
                  </DialogHeader>
                  <DialogFooter>
                    <Button
                      variant="outline"
                      onClick={() => setBulkOperation(prev => ({ ...prev, open: false }))}
                    >
                      Cancel
                    </Button>
                    <Button onClick={handleBulkOperation} disabled={processing}>
                      {processing ? (
                        <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      ) : (
                        <CheckCircle className="h-4 w-4 mr-2" />
                      )}
                      Confirm
                    </Button>
                  </DialogFooter>
                </DialogContent>
              </Dialog>

              <Button variant="outline" onClick={() => setSelectedAssets([])} size="sm">
                Clear Selection
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {selectedAssets.length > 0 && (
        <div className="flex items-center mb-2 space-x-2">
          <Button variant="destructive" size="sm" onClick={() => setBulkDeleteDialog(true)}>
            <Trash2 className="h-4 w-4 mr-1" /> Delete Selected ({selectedAssets.length})
          </Button>
          <Button variant="secondary" size="sm" onClick={() => setBulkUpdateDialog(true)}>
            <Pencil className="h-4 w-4 mr-1" /> Bulk Update
          </Button>
        </div>
      )}

      {/* Assets Table */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center">
              <Package className="h-5 w-5 mr-2" />
              Assets ({filteredAssets.length})
            </div>
            <div className="flex items-center space-x-2">
              <Checkbox
                checked={
                  selectedAssets.length === filteredAssets.length && filteredAssets.length > 0
                }
                onCheckedChange={handleSelectAll}
              />
              <span className="text-sm text-gray-500">Select All</span>
            </div>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
              <span className="ml-2">Loading assets...</span>
            </div>
          ) : filteredAssets.length === 0 ? (
            <div className="text-center py-8">
              <Package className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No assets found</h3>
              <p className="text-gray-600 mb-4">
                {searchTerm || Object.keys(filters).length > 0
                  ? 'Try adjusting your search or filters'
                  : 'Get started by adding your first asset'}
              </p>
              {!searchTerm && Object.keys(filters).length === 0 && (
                <Button asChild>
                  <Link href="/add-asset">
                    <Plus className="h-4 w-4 mr-2" />
                    Add Your First Asset
                  </Link>
                </Button>
              )}
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead className="w-12"></TableHead>
                    <TableHead>Asset ID</TableHead>
                    <TableHead>Name</TableHead>
                    <TableHead>Category</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Location</TableHead>
                    <TableHead>Value</TableHead>
                    {/* Custom Field Columns */}
                    {customFieldDefs.slice(0, 2).map(field => (
                      <TableHead key={field.id}>{field.label}</TableHead>
                    ))}
                    <TableHead>Created</TableHead>
                    <TableHead className="w-32">Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredAssets.map(asset => (
                    <TableRow key={asset.id}>
                      <TableCell>
                        <Checkbox
                          checked={selectedAssets.includes(asset.id!)}
                          onCheckedChange={checked =>
                            handleSelectAsset(asset.id!, checked as boolean)
                          }
                        />
                      </TableCell>
                      <TableCell className="font-mono text-sm">{asset.asset_id}</TableCell>
                      <TableCell>
                        <div>
                          <div className="font-medium">{asset.name}</div>
                          {asset.description && (
                            <div className="text-sm text-gray-500 truncate max-w-xs">
                              {asset.description}
                            </div>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <Badge variant="outline">
                          {categories.find(c => c.id === asset.category)?.name || asset.category}
                        </Badge>
                      </TableCell>
                      <TableCell>{getStatusBadge(asset.status)}</TableCell>
                      <TableCell>{asset.location}</TableCell>
                      <TableCell>{formatCurrency(asset.purchase_value)}</TableCell>
                      {/* Custom Field Values */}
                      {customFieldDefs.slice(0, 2).map(field => (
                        <TableCell key={field.id}>
                          {customFieldValues[asset.id!] &&
                          customFieldValues[asset.id!][field.id] ? (
                            customFieldValues[asset.id!][field.id]
                          ) : (
                            <span className="text-gray-400">-</span>
                          )}
                        </TableCell>
                      ))}
                      <TableCell>{formatDate(asset.created_at)}</TableCell>
                      <TableCell>
                        <Button asChild size="sm" variant="outline">
                          <Link href={`/asset/${asset.id}`}>View</Link>
                        </Button>
                        <Button asChild size="sm" variant="outline" className="ml-2">
                          <Link href={`/asset/${asset.id}/edit`}>Edit</Link>
                        </Button>
                        <Button
                          size="sm"
                          variant="destructive"
                          className="ml-2"
                          onClick={() => handleDelete(asset)}
                        >
                          Delete
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Delete Confirmation Dialog */}
      <Dialog
        open={deleteDialog.open}
        onOpenChange={open => setDeleteDialog({ open, asset: deleteDialog.asset })}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Delete Asset</DialogTitle>
          </DialogHeader>
          <div>
            Are you sure you want to delete <b>{deleteDialog.asset?.name}</b>?
          </div>
          <DialogFooter>
            <Button
              variant="destructive"
              onClick={() => deleteDialog.asset && handleDelete(deleteDialog.asset)}
            >
              Delete
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Bulk Delete Confirmation Dialog */}
      <Dialog open={bulkDeleteDialog} onOpenChange={setBulkDeleteDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Delete Selected Assets</DialogTitle>
          </DialogHeader>
          <div>
            Are you sure you want to delete <b>{selectedAssets.length}</b> selected assets? This
            action cannot be undone.
          </div>
          <DialogFooter>
            <Button variant="destructive" onClick={handleBulkDelete}>
              Delete
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Dialog open={bulkUpdateDialog} onOpenChange={setBulkUpdateDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Bulk Update Assets</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-1">Field to Update</label>
              <select
                className="w-full border rounded p-2"
                value={bulkField}
                onChange={e => setBulkField(e.target.value as any)}
              >
                <option value="status">Status</option>
                <option value="category">Category</option>
                <option value="location">Location</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">New Value</label>
              <input
                className="w-full border rounded p-2"
                value={bulkValue}
                onChange={e => setBulkValue(e.target.value)}
                placeholder={`Enter new ${bulkField}`}
              />
            </div>
          </div>
          <DialogFooter>
            <Button onClick={handleBulkUpdate}>Update</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { createClient } from '@/lib/supabase/client'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Badge } from '@/components/ui/badge'
import { CheckCircle, XCircle, AlertTriangle, RefreshCw, ExternalLink } from 'lucide-react'

export default function AuthDebug() {
  const [status, setStatus] = useState<{
    supabase: 'loading' | 'connected' | 'error'
    auth: 'loading' | 'authenticated' | 'unauthenticated' | 'error'
    user: any
    session: any
    error: string | null
  }>({
    supabase: 'loading',
    auth: 'loading',
    user: null,
    session: null,
    error: null,
  })

  const [loading, setLoading] = useState(false)

  const checkStatus = async () => {
    setLoading(true)
    try {
      const supabase = createClient()

      // Check Supabase connection
      const { data: sessionData, error: sessionError } = await supabase.auth.getSession()

      if (sessionError) {
        setStatus({
          supabase: 'error',
          auth: 'error',
          user: null,
          session: null,
          error: sessionError.message,
        })
        return
      }

      setStatus({
        supabase: 'connected',
        auth: sessionData.session ? 'authenticated' : 'unauthenticated',
        user: sessionData.session?.user || null,
        session: sessionData.session,
        error: null,
      })
    } catch (error) {
      setStatus({
        supabase: 'error',
        auth: 'error',
        user: null,
        session: null,
        error: error instanceof Error ? error.message : 'Unknown error',
      })
    } finally {
      setLoading(false)
    }
  }

  const testSignIn = async () => {
    setLoading(true)
    try {
      const supabase = createClient()

      const { data, error } = await supabase.auth.signInWithOAuth({
        provider: 'github',
        options: {
          redirectTo: `${window.location.origin}/auth/callback`,
        },
      })

      if (error) {
        setStatus(prev => ({ ...prev, error: error.message }))
      } else {
        console.log('OAuth initiated successfully:', data)
      }
    } catch (error) {
      setStatus(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Unknown error',
      }))
    } finally {
      setLoading(false)
    }
  }

  const testServerAuth = async () => {
    setLoading(true)
    try {
      const response = await fetch('/api/test-auth', {
        method: 'GET',
        credentials: 'include',
      })

      const result = await response.json()
      console.log('Server auth test result:', result)

      if (result.error) {
        setStatus(prev => ({ ...prev, error: result.error }))
      } else {
        setStatus(prev => ({
          ...prev,
          supabase: 'connected',
          auth: result.user ? 'authenticated' : 'unauthenticated',
          user: result.user,
        }))
      }
    } catch (error) {
      console.error('Server auth test error:', error)
      setStatus(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Unknown error',
      }))
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    checkStatus()
  }, [])

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold">Authentication Debug</h1>
        <Button onClick={checkStatus} disabled={loading} variant="outline">
          <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
          Refresh
        </Button>
      </div>

      {/* Environment Status */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">Environment Configuration</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">Supabase URL:</span>
              <Badge variant={process.env.NEXT_PUBLIC_SUPABASE_URL ? 'default' : 'destructive'}>
                {process.env.NEXT_PUBLIC_SUPABASE_URL ? ' Set' : ' Missing'}
              </Badge>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">Supabase Anon Key:</span>
              <Badge
                variant={process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY ? 'default' : 'destructive'}
              >
                {process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY ? ' Set' : ' Missing'}
              </Badge>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">App URL:</span>
              <Badge variant="outline">
                {process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}
              </Badge>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">Environment:</span>
              <Badge variant="outline">{process.env.NODE_ENV || 'development'}</Badge>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Connection Status */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            Connection Status
            {status.supabase === 'connected' && <CheckCircle className="h-5 w-5 text-green-500" />}
            {status.supabase === 'error' && <XCircle className="h-5 w-5 text-red-500" />}
            {status.supabase === 'loading' && <RefreshCw className="h-5 w-5 animate-spin" />}
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center justify-between">
            <span className="text-sm font-medium">Supabase Connection:</span>
            <Badge
              variant={
                status.supabase === 'connected'
                  ? 'default'
                  : status.supabase === 'error'
                    ? 'destructive'
                    : 'secondary'
              }
            >
              {status.supabase === 'connected' && ' Connected'}
              {status.supabase === 'error' && ' Error'}
              {status.supabase === 'loading' && ' Loading'}
            </Badge>
          </div>

          <div className="flex items-center justify-between">
            <span className="text-sm font-medium">Authentication Status:</span>
            <Badge
              variant={
                status.auth === 'authenticated'
                  ? 'default'
                  : status.auth === 'error'
                    ? 'destructive'
                    : 'secondary'
              }
            >
              {status.auth === 'authenticated' && ' Authenticated'}
              {status.auth === 'unauthenticated' && ' Not Authenticated'}
              {status.auth === 'error' && ' Error'}
              {status.auth === 'loading' && ' Loading'}
            </Badge>
          </div>

          {status.user && (
            <div className="bg-gray-50 p-4 rounded-lg">
              <h4 className="font-medium mb-2">User Info:</h4>
              <pre className="text-xs overflow-auto">{JSON.stringify(status.user, null, 2)}</pre>
            </div>
          )}

          {status.error && (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>{status.error}</AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>

      {/* Test Actions */}
      <Card>
        <CardHeader>
          <CardTitle>Test Actions</CardTitle>
          <CardDescription>Test authentication functionality</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex flex-wrap gap-4">
            <Button onClick={testSignIn} disabled={loading} variant="outline">
              Test GitHub Sign In
            </Button>
            <Button onClick={testServerAuth} disabled={loading} variant="outline">
              Test Server Auth
            </Button>
            <Button asChild variant="outline">
              <a href="/login" target="_blank" rel="noopener noreferrer">
                <ExternalLink className="h-4 w-4 mr-2" />
                Open Login Page
              </a>
            </Button>
            <Button asChild variant="outline">
              <a href="/signup" target="_blank" rel="noopener noreferrer">
                <ExternalLink className="h-4 w-4 mr-2" />
                Open Signup Page
              </a>
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import type React from 'react'
import { createContext, useContext, useEffect, useState } from 'react'
import type { User, Session } from '@supabase/supabase-js'
import { DEFAULT_ROLE_PERMISSIONS, Role, Permission } from '@/lib/rbac/types'
import { createClient } from '@/lib/supabase/client'

type AuthContextType = {
  user: User | null
  loading: boolean
  error: string | null
  signOut: () => Promise<void>
  retry: () => void
  role: Role | null
  permissions: Permission[]
}

const AuthContext = createContext<AuthContextType>({
  user: null,
  loading: true,
  error: null,
  signOut: async () => {},
  retry: () => {},
  role: null,
  permissions: [],
})

export const useAuth = () => {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider')
  }
  return context
}

export const usePermissions = () => {
  const { permissions, role } = useAuth()
  const hasPermission = (perm: Permission) => permissions.includes(perm)
  const hasAnyPermission = (perms: Permission[]) => perms.some(p => permissions.includes(p))
  return { permissions, role, hasPermission, hasAnyPermission }
}

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [mounted, setMounted] = useState(false)
  const [retryCount, setRetryCount] = useState(0)
  const [role, setRole] = useState<Role | null>(null)
  const [permissions, setPermissions] = useState<Permission[]>([])

  useEffect(() => {
    setMounted(true)
  }, [])

  const retry = () => {
    setRetryCount(prev => prev + 1)
    setError(null)
    setLoading(true)
  }

  useEffect(() => {
    if (!mounted) return

    let isMounted = true

    const initializeAuth = async () => {
      try {
        setError(null)

        // Dynamic import to prevent SSR issues
        const { createClient, checkSupabaseConnection } = await import('@/lib/supabase/client')

        // First check if Supabase is properly configured
        const healthCheck = await checkSupabaseConnection()
        if (!healthCheck.connected) {
          throw new Error(`Supabase connection failed: ${healthCheck.error}`)
        }

        const supabase = createClient()

        // Get initial session
        const {
          data: { session },
          error: sessionError,
        } = await supabase.auth.getSession()

        if (sessionError) {
          console.error('Session error:', sessionError)
          throw new Error(`Authentication error: ${sessionError.message}`)
        }

        if (isMounted) {
          setUser(session?.user ?? null)
          setLoading(false)
          setError(null)
        }

        // Fetch user profile for role/permissions
        if (session?.user) {
          const { data: profile, error: profileError } = await supabase
            .from('profiles')
            .select('role')
            .eq('id', session.user.id)
            .single()
          if (!profileError && profile?.role) {
            setRole(profile.role as Role)
            setPermissions(DEFAULT_ROLE_PERMISSIONS[profile.role as Role] || [])
          } else {
            setRole(null)
            setPermissions([])
          }
        } else {
          setRole(null)
          setPermissions([])
        }

        // Listen for auth changes
        const {
          data: { subscription },
        } = supabase.auth.onAuthStateChange(async (event: string, session: Session | null) => {
          console.log('Auth state changed:', event, session?.user?.id)
          if (isMounted) {
            setUser(session?.user ?? null)
            setLoading(false)
            setError(null)
          }
          // Refetch profile/permissions on auth change
          if (session?.user) {
            const { data: profile, error: profileError } = await supabase
              .from('profiles')
              .select('role')
              .eq('id', session.user.id)
              .single()
            if (!profileError && profile?.role) {
              setRole(profile.role as Role)
              setPermissions(DEFAULT_ROLE_PERMISSIONS[profile.role as Role] || [])
            } else {
              setRole(null)
              setPermissions([])
            }
          } else {
            setRole(null)
            setPermissions([])
          }
        })

        return () => {
          subscription.unsubscribe()
        }
      } catch (error) {
        console.error('Auth initialization error:', error)
        if (isMounted) {
          setError(error instanceof Error ? error.message : 'Authentication initialization failed')
          setLoading(false)
        }
      }
    }

    const cleanup = initializeAuth()

    return () => {
      isMounted = false
      cleanup?.then(unsubscribe => unsubscribe?.())
    }
  }, [mounted, retryCount])

  const signOut = async () => {
    try {
      const { createClient } = await import('@/lib/supabase/client')
      const supabase = createClient()
      const { error } = await supabase.auth.signOut()

      if (error) {
        console.error('Sign out error:', error)
        setError(`Sign out failed: ${error.message}`)
      }
    } catch (error) {
      console.error('Sign out error:', error)
      setError('Sign out failed')
    }
  }

  // Don't render anything until mounted to prevent hydration issues
  if (!mounted) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Initializing AssetTracker Pro...</p>
        </div>
      </div>
    )
  }

  // Show error state with retry option
  if (error && !loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-red-50 via-white to-orange-50">
        <div className="text-center max-w-md mx-auto p-6">
          <div className="bg-red-100 rounded-full h-16 w-16 flex items-center justify-center mx-auto mb-4">
            <svg
              className="h-8 w-8 text-red-600"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z"
              />
            </svg>
          </div>
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Connection Error</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button
            onClick={retry}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    )
  }

  return (
    <AuthContext.Provider value={{ user, loading, error, signOut, retry, role, permissions }}>
      {children}
    </AuthContext.Provider>
  )
}

'use client'

import type React from 'react'

import { useState } from 'react'
import { useRouter } from 'next/navigation'
import { createClient } from '@/lib/supabase/client'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Eye, EyeOff, Mail, Lock, Github, AlertTriangle, ExternalLink } from 'lucide-react'
import Link from 'next/link'

export default function LoginForm() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [showPassword, setShowPassword] = useState(false)
  const [loading, setLoading] = useState(false)
  const [githubLoading, setGithubLoading] = useState(false)
  const [error, setError] = useState('')
  const router = useRouter()
  const supabase = createClient()

  const isV0Preview =
    typeof window !== 'undefined' && window.location.hostname.includes('vusercontent.net')
  const isLocalhost = typeof window !== 'undefined' && window.location.hostname === 'localhost'

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError('')

    try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      })

      if (error) {
        if (error.message.includes('Email not confirmed')) {
          setError('Please check your email and click the confirmation link before signing in.')
        } else {
          setError(error.message)
        }
      } else {
        // Sync session for server-side
        const {
          data: { session },
        } = await supabase.auth.getSession()
        if (session) {
          await fetch('/api/auth/set-session', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              access_token: session.access_token,
              refresh_token: session.refresh_token,
            }),
          })
        }
        router.push('/')
        router.refresh()
      }
    } catch (err) {
      setError('An unexpected error occurred')
    } finally {
      setLoading(false)
    }
  }

  const handleGithubLogin = async () => {
    try {
      setGithubLoading(true)
      setError('')

      console.log('GitHub OAuth redirect URL:', `${window.location.origin}/auth/callback`)

      const { data, error } = await supabase.auth.signInWithOAuth({
        provider: 'github',
        options: {
          redirectTo: `${window.location.origin}/auth/callback`,
        },
      })

      console.log('GitHub auth response:', { data, error })

      if (error) {
        console.error('GitHub auth error:', error)
        setError(`GitHub authentication error: ${error.message}`)
      }
    } catch (err) {
      console.error('Unexpected GitHub auth error:', err)
      setError('Failed to authenticate with GitHub. Please try again.')
    } finally {
      setGithubLoading(false)
    }
  }

  const handleGoogleLogin = async () => {
    try {
      const { error } = await supabase.auth.signInWithOAuth({
        provider: 'google',
        options: {
          redirectTo: `${window.location.origin}/auth/callback`,
        },
      })

      if (error) {
        setError(error.message)
      }
    } catch (err) {
      setError('Failed to authenticate with Google')
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1">
          <CardTitle className="text-2xl font-bold text-center">Sign in to AssetTracker</CardTitle>
          <CardDescription className="text-center">
            Enter your email and password to access your account
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Environment Notice */}
          {isV0Preview && (
            <Alert>
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                <div className="space-y-2">
                  <p>
                    <strong>v0 Preview Environment Detected</strong>
                  </p>
                  <p className="text-sm">
                    GitHub OAuth needs to be configured for this preview URL. For full
                    functionality, please:
                  </p>
                  <ol className="text-sm list-decimal list-inside space-y-1 ml-2">
                    <li>
                      Update your GitHub OAuth app's Homepage URL to:{' '}
                      <code className="bg-gray-100 px-1 rounded text-xs">
                        {window.location.origin}
                      </code>
                    </li>
                    <li>Or download and run locally at http://localhost:3000</li>
                  </ol>
                  <div className="mt-2">
                    <a
                      href="https://github.com/settings/developers"
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-flex items-center text-blue-600 hover:text-blue-800 text-sm"
                    >
                      Update GitHub OAuth Settings <ExternalLink className="h-3 w-3 ml-1" />
                    </a>
                  </div>
                </div>
              </AlertDescription>
            </Alert>
          )}

          {error && (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                {error}
                {error.includes('Email not confirmed') && (
                  <div className="mt-2">
                    <Link
                      href="/auth/resend"
                      className="text-blue-600 hover:text-blue-500 underline"
                    >
                      Resend confirmation email
                    </Link>
                  </div>
                )}
              </AlertDescription>
            </Alert>
          )}

          {/* Quick access to troubleshooting */}
          {error && error.includes('blocked') && (
            <div className="text-center">
              <Button asChild variant="outline" size="sm">
                <Link href="/auth/oauth-status">Advanced OAuth Troubleshooting</Link>
              </Button>
            </div>
          )}

          {/* Quick access to troubleshooting */}
          {error && error.includes('blocked') && (
            <div className="text-center">
              <Button asChild variant="outline" size="sm">
                <Link href="/auth/blocked">Fix "Site Blocked" Error</Link>
              </Button>
            </div>
          )}

          {/* Social Login Buttons */}
          <div className="space-y-3">
            <Button
              type="button"
              variant="outline"
              onClick={handleGithubLogin}
              className="w-full"
              disabled={githubLoading}
            >
              {githubLoading ? (
                <div className="w-4 h-4 mr-2 animate-spin rounded-full border-2 border-gray-300 border-t-gray-600" />
              ) : (
                <Github className="w-4 h-4 mr-2" />
              )}
              Continue with GitHub
              {isV0Preview && <span className="ml-2 text-xs text-orange-600">(Needs setup)</span>}
            </Button>

            <Button type="button" variant="outline" onClick={handleGoogleLogin} className="w-full">
              <svg className="w-4 h-4 mr-2" viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"
                />
                <path
                  fill="currentColor"
                  d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"
                />
                <path
                  fill="currentColor"
                  d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"
                />
                <path
                  fill="currentColor"
                  d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"
                />
              </svg>
              Continue with Google
            </Button>
          </div>

          <div className="relative">
            <div className="absolute inset-0 flex items-center">
              <span className="w-full border-t" />
            </div>
            <div className="relative flex justify-center text-xs uppercase">
              <span className="bg-white px-2 text-gray-500">Or continue with email</span>
            </div>
          </div>

          <form onSubmit={handleLogin} className="space-y-4">
            <div className="space-y-2">
              <label htmlFor="email" className="text-sm font-medium">
                Email
              </label>
              <div className="relative">
                <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  id="email"
                  type="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                  className="pl-10"
                  required
                />
              </div>
            </div>

            <div className="space-y-2">
              <label htmlFor="password" className="text-sm font-medium">
                Password
              </label>
              <div className="relative">
                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  id="password"
                  type={showPassword ? 'text' : 'password'}
                  placeholder="Enter your password"
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                  className="pl-10 pr-10"
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2"
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4 text-gray-400" />
                  ) : (
                    <Eye className="h-4 w-4 text-gray-400" />
                  )}
                </button>
              </div>
            </div>

            <div className="flex items-center justify-between">
              <Link href="/auth/resend" className="text-sm text-blue-600 hover:text-blue-500">
                Resend confirmation
              </Link>
            </div>

            <Button type="submit" className="w-full" disabled={loading}>
              {loading ? 'Signing in...' : 'Sign in'}
            </Button>
          </form>

          <div className="text-center text-sm">
            {"Don't have an account? "}
            <Link href="/signup" className="text-blue-600 hover:text-blue-500 font-medium">
              Sign up
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import type React from 'react'

import { useState } from 'react'
import Link from 'next/link'
import { useRouter } from 'next/navigation'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { Separator } from '@/components/ui/separator'
import {
  Package,
  AlertCircle,
  Loader2,
  Github,
  Mail,
  ArrowRight,
  CheckCircle,
  Building2,
} from 'lucide-react'
import { motion } from 'framer-motion'
import { useBranding } from '@/components/branding-provider'

export default function SignupForm() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [name, setName] = useState('')
  const [orgName, setOrgName] = useState('')
  const [error, setError] = useState<string | null>(null)
  const [loading, setLoading] = useState(false)
  const [githubLoading, setGithubLoading] = useState(false)
  const [success, setSuccess] = useState(false)
  const router = useRouter()
  const branding = useBranding()

  const handleSignup = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setError(null)

    if (!orgName.trim()) {
      setError('Organization name is required')
      setLoading(false)
      return
    }

    try {
      // Dynamically import to prevent SSR issues
      const { createClient } = await import('@/lib/supabase/client')
      const supabase = createClient()

      const { error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: {
            full_name: name,
            org_name: orgName,
          },
          emailRedirectTo: `${window.location.origin}/auth/callback`,
        },
      })

      if (error) {
        setError(error.message)
      } else {
        setSuccess(true)
      }
    } catch (err) {
      console.error('Signup error:', err)
      setError('An unexpected error occurred. Please try again.')
    } finally {
      setLoading(false)
    }
  }

  const handleGithubSignup = async () => {
    if (!orgName.trim()) {
      setError('Organization name is required')
      return
    }
    setGithubLoading(true)
    setError(null)

    try {
      const { createClient } = await import('@/lib/supabase/client')
      const supabase = createClient()
      // Store org name in a cookie before GitHub OAuth
      document.cookie = `signup_org_name=${encodeURIComponent(orgName)}; path=/; max-age=300`
      const { error } = await supabase.auth.signInWithOAuth({
        provider: 'github',
        options: {
          redirectTo: `${window.location.origin}/auth/callback`,
        },
      })
      if (error) {
        setError(error.message)
        document.cookie = 'signup_org_name=; path=/; expires=Thu, 01 Jan 1970 00:00:00 GMT'
      }
    } catch (err) {
      console.error('GitHub signup error:', err)
      setError('Failed to authenticate with GitHub. Please try again.')
      document.cookie = 'signup_org_name=; path=/; expires=Thu, 01 Jan 1970 00:00:00 GMT'
    } finally {
      setGithubLoading(false)
    }
  }

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-blue-50 via-white to-purple-50">
      {/* Header */}
      <header className="w-full py-6 px-4 sm:px-6 lg:px-8 flex justify-center">
        <Link href="/" className="flex items-center space-x-2">
          {branding?.logoUrl && (
            <img src={branding.logoUrl} alt="Logo" className="h-12 w-12 rounded bg-white border" />
          )}
