'use client'
import React, { createContext, useContext, useEffect, useState, ReactNode } from 'react'
import { createClient } from '@/lib/supabase/client'
import { useAuth } from '@/components/auth/auth-provider'
import { Tenant } from '@/lib/rbac/types'

export interface Branding {
  logoUrl?: string
  primaryColor?: string
  secondaryColor?: string
  companyName?: string
}

const BrandingContext = createContext<Branding | null>(null)

export function BrandingProvider({ children }: { children: ReactNode }) {
  const { user } = useAuth()
  const [branding, setBranding] = useState<Branding>({})

  useEffect(() => {
    async function fetchBranding() {
      if (!user) return
      const supabase = createClient()
      const { data: profile } = await supabase
        .from('profiles')
        .select('*, tenants(*)')
        .eq('id', user.id)
        .single()
      if (profile?.tenants) {
        setBranding({
          logoUrl: profile.tenants.branding_logo_url || undefined,
          primaryColor: profile.tenants.branding_primary_color || undefined,
          secondaryColor: profile.tenants.branding_secondary_color || undefined,
          companyName: profile.tenants.branding_company_name || profile.tenants.name || undefined,
        })
      }
    }
    fetchBranding()
  }, [user])

  return <BrandingContext.Provider value={branding}>{children}</BrandingContext.Provider>
}

export function useBranding() {
  return useContext(BrandingContext)
}

'use client'

import { useState, useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Progress } from '@/components/ui/progress'
import { Checkbox } from '@/components/ui/checkbox'
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table'
import {
  QrCode,
  Download,
  Upload,
  CheckCircle,
  AlertTriangle,
  FileText,
  Printer,
} from 'lucide-react'
import { generateBulkQRCodes } from '@/lib/qr-actions'
import { fetchQRTemplates, fetchDefaultQRTemplate } from '@/lib/qr-template-utils'
import jsPDF from 'jspdf'
import html2canvas from 'html2canvas'
import QRLabel from '@/components/qr-label'
import { useToast } from '@/hooks/use-toast'

interface Asset {
  id: string
  asset_id: string
  name: string
  category: string
  status: string
  qr_code?: string | null
}

interface BulkQROperationsProps {
  assets: Asset[]
  onBulkGenerated?: (results: any[]) => void
}

export default function BulkQROperations({ assets, onBulkGenerated }: BulkQROperationsProps) {
  const [selectedAssets, setSelectedAssets] = useState<string[]>([])
  const [loading, setLoading] = useState(false)
  const [progress, setProgress] = useState(0)
  const [results, setResults] = useState<any[]>([])
  const [error, setError] = useState<string | null>(null)
  const [templates, setTemplates] = useState<any[]>([])
  const [selectedTemplateId, setSelectedTemplateId] = useState<string>('')
  const [templateConfig, setTemplateConfig] = useState<any>(null)
  const { toast } = useToast()
  const [printLabels, setPrintLabels] = useState(false)

  useEffect(() => {
    async function loadTemplates() {
      try {
        const all = await fetchQRTemplates()
        setTemplates(all)
        const def = all.find((tpl: any) => tpl.is_default) || all[0]
        if (def) {
          setSelectedTemplateId(def.id)
          setTemplateConfig(def.config)
        }
      } catch {}
    }
    loadTemplates()
  }, [])

  useEffect(() => {
    if (!selectedTemplateId) return
    const tpl = templates.find(t => t.id === selectedTemplateId)
    if (tpl) setTemplateConfig(tpl.config)
  }, [selectedTemplateId, templates])

  const assetsWithoutQR = assets.filter(asset => !asset.qr_code)
  const allSelected = selectedAssets.length === assetsWithoutQR.length
  const someSelected = selectedAssets.length > 0

  const handleSelectAll = () => {
    if (allSelected) {
      setSelectedAssets([])
    } else {
      setSelectedAssets(assetsWithoutQR.map(asset => asset.asset_id))
    }
  }

  const handleSelectAsset = (assetId: string) => {
    setSelectedAssets(prev =>
      prev.includes(assetId) ? prev.filter(id => id !== assetId) : [...prev, assetId]
    )
  }

  const handleBulkGenerate = async () => {
    if (selectedAssets.length === 0) {
      setError('Please select at least one asset')
      return
    }

    setLoading(true)
    setError(null)
    setProgress(0)
    setResults([])

    try {
      // Simulate progress updates
      const progressInterval = setInterval(() => {
        setProgress(prev => Math.min(prev + 10, 90))
      }, 200)

      const result = await generateBulkQRCodes(selectedAssets)

      clearInterval(progressInterval)
      setProgress(100)

      if (result.error) {
        setError(result.error)
      } else {
        setResults(result.results!)
        if (onBulkGenerated) {
          onBulkGenerated(result.results!)
        }
      }
    } catch (err) {
      setError('Failed to generate QR codes')
    } finally {
      setLoading(false)
      setTimeout(() => setProgress(0), 2000)
    }
  }

  const handleDownloadAll = () => {
    const successfulResults = results.filter(r => r.success)

    successfulResults.forEach((result, index) => {
      setTimeout(() => {
        const link = document.createElement('a')
        link.download = `qr-${result.assetId}.png`
        link.href = result.qrCode
        link.click()
      }, index * 100) // Stagger downloads
    })
  }

  // Add this function to generate a PDF with all QR codes and asset info
  const handleDownloadPDF = async () => {
    const successfulResults = results.filter(r => r.success)
    if (successfulResults.length === 0) {
      toast({
        title: 'Nothing to export',
        description: 'No QR codes to export.',
        variant: 'destructive',
      })
      return
    }
    try {
      const pdf = new jsPDF({ orientation: 'portrait', unit: 'px', format: [800, 1120] })
      const itemsPerRow = 3
      const itemWidth = (templateConfig?.qrSize || 120) + 40
      const itemHeight = (templateConfig?.qrSize || 120) + 80
      let x = 0,
        y = 0,
        row = 0,
        col = 0
      for (let i = 0; i < successfulResults.length; i++) {
        const result = successfulResults[i]
        const asset = assets.find(a => a.asset_id === result.assetId)
        if (!asset) continue
        const container = document.createElement('div')
        container.style.position = 'fixed'
        container.style.left = '-9999px'
        container.style.top = '0'
        document.body.appendChild(container)
        const label = (
          <QRLabel asset={asset} templateConfig={templateConfig} qrCodeUrl={result.qrCode} />
        )
        await new Promise(resolve => {
          import('react-dom/client').then(({ createRoot }) => {
            const root = createRoot(container)
            root.render(label)
            setTimeout(async () => {
              const canvas = await (
                await import('html2canvas')
              ).default(container, { backgroundColor: '#fff', scale: 2 })
              const imgData = canvas.toDataURL('image/png')
              pdf.addImage(imgData, 'PNG', x, y, itemWidth, itemHeight)
              root.unmount()
              document.body.removeChild(container)
              col++
              if (col >= itemsPerRow) {
                col = 0
                row++
                x = 0
                y += itemHeight + 16
              } else {
                x += itemWidth + 16
              }
              resolve(null)
            }, 100)
          })
        })
      }
      pdf.save('qr-codes-batch.pdf')
      toast({ title: 'Exported PDF', description: 'QR report exported as PDF.' })
    } catch (err) {
      toast({
        title: 'Export Failed',
        description: 'Failed to export PDF.',
        variant: 'destructive',
      })
    }
  }

  // Print all QR labels
  const handlePrintAll = () => {
    if (results.filter(r => r.success).length === 0) {
      toast({
        title: 'Nothing to print',
        description: 'No QR codes to print.',
        variant: 'destructive',
      })
      return
    }
    setPrintLabels(true)
    setTimeout(() => {
      window.print()
      setTimeout(() => setPrintLabels(false), 1000)
    }, 100)
  }

  const generateCSVTemplate = () => {
    const csvContent = [
      'asset_id,name,category,location,value',
      'AST-001,MacBook Pro 16",it-equipment,Office A,2499.99',
      'AST-002,Office Chair,furniture,Office B,299.99',
      'AST-003,Projector,av-equipment,Conference Room,899.99',
    ].join('\n')

    const blob = new Blob([csvContent], { type: 'text/csv' })
    const url = URL.createObjectURL(blob)
    const link = document.createElement('a')
    link.href = url
    link.download = 'asset-template.csv'
    link.click()
    URL.revokeObjectURL(url)
  }

  return (
    <div className="space-y-6">
      {/* Print container for all QR labels */}
      {printLabels && (
        <div className="print:block hidden">
          {results
            .filter(r => r.success)
            .map(result => {
              const asset = assets.find(a => a.asset_id === result.assetId)
              if (!asset) return null
              return (
                <div key={result.assetId} className="mb-4">
                  <QRLabel
                    asset={asset}
                    templateConfig={templateConfig}
                    qrCodeUrl={result.qrCode}
                  />
                </div>
              )
            })}
        </div>
      )}
      {templates.length > 0 && (
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1">QR Template</label>
          <select
            className="border rounded px-2 py-1"
            value={selectedTemplateId}
            onChange={e => setSelectedTemplateId(e.target.value)}
          >
            {templates.map(tpl => (
              <option key={tpl.id} value={tpl.id}>
                {tpl.name} {tpl.is_default ? '(Default)' : ''}
              </option>
            ))}
          </select>
        </div>
      )}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <QrCode className="h-5 w-5 mr-2" />
            Bulk QR Code Operations
          </CardTitle>
          <CardDescription>Generate QR codes for multiple assets at once</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {error && (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          {loading && (
            <div className="space-y-2">
              <div className="flex items-center justify-between text-sm">
                <span>Generating QR codes...</span>
                <span>{progress}%</span>
              </div>
              <Progress value={progress} className="w-full" />
            </div>
          )}

          {results.length > 0 && (
            <Alert className="border-green-200 bg-green-50">
              <CheckCircle className="h-4 w-4 text-green-600" />
              <AlertDescription className="text-green-800">
                <div className="flex items-center justify-between">
                  <span>
                    Generated {results.filter(r => r.success).length} QR codes successfully
                  </span>
                  <div className="flex gap-2">
                    <Button size="sm" onClick={handleDownloadAll}>
                      <Download className="h-4 w-4 mr-2" />
                      Download All
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={handleDownloadPDF}
                      disabled={results.filter(r => r.success).length === 0}
                    >
                      <FileText className="h-4 w-4 mr-2" />
                      Download PDF
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={handlePrintAll}
                      disabled={results.filter(r => r.success).length === 0}
                    >
                      <Printer className="h-4 w-4 mr-2" />
                      Print All QR Labels
                    </Button>
                  </div>
                </div>
              </AlertDescription>
            </Alert>
          )}

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Asset Selection */}
            <div className="lg:col-span-2 space-y-4">
              <div className="flex items-center justify-between">
                <h4 className="font-medium">
                  Select Assets ({selectedAssets.length} of {assetsWithoutQR.length} selected)
                </h4>
                <div className="flex space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleSelectAll}
                    disabled={assetsWithoutQR.length === 0}
                  >
                    {allSelected ? 'Deselect All' : 'Select All'}
                  </Button>
                  <Button
                    onClick={handleBulkGenerate}
                    disabled={!someSelected || loading}
                    size="sm"
                  >
                    Generate QR Codes
                  </Button>
                </div>
              </div>

              {assetsWithoutQR.length === 0 ? (
                <Alert>
                  <CheckCircle className="h-4 w-4" />
                  <AlertDescription>All assets already have QR codes generated!</AlertDescription>
                </Alert>
              ) : (
                <Card>
                  <CardContent className="p-0">
                    <Table>
                      <TableHeader>
                        <TableRow>
                          <TableHead className="w-12">
                            <Checkbox checked={allSelected} onCheckedChange={handleSelectAll} />
                          </TableHead>
                          <TableHead>Asset ID</TableHead>
                          <TableHead>Name</TableHead>
                          <TableHead>Category</TableHead>
                          <TableHead>Status</TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {assetsWithoutQR.map(asset => (
                          <TableRow key={asset.id}>
                            <TableCell>
                              <Checkbox
                                checked={selectedAssets.includes(asset.asset_id)}
                                onCheckedChange={() => handleSelectAsset(asset.asset_id)}
                              />
                            </TableCell>
                            <TableCell className="font-medium">{asset.asset_id}</TableCell>
                            <TableCell>{asset.name}</TableCell>
                            <TableCell className="capitalize">{asset.category}</TableCell>
                            <TableCell className="capitalize">{asset.status}</TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </CardContent>
                </Card>
              )}
            </div>

            {/* Tools and Templates */}
            <div className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Import Tools</CardTitle>
                  <CardDescription>Bulk import assets from CSV</CardDescription>
                </CardHeader>
                <CardContent className="space-y-3">
                  <Button variant="outline" className="w-full" onClick={generateCSVTemplate}>
                    <FileText className="h-4 w-4 mr-2" />
                    Download CSV Template
                  </Button>

                  <Button variant="outline" className="w-full" disabled>
                    <Upload className="h-4 w-4 mr-2" />
                    Upload CSV File
                  </Button>

                  <p className="text-xs text-gray-500">
                    Upload a CSV file with asset data to bulk create assets and generate QR codes
                  </p>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">QR Code Settings</CardTitle>
                  <CardDescription>Default settings for bulk generation</CardDescription>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="space-y-2">
                    <label className="text-sm font-medium">Size</label>
                    <select className="w-full px-3 py-2 border rounded-md text-sm">
                      <option value="200">200x200 px</option>
                      <option value="300">300x300 px</option>
                      <option value="400">400x400 px</option>
                    </select>
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-medium">Format</label>
                    <select className="w-full px-3 py-2 border rounded-md text-sm">
                      <option value="png">PNG</option>
                      <option value="svg">SVG</option>
                      <option value="pdf">PDF</option>
                    </select>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>

          {/* Results Display */}
          {results.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Generation Results</CardTitle>
              </CardHeader>
              <CardContent>
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Asset ID</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {results.map(result => (
                      <TableRow key={result.assetId}>
                        <TableCell className="font-medium">{result.assetId}</TableCell>
                        <TableCell>
                          {result.success ? (
                            <span className="text-green-600 flex items-center">
                              <CheckCircle className="h-4 w-4 mr-1" />
                              Success
                            </span>
                          ) : (
                            <span className="text-red-600 flex items-center">
                              <AlertTriangle className="h-4 w-4 mr-1" />
                              Failed
                            </span>
                          )}
                        </TableCell>
                        <TableCell>
                          {result.success && (
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => {
                                const link = document.createElement('a')
                                link.download = `qr-${result.assetId}.png`
                                link.href = result.qrCode
                                link.click()
                              }}
                            >
                              <Download className="h-4 w-4 mr-1" />
                              Download
                            </Button>
                          )}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </CardContent>
            </Card>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

import { useEffect, useState } from 'react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Alert, AlertDescription } from '@/components/ui/alert'

const FIELD_OPTIONS = [
  { value: 'asset_id', label: 'Asset ID' },
  { value: 'name', label: 'Name' },
  { value: 'category', label: 'Category' },
  { value: 'status', label: 'Status' },
  { value: 'created_at', label: 'Created At' },
]

export default function CustomReportBuilder({
  report,
  onClose,
}: {
  report?: any
  onClose: () => void
}) {
  const [name, setName] = useState(report?.name || '')
  const [fields, setFields] = useState<string[]>(report?.config?.fields || ['asset_id', 'name'])
  const [dateFrom, setDateFrom] = useState(report?.config?.dateFrom || '')
  const [dateTo, setDateTo] = useState(report?.config?.dateTo || '')
  const [category, setCategory] = useState(report?.config?.category || '')
  const [status, setStatus] = useState(report?.config?.status || '')
  const [groupBy, setGroupBy] = useState(report?.config?.groupBy || '')
  const [sortBy, setSortBy] = useState(report?.config?.sortBy || '')
  const [sortDir, setSortDir] = useState(report?.config?.sortDir || 'asc')
  const [error, setError] = useState<string | null>(null)
  const [saving, setSaving] = useState(false)

  const handleFieldChange = (field: string) => {
    setFields(prev => (prev.includes(field) ? prev.filter(f => f !== field) : [...prev, field]))
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setError(null)
    setSaving(true)
    try {
      const config = { fields, dateFrom, dateTo, category, status, groupBy, sortBy, sortDir }
      let res, data
      if (report) {
        res = await fetch(`/api/custom-reports/${report.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ name, config }),
        })
        data = await res.json()
      } else {
        res = await fetch('/api/custom-reports', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ name, config }),
        })
        data = await res.json()
      }
      if (data.error) throw new Error(data.error)
      onClose()
    } catch (e: any) {
      setError(e.message)
    } finally {
      setSaving(false)
    }
  }

  return (
    <div className="border rounded p-4 mt-6 bg-gray-50">
      <form className="space-y-4" onSubmit={handleSubmit}>
        <div>
          <label className="block text-sm font-medium mb-1">Report Name</label>
          <Input value={name} onChange={e => setName(e.target.value)} required />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Fields</label>
          <div className="flex gap-2 flex-wrap">
            {FIELD_OPTIONS.map(opt => (
              <label key={opt.value} className="flex items-center gap-1">
                <input
                  type="checkbox"
                  checked={fields.includes(opt.value)}
                  onChange={() => handleFieldChange(opt.value)}
                />
                {opt.label}
              </label>
            ))}
          </div>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Filters</label>
          <div className="flex gap-2 flex-wrap">
            <div>
              <label className="block text-xs mb-0.5">Date From</label>
              <input
                type="date"
                value={dateFrom}
                onChange={e => setDateFrom(e.target.value)}
                className="border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-xs mb-0.5">Date To</label>
              <input
                type="date"
                value={dateTo}
                onChange={e => setDateTo(e.target.value)}
                className="border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-xs mb-0.5">Category</label>
              <input
                type="text"
                value={category}
                onChange={e => setCategory(e.target.value)}
                placeholder="e.g. IT"
                className="border rounded px-2 py-1"
              />
            </div>
            <div>
              <label className="block text-xs mb-0.5">Status</label>
              <input
                type="text"
                value={status}
                onChange={e => setStatus(e.target.value)}
                placeholder="e.g. active"
                className="border rounded px-2 py-1"
              />
            </div>
          </div>
        </div>
        <div className="flex gap-4">
          <div>
            <label className="block text-sm font-medium mb-1">Group by</label>
            <select
              className="border rounded px-2 py-1"
              value={groupBy}
              onChange={e => setGroupBy(e.target.value)}
            >
              <option value="">None</option>
              {FIELD_OPTIONS.map(opt => (
                <option key={opt.value} value={opt.value}>
                  {opt.label}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Sort by</label>
            <select
              className="border rounded px-2 py-1"
              value={sortBy}
              onChange={e => setSortBy(e.target.value)}
            >
              <option value="">None</option>
              {FIELD_OPTIONS.map(opt => (
                <option key={opt.value} value={opt.value}>
                  {opt.label}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Sort direction</label>
            <select
              className="border rounded px-2 py-1"
              value={sortDir}
              onChange={e => setSortDir(e.target.value)}
            >
              <option value="asc">Ascending</option>
              <option value="desc">Descending</option>
            </select>
          </div>
        </div>
        {error && (
          <Alert variant="destructive">
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}
        <div className="flex gap-2">
          <Button type="submit" disabled={saving}>
            {saving ? 'Saving...' : 'Save Report'}
          </Button>
          <Button type="button" variant="outline" onClick={onClose}>
            Cancel
          </Button>
        </div>
      </form>
    </div>
  )
}

