import React from 'react'
// You can use a simple calendar library or build a basic grid
// For brevity, this is a minimal calendar view

interface MaintenanceCalendarProps {
  assetId: string
  schedules: any[]
  history: any[]
}

export default function MaintenanceCalendar({
  assetId,
  schedules,
  history,
}: MaintenanceCalendarProps) {
  // Flatten all dates (next_due for schedules, performed_at for history)
  const events = [
    ...schedules.map(s => ({
      date: s.next_due,
      type: s.status === 'overdue' ? 'overdue' : 'due',
      label: s.type,
    })),
    ...history.map(h => ({ date: h.performed_at, type: 'completed', label: 'Completed' })),
  ]
  // Group by date
  const grouped = events.reduce(
    (acc, ev) => {
      if (!ev.date) return acc
      acc[ev.date] = acc[ev.date] || []
      acc[ev.date].push(ev)
      return acc
    },
    {} as Record<string, any[]>
  )

  // Show current month
  const today = new Date()
  const year = today.getFullYear()
  const month = today.getMonth()
  const daysInMonth = new Date(year, month + 1, 0).getDate()
  const days = Array.from({ length: daysInMonth }, (_, i) => new Date(year, month, i + 1))

  return (
    <div className="border rounded p-4 bg-white">
      <h3 className="font-semibold mb-2">Maintenance Calendar</h3>
      <div className="grid grid-cols-7 gap-2">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(d => (
          <div key={d} className="text-xs font-bold text-center">
            {d}
          </div>
        ))}
        {days.map(date => {
          const key = date.toISOString().slice(0, 10)
          const evs = grouped[key] || []
          return (
            <div
              key={key}
              className="h-16 border rounded flex flex-col items-center justify-center bg-gray-50 relative"
            >
              <span className="text-xs text-gray-700">{date.getDate()}</span>
              {evs.map((ev, i) => (
                <span
                  key={i}
                  className={
                    ev.type === 'overdue'
                      ? 'text-red-600 text-xs font-bold'
                      : ev.type === 'due'
                        ? 'text-yellow-600 text-xs font-bold'
                        : 'text-green-600 text-xs font-bold'
                  }
                >
                  {ev.label}
                </span>
              ))}
            </div>
          )
        })}
      </div>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Button } from '@/components/ui/button'
import { Wifi, WifiOff, RefreshCw } from 'lucide-react'
import { firebaseConfig, vapidKey } from '@/lib/firebase'

export default function NetworkStatus() {
  const [isOnline, setIsOnline] = useState(true)
  const [showOfflineMessage, setShowOfflineMessage] = useState(false)
  const [permission, setPermission] = useState<NotificationPermission | null>(null)
  const [token, setToken] = useState<string | null>(null)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    const handleOnline = () => {
      setIsOnline(true)
      setShowOfflineMessage(false)
    }

    const handleOffline = () => {
      setIsOnline(false)
      setShowOfflineMessage(true)
    }

    // Check initial status
    setIsOnline(navigator.onLine)

    window.addEventListener('online', handleOnline)
    window.addEventListener('offline', handleOffline)

    if (typeof window !== 'undefined' && 'Notification' in window) {
      setPermission(Notification.permission)
    }

    return () => {
      window.removeEventListener('online', handleOnline)
      window.removeEventListener('offline', handleOffline)
    }
  }, [])

  const subscribe = async () => {
    try {
      setError(null)
      // Modular Firebase imports
      const { initializeApp, getApps } = await import('firebase/app')
      const { getMessaging, getToken } = await import('firebase/messaging')
      let app
      if (!getApps().length) {
        app = initializeApp(firebaseConfig)
      } else {
        app = getApps()[0]
      }
      const messaging = getMessaging(app)
      const currentToken = await getToken(messaging, { vapidKey })
      setToken(currentToken)
      setPermission(Notification.permission)
      // Send currentToken to your backend to associate with the user
      await fetch('/api/notifications/token', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ token: currentToken }),
      })
    } catch (err: any) {
      setError(err.message || 'Failed to subscribe to notifications')
    }
  }

  if (!showOfflineMessage && isOnline) {
    return null
  }

  return (
    <div>
      <Alert variant={isOnline ? 'default' : 'destructive'} className="mb-4">
        {isOnline ? <Wifi className="h-4 w-4" /> : <WifiOff className="h-4 w-4" />}
        <AlertDescription className="flex items-center justify-between">
          <span>
            {isOnline
              ? 'Connection restored! You may need to refresh the page.'
              : 'You appear to be offline. Some features may not work properly.'}
          </span>
          {isOnline && (
            <Button size="sm" variant="outline" onClick={() => window.location.reload()}>
              <RefreshCw className="h-4 w-4 mr-2" />
              Refresh
            </Button>
          )}
        </AlertDescription>
      </Alert>
      <div className="my-4">
        <div className="mb-2">
          Push Notifications: <b>{permission}</b>
        </div>
        <Button onClick={subscribe} disabled={permission === 'granted'}>
          {permission === 'granted' ? 'Subscribed' : 'Enable Notifications'}
        </Button>
        {token && <div className="text-xs mt-2 break-all">Token: {token}</div>}
        {error && <div className="text-xs text-red-600 mt-2">{error}</div>}
      </div>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { createClient } from '@/lib/supabase/client'
import { Bell, CheckCircle, Loader2 } from 'lucide-react'
import { Button } from '@/components/ui/button'
import Link from 'next/link'

export default function NotificationsBell() {
  const [notifications, setNotifications] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [open, setOpen] = useState(false)
  const [marking, setMarking] = useState(false)
  const supabase = createClient()

  useEffect(() => {
    fetchNotifications()
    // Optionally, set up polling or Supabase Realtime here
  }, [])

  const fetchNotifications = async () => {
    setLoading(true)
    const { data, error } = await supabase
      .from('notifications')
      .select('*')
      .order('created_at', { ascending: false })
      .limit(10)
    setNotifications(data || [])
    setLoading(false)
  }

  const unreadCount = notifications.filter(n => !n.read).length

  const markAllAsRead = async () => {
    setMarking(true)
    await supabase.from('notifications').update({ read: true }).eq('read', false)
    await fetchNotifications()
    setMarking(false)
  }

  return (
    <div className="relative">
      <Button
        variant="ghost"
        size="icon"
        onClick={() => setOpen(v => !v)}
        aria-label="Notifications"
      >
        <Bell className="h-5 w-5" />
        {unreadCount > 0 && (
          <span className="absolute -top-1 -right-1 bg-red-500 text-white rounded-full text-xs w-5 h-5 flex items-center justify-center">
            {unreadCount}
          </span>
        )}
      </Button>
      {open && (
        <div className="absolute right-0 mt-2 w-80 bg-white shadow-lg rounded-lg z-50 border">
          <div className="p-4 border-b flex items-center justify-between">
            <span className="font-semibold">Notifications</span>
            <Button
              size="sm"
              variant="outline"
              onClick={markAllAsRead}
              disabled={marking || unreadCount === 0}
            >
              {marking ? (
                <Loader2 className="h-4 w-4 animate-spin" />
              ) : (
                <CheckCircle className="h-4 w-4 mr-1" />
              )}
              Mark all as read
            </Button>
          </div>
          <div className="max-h-96 overflow-y-auto">
            {loading ? (
              <div className="flex justify-center p-4">
                <Loader2 className="h-6 w-6 animate-spin" />
              </div>
            ) : notifications.length === 0 ? (
              <div className="p-4 text-center text-gray-500">No notifications</div>
            ) : (
              notifications.map(n => (
                <div
                  key={n.id}
                  className={`p-4 border-b last:border-b-0 ${n.read ? 'bg-gray-50' : 'bg-blue-50'}`}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <div className="font-medium">{n.message}</div>
                      <div className="text-xs text-gray-500">
                        {new Date(n.created_at).toLocaleString()}
                      </div>
                    </div>
                    {n.link && (
                      <Link href={n.link} className="text-blue-600 hover:underline text-xs ml-2">
                        View
                      </Link>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { createClient } from '@/lib/supabase/client'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { AlertTriangle, CheckCircle, RefreshCw } from 'lucide-react'

export default function ProfileCheck() {
  const [profileStatus, setProfileStatus] = useState<'checking' | 'exists' | 'missing' | 'error'>(
    'checking'
  )
  const [error, setError] = useState<string | null>(null)
  const [creating, setCreating] = useState(false)
  const supabase = createClient()

  const checkProfile = async () => {
    try {
      setProfileStatus('checking')
      setError(null)

      const {
        data: { user },
      } = await supabase.auth.getUser()

      if (!user) {
        setError('No authenticated user found')
        setProfileStatus('error')
        return
      }

      const { data: profile, error: profileError } = await supabase
        .from('profiles')
        .select('id, email, full_name, role')
        .eq('id', user.id)
        .single()

      if (profileError && profileError.code === 'PGRST116') {
        setProfileStatus('missing')
      } else if (profileError) {
        setError(profileError.message)
        setProfileStatus('error')
      } else {
        setProfileStatus('exists')
      }
    } catch (err) {
      setError('Failed to check profile')
      setProfileStatus('error')
    }
  }

  const createProfile = async () => {
    try {
      setCreating(true)
      setError(null)

      const {
        data: { user },
      } = await supabase.auth.getUser()

      if (!user) {
        setError('No authenticated user found')
        return
      }

      const { error: createError } = await supabase.from('profiles').insert({
        id: user.id,
        email: user.email!,
        full_name:
          user.user_metadata?.full_name ||
          user.user_metadata?.name ||
          user.email?.split('@')[0] ||
          null,
        avatar_url: user.user_metadata?.avatar_url || user.user_metadata?.picture || null,
        role: 'user',
      })

      if (createError) {
        setError(createError.message)
      } else {
        setProfileStatus('exists')
        // Refresh the page to update the UI
        window.location.reload()
      }
    } catch (err) {
      setError('Failed to create profile')
    } finally {
      setCreating(false)
    }
  }

  useEffect(() => {
    checkProfile()
  }, [])

  if (profileStatus === 'checking') {
    return (
      <Alert>
        <RefreshCw className="h-4 w-4 animate-spin" />
        <AlertDescription>Checking user profile...</AlertDescription>
      </Alert>
    )
  }

  if (profileStatus === 'exists') {
    return (
      <Alert className="border-green-200 bg-green-50">
        <CheckCircle className="h-4 w-4 text-green-600" />
        <AlertDescription className="text-green-800">
          User profile is properly configured
        </AlertDescription>
      </Alert>
    )
  }

  if (profileStatus === 'missing') {
    return (
      <Card className="border-orange-200 bg-orange-50">
        <CardHeader>
          <CardTitle className="text-orange-800 flex items-center">
            <AlertTriangle className="h-5 w-5 mr-2" />
            Profile Setup Required
          </CardTitle>
          <CardDescription className="text-orange-700">
            Your user profile needs to be created before you can add assets.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                Your user profile is missing from the database. This is required to create assets.
              </AlertDescription>
            </Alert>

            <Button onClick={createProfile} disabled={creating} className="w-full">
              {creating ? (
                <>
                  <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                  Creating Profile...
                </>
              ) : (
                'Create User Profile'
              )}
            </Button>

            {error && (
              <Alert variant="destructive">
                <AlertTriangle className="h-4 w-4" />
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}
          </div>
        </CardContent>
      </Card>
    )
  }

  return (
    <Alert variant="destructive">
      <AlertTriangle className="h-4 w-4" />
      <AlertDescription>
        Error checking profile: {error}
        <Button variant="outline" size="sm" onClick={checkProfile} className="ml-2">
          Retry
        </Button>
      </AlertDescription>
    </Alert>
  )
}

'use client'

import { useEffect, useState, useRef } from 'react'
import { useRouter } from 'next/navigation'
import { createClient } from '@/lib/supabase/client'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Label } from '@/components/ui/label'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { LogOut, Upload, User, Mail, Building2, Loader2 } from 'lucide-react'
import SignOutButton from '@/components/auth/sign-out-button'

export default function ProfileSettings() {
  const [profile, setProfile] = useState<any>(null)
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [fullName, setFullName] = useState('')
  const [avatarUrl, setAvatarUrl] = useState('')
  const [avatarFile, setAvatarFile] = useState<File | null>(null)
  const fileInputRef = useRef<HTMLInputElement>(null)
  const router = useRouter()
  const supabase = createClient()

  useEffect(() => {
    fetchProfile()
  }, [])

  const fetchProfile = async () => {
    setLoading(true)
    setError('')
    const {
      data: { user },
    } = await supabase.auth.getUser()
    if (!user) {
      router.push('/login')
      return
    }
    const { data, error } = await supabase
      .from('profiles')
      .select('*', { count: 'exact' })
      .eq('id', user.id)
      .single()
    if (error) {
      setError('Failed to load profile.')
    } else {
      setProfile(data)
      setFullName(data.full_name || '')
      setAvatarUrl(data.avatar_url || '')
    }
    setLoading(false)
  }

  const handleAvatarChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0]
    if (!file) return
    setAvatarFile(file)
    // Optionally preview
    const reader = new FileReader()
    reader.onload = ev => {
      if (ev.target?.result) setAvatarUrl(ev.target.result as string)
    }
    reader.readAsDataURL(file)
  }

  const uploadAvatar = async (file: File) => {
    const {
      data: { user },
    } = await supabase.auth.getUser()
    if (!user) return null
    const fileExt = file.name.split('.').pop()
    const filePath = `avatars/${user.id}.${fileExt}`
    const { error: uploadError } = await supabase.storage
      .from('avatars')
      .upload(filePath, file, { upsert: true })
    if (uploadError) {
      setError('Failed to upload avatar.')
      return null
    }
    const { data } = supabase.storage.from('avatars').getPublicUrl(filePath)
    return data.publicUrl
  }

  const handleSave = async (e: React.FormEvent) => {
    e.preventDefault()
    setSaving(true)
    setError('')
    setSuccess('')
    let newAvatarUrl = avatarUrl
    if (avatarFile) {
      const url = await uploadAvatar(avatarFile)
      if (url) newAvatarUrl = url
    }
    const {
      data: { user },
    } = await supabase.auth.getUser()
    if (!user) {
      setError('Not authenticated.')
      setSaving(false)
      return
    }
    const { error: updateError } = await supabase
      .from('profiles')
      .update({
        full_name: fullName,
        avatar_url: newAvatarUrl,
      })
      .eq('id', user.id)
    if (updateError) {
      setError('Failed to update profile.')
    } else {
      setSuccess('Profile updated successfully.')
      setAvatarFile(null)
    }
    setSaving(false)
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
      </div>
    )
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-8 px-4">
      <Card className="w-full max-w-lg shadow-xl border-0 bg-white/90 backdrop-blur-sm">
        <CardHeader>
          <CardTitle className="text-2xl font-bold">Profile Settings</CardTitle>
          <CardDescription>Manage your personal information and avatar</CardDescription>
        </CardHeader>
        <CardContent>
          {error && (
            <Alert variant="destructive" className="mb-4">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
          {success && (
            <Alert className="mb-4 border-green-200 bg-green-50">
              <AlertDescription className="text-green-800">{success}</AlertDescription>
            </Alert>
          )}
          <form onSubmit={handleSave} className="space-y-6">
            <div className="flex items-center space-x-6">
              <div className="relative w-20 h-20 rounded-full overflow-hidden border-2 border-blue-200 bg-gray-100">
                {avatarUrl ? (
                  <img src={avatarUrl} alt="Avatar" className="object-cover w-full h-full" />
                ) : (
                  <User className="w-full h-full text-gray-400" />
                )}
                <input
                  type="file"
                  accept="image/*"
                  className="hidden"
                  ref={fileInputRef}
                  onChange={handleAvatarChange}
                />
                <Button
                  type="button"
                  size="sm"
                  variant="outline"
                  className="absolute bottom-0 right-0 rounded-full p-1"
                  onClick={() => fileInputRef.current?.click()}
                >
                  <Upload className="h-4 w-4" />
                </Button>
              </div>
              <div className="flex-1">
                <Label htmlFor="fullName">Full Name</Label>
                <Input
                  id="fullName"
                  type="text"
                  value={fullName}
                  onChange={e => setFullName(e.target.value)}
                  className="mt-1"
                  required
                />
              </div>
            </div>
            <div>
              <Label>Email</Label>
              <Input
                type="email"
                value={profile?.email || ''}
                readOnly
                className="mt-1 bg-gray-100"
              />
            </div>
            <div>
              <Label>Organization</Label>
              <Input
                type="text"
                value={profile?.tenant_id || ''}
                readOnly
                className="mt-1 bg-gray-100"
              />
            </div>
            {/* Password change could be added here */}
            <div className="flex justify-between items-center pt-4">
              <Button type="submit" disabled={saving}>
                {saving ? <Loader2 className="h-4 w-4 animate-spin mr-2" /> : null}
                Save Changes
              </Button>
              <SignOutButton />
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { AlertTriangle, CheckCircle, RefreshCw, User } from 'lucide-react'
import { createUserProfile, checkUserProfile } from '@/lib/profile-actions'

export default function ProfileSetup() {
  const [profileStatus, setProfileStatus] = useState<'checking' | 'exists' | 'missing' | 'error'>(
    'checking'
  )
  const [error, setError] = useState<string | null>(null)
  const [creating, setCreating] = useState(false)
  const [userInfo, setUserInfo] = useState<any>(null)
  const [formData, setFormData] = useState({
    full_name: '',
    role: 'user',
  })

  const checkProfile = async () => {
    try {
      setProfileStatus('checking')
      setError(null)

      const result = await checkUserProfile()

      if (result.error) {
        setError(result.error)
        setProfileStatus('error')
        return
      }

      if (result.user) {
        setUserInfo(result.user)
        // Pre-fill form with user metadata
        setFormData({
          full_name:
            result.user.user_metadata?.full_name ||
            result.user.user_metadata?.name ||
            result.user.email?.split('@')[0] ||
            '',
          role: 'user',
        })
      }

      if (result.exists) {
        setProfileStatus('exists')
      } else {
        setProfileStatus('missing')
      }
    } catch (err) {
      console.error('Unexpected error checking profile:', err)
      setError('Failed to check profile')
      setProfileStatus('error')
    }
  }

  const createProfile = async () => {
    try {
      setCreating(true)
      setError(null)

      const result = await createUserProfile({
        full_name: formData.full_name,
        role: formData.role,
      })

      if (result.error) {
        setError(result.error)
      } else {
        setProfileStatus('exists')
        // Refresh the page to update all components
        setTimeout(() => {
          window.location.reload()
        }, 1000)
      }
    } catch (err) {
      console.error('Unexpected error creating profile:', err)
      setError('An unexpected error occurred while creating your profile')
    } finally {
      setCreating(false)
    }
  }

  useEffect(() => {
    checkProfile()
  }, [])

  if (profileStatus === 'checking') {
    return (
      <Alert>
        <RefreshCw className="h-4 w-4 animate-spin" />
        <AlertDescription>Checking user profile...</AlertDescription>
      </Alert>
    )
  }

  if (profileStatus === 'exists') {
    return (
      <Alert className="border-green-200 bg-green-50">
        <CheckCircle className="h-4 w-4 text-green-600" />
        <AlertDescription className="text-green-800">
           User profile is properly configured. You can now add assets!
        </AlertDescription>
      </Alert>
    )
  }

  if (profileStatus === 'missing') {
    return (
      <Card className="border-orange-200 bg-orange-50">
        <CardHeader>
          <CardTitle className="text-orange-800 flex items-center">
            <User className="h-5 w-5 mr-2" />
            Complete Your Profile Setup
          </CardTitle>
          <CardDescription className="text-orange-700">
            We need to create your user profile to enable asset management features.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <Alert>
            <AlertTriangle className="h-4 w-4" />
            <AlertDescription>
              <strong>Profile Required:</strong> Your user profile is missing from the database.
              This is required to create and manage assets.
            </AlertDescription>
          </Alert>

          {userInfo && (
            <div className="bg-blue-50 p-3 rounded-lg">
              <h4 className="font-medium text-blue-800 mb-2">Account Information</h4>
              <div className="text-sm text-blue-700 space-y-1">
                <p>
                  <strong>Email:</strong> {userInfo.email}
                </p>
                <p>
                  <strong>User ID:</strong> {userInfo.id}
                </p>
                <p>
                  <strong>Auth Provider:</strong> {userInfo.app_metadata?.provider || 'email'}
                </p>
              </div>
            </div>
          )}

          <div className="space-y-3">
            <div className="space-y-2">
              <Label htmlFor="full_name">Full Name</Label>
              <Input
                id="full_name"
                placeholder="Enter your full name"
                value={formData.full_name}
                onChange={e => setFormData({ ...formData, full_name: e.target.value })}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="role">Role</Label>
              <select
                id="role"
                value={formData.role}
                onChange={e => setFormData({ ...formData, role: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="user">User</option>
                <option value="manager">Manager</option>
                <option value="admin">Admin</option>
              </select>
            </div>
          </div>

          <Button onClick={createProfile} disabled={creating} className="w-full">
            {creating ? (
              <>
                <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                Creating Profile...
              </>
            ) : (
              <>
                <User className="h-4 w-4 mr-2" />
                Create My Profile
              </>
            )}
          </Button>

          {error && (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                <strong>Error:</strong> {error}
                <div className="mt-2">
                  <Button variant="outline" size="sm" onClick={checkProfile}>
                    Retry Check
                  </Button>
                </div>
              </AlertDescription>
            </Alert>
          )}

          <div className="text-xs text-gray-600 bg-gray-50 p-3 rounded">
            <strong>Why is this needed?</strong> Your profile stores essential information like your
            name and role, which is required for asset ownership and team collaboration features.
          </div>
        </CardContent>
      </Card>
    )
  }

  return (
    <Alert variant="destructive">
      <AlertTriangle className="h-4 w-4" />
      <AlertDescription>
        <strong>Profile Check Failed:</strong> {error}
        <div className="mt-2">
          <Button variant="outline" size="sm" onClick={checkProfile}>
            <RefreshCw className="h-4 w-4 mr-2" />
            Retry Check
          </Button>
        </div>
      </AlertDescription>
    </Alert>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Progress } from '@/components/ui/progress'
import { BarChart3, TrendingUp, Scan, QrCode, MapPin, Activity } from 'lucide-react'
import { createClient } from '@/lib/supabase/client'

interface QRAnalyticsProps {
  assets: Array<{
    id: string
    asset_id: string
    name: string
    category: string
    qr_code?: string | null
    location?: string | null
  }>
}

export default function QRAnalytics({ assets }: QRAnalyticsProps) {
  const [analytics, setAnalytics] = useState({
    totalQRCodes: 0,
    qrCoverage: 0,
    scansByCategory: {} as Record<string, number>,
    scansByLocation: {} as Record<string, number>,
    recentScans: [] as Array<{
      assetId: string
      assetName: string
      timestamp: string
      location?: string
    }>,
  })
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const fetchAnalytics = async () => {
      setLoading(true)
      // Calculate analytics from assets data
      const totalAssets = assets.length
      const assetsWithQR = assets.filter(asset => asset.qr_code).length
      const qrCoverage = totalAssets > 0 ? Math.round((assetsWithQR / totalAssets) * 100) : 0

      // Fetch real QR analytics from Supabase
      let scansByCategory: Record<string, number> = {}
      let scansByLocation: Record<string, number> = {}
      let recentScans: Array<{
        assetId: string
        assetName: string
        timestamp: string
        location?: string
      }> = []
      try {
        const supabase = createClient()
        // Get recent QR analytics events (scans and generations)
        const { data: qrEvents, error } = await supabase
          .from('analytics_events')
          .select('asset_id, event_type, created_at, metadata')
          .in('event_type', ['asset_scanned', 'qr_generated'])
          .order('created_at', { ascending: false })
          .limit(20)
        if (!error && qrEvents && qrEvents.length > 0) {
          // Map asset_id to asset info
          const assetMap = Object.fromEntries(assets.map(a => [a.id, a]))
          // Count scans by category/location
          for (const event of qrEvents) {
            const asset = assetMap[event.asset_id]
            if (!asset) continue
            // By category
            if (asset.category) {
              scansByCategory[asset.category] = (scansByCategory[asset.category] || 0) + 1
            }
            // By location
            if (asset.location) {
              scansByLocation[asset.location] = (scansByLocation[asset.location] || 0) + 1
            }
            // Recent scans
            if (event.event_type === 'asset_scanned' || event.event_type === 'qr_generated') {
              recentScans.push({
                assetId: asset.asset_id,
                assetName: asset.name,
                timestamp: event.created_at,
                location: asset.location ?? undefined,
              })
            }
          }
        } else {
          // Fallback to mock data if no analytics
          scansByCategory = {
            'it-equipment': 45,
            furniture: 23,
            'av-equipment': 18,
            vehicles: 12,
            tools: 8,
          }
          scansByLocation = {
            'Office A': 32,
            Warehouse: 28,
            'Office B': 21,
            'Conference Room': 15,
            Storage: 10,
          }
          recentScans = [
            {
              assetId: 'AST-001',
              assetName: 'MacBook Pro 16"',
              timestamp: new Date(Date.now() - 5 * 60 * 1000).toISOString(),
              location: 'Office A',
            },
            {
              assetId: 'AST-002',
              assetName: 'Office Chair',
              timestamp: new Date(Date.now() - 15 * 60 * 1000).toISOString(),
              location: 'Office B',
            },
            {
              assetId: 'AST-003',
              assetName: 'Projector',
              timestamp: new Date(Date.now() - 30 * 60 * 1000).toISOString(),
              location: 'Conference Room',
            },
          ]
        }
      } catch (e) {
        // Fallback to mock data on error
        scansByCategory = {
          'it-equipment': 45,
          furniture: 23,
          'av-equipment': 18,
          vehicles: 12,
          tools: 8,
        }
        scansByLocation = {
          'Office A': 32,
          Warehouse: 28,
          'Office B': 21,
          'Conference Room': 15,
          Storage: 10,
        }
        recentScans = [
          {
            assetId: 'AST-001',
            assetName: 'MacBook Pro 16"',
            timestamp: new Date(Date.now() - 5 * 60 * 1000).toISOString(),
            location: 'Office A',
          },
          {
            assetId: 'AST-002',
            assetName: 'Office Chair',
            timestamp: new Date(Date.now() - 15 * 60 * 1000).toISOString(),
            location: 'Office B',
          },
          {
            assetId: 'AST-003',
            assetName: 'Projector',
            timestamp: new Date(Date.now() - 30 * 60 * 1000).toISOString(),
            location: 'Conference Room',
          },
        ]
      }
      setAnalytics({
        totalQRCodes: assetsWithQR,
        qrCoverage,
        scansByCategory,
        scansByLocation,
        recentScans,
      })
      setLoading(false)
    }
    fetchAnalytics()
  }, [assets])

  const formatTimeAgo = (timestamp: string) => {
    const now = new Date()
    const scanTime = new Date(timestamp)
    const diffInMinutes = Math.floor((now.getTime() - scanTime.getTime()) / (1000 * 60))

    if (diffInMinutes < 1) return 'Just now'
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h ago`
    return `${Math.floor(diffInMinutes / 1440)}d ago`
  }

  return (
    <div className="space-y-6">
      {/* Overview Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <QrCode className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total QR Codes</p>
                <p className="text-2xl font-bold">{analytics.totalQRCodes}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <BarChart3 className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Coverage</p>
                <p className="text-2xl font-bold">{analytics.qrCoverage}%</p>
              </div>
            </div>
            <Progress value={analytics.qrCoverage} className="mt-2" />
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <Scan className="h-8 w-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Scans</p>
                <p className="text-2xl font-bold">
                  {Object.values(analytics.scansByCategory).reduce((a, b) => a + b, 0)}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <TrendingUp className="h-8 w-8 text-orange-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">This Week</p>
                <p className="text-2xl font-bold">+24%</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Scans by Category */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <BarChart3 className="h-5 w-5 mr-2" />
              Scans by Category
            </CardTitle>
            <CardDescription>QR code usage across asset categories</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            {Object.entries(analytics.scansByCategory).map(([category, scans]) => {
              const maxScans = Math.max(...Object.values(analytics.scansByCategory))
              const percentage = (scans / maxScans) * 100

              return (
                <div key={category} className="space-y-2">
                  <div className="flex items-center justify-between">
                    <span className="text-sm font-medium capitalize">
                      {category.replace('-', ' ')}
                    </span>
                    <Badge variant="outline">{scans} scans</Badge>
                  </div>
                  <Progress value={percentage} />
                </div>
              )
            })}
          </CardContent>
        </Card>

        {/* Scans by Location */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <MapPin className="h-5 w-5 mr-2" />
              Scans by Location
            </CardTitle>
            <CardDescription>QR code usage across locations</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            {Object.entries(analytics.scansByLocation).map(([location, scans]) => {
              const maxScans = Math.max(...Object.values(analytics.scansByLocation))
              const percentage = (scans / maxScans) * 100

              return (
                <div key={location} className="space-y-2">
                  <div className="flex items-center justify-between">
                    <span className="text-sm font-medium">{location}</span>
                    <Badge variant="outline">{scans} scans</Badge>
                  </div>
                  <Progress value={percentage} />
                </div>
              )
            })}
          </CardContent>
        </Card>
      </div>

      {/* Recent Activity */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Activity className="h-5 w-5 mr-2" />
            Recent QR Scans
          </CardTitle>
          <CardDescription>Latest QR code scan activity</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {analytics.recentScans.map((scan, index) => (
              <div key={index} className="flex items-center space-x-3 p-3 border rounded-lg">
                <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                <div className="flex-1">
                  <p className="text-sm font-medium">{scan.assetName}</p>
                  <p className="text-xs text-gray-500">
                    {scan.assetId}  {scan.location}
                  </p>
                </div>
                <div className="text-right">
                  <Badge variant="outline">Scanned</Badge>
                  <p className="text-xs text-gray-500 mt-1">{formatTimeAgo(scan.timestamp)}</p>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { QrCode, Download, Copy, CheckCircle, AlertTriangle, RefreshCw } from 'lucide-react'
import { generateAssetQRCode } from '@/lib/qr-actions'
import { useAuth } from '@/components/auth/auth-provider'
import { createClient } from '@/lib/supabase/client'
import { updateAssetQRCodeUrl } from '@/lib/qr-actions'
import { toast } from '@/components/ui/use-toast'
import clsx from 'clsx'
import { fetchQRTemplates, fetchDefaultQRTemplate } from '@/lib/qr-template-utils'
import QRLabel from '@/components/qr-label'

interface QRGeneratorProps {
  assets: any[]
  onQRGenerated: (assetId: string, qrCode: string) => void
  settings: {
    autoGenerate: boolean
    includeDetails: boolean
    trackAnalytics: boolean
    mobileNotifications: boolean
    defaultSize: string
    errorCorrection: string
    defaultFormat: string
  }
}

export default function QRGenerator({ assets, onQRGenerated, settings }: QRGeneratorProps) {
  const { user, loading: authLoading } = useAuth()
  const [selectedAssetId, setSelectedAssetId] = useState('')
  const [customAssetId, setCustomAssetId] = useState('')
  const [qrSize, setQrSize] = useState('200')
  const [qrColor, setQrColor] = useState('#000000')
  const [qrBgColor, setQrBgColor] = useState('#FFFFFF')
  const [generatedQR, setGeneratedQR] = useState<string | null>(null)
  const [assetUrl, setAssetUrl] = useState<string | null>(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [copied, setCopied] = useState(false)
  const [selectedAsset, setSelectedAsset] = useState<any | null>(null)
  const [generating, setGenerating] = useState(false)
  const [templates, setTemplates] = useState<any[]>([])
  const [selectedTemplateId, setSelectedTemplateId] = useState<string>('')
  const [templateConfig, setTemplateConfig] = useState<any>(null)

  useEffect(() => {
    async function loadTemplates() {
      try {
        const all = await fetchQRTemplates()
        setTemplates(all)
        const def = all.find((tpl: any) => tpl.is_default) || all[0]
        if (def) {
          setSelectedTemplateId(def.id)
          setTemplateConfig(def.config)
        }
      } catch {}
    }
    loadTemplates()
  }, [])

  useEffect(() => {
    if (!selectedTemplateId) return
    const tpl = templates.find(t => t.id === selectedTemplateId)
    if (tpl) setTemplateConfig(tpl.config)
  }, [selectedTemplateId, templates])

  const handleGenerate = async () => {
    const assetId = selectedAssetId || customAssetId
    if (!assetId) {
      setError('Please select or enter an Asset ID')
      return
    }

    if (!user) {
      setError('You must be logged in to generate QR codes. Please sign in and try again.')
      return
    }

    setLoading(true)
    setError(null)

    try {
      setGenerating(true)
      setSelectedAsset(assets.find(asset => asset.asset_id === assetId))

      const supabase = createClient() // <-- Add this line
      // Create QR code data with settings
      const qrData = settings.includeDetails
        ? {
            assetId: assetId,
            name: selectedAsset?.name || assetId,
            category: selectedAsset?.category || '',
            url: `${window.location.origin}/asset/${assetId}`,
          }
        : assetId

      const QRCode = (await import('qrcode')).default
      const qrCodeDataUrl = await QRCode.toDataURL(JSON.stringify(qrData), {
        width: parseInt(settings.defaultSize),
        margin: 1,
        errorCorrectionLevel: settings.errorCorrection as 'L' | 'M' | 'Q' | 'H',
      })

      // Instead of just saving the QR code image, render the label to a hidden div and use html2canvas
      const labelContainer = document.createElement('div')
      labelContainer.style.position = 'fixed'
      labelContainer.style.left = '-9999px'
      labelContainer.style.top = '0'
      document.body.appendChild(labelContainer)
      const label = (
        <QRLabel asset={selectedAsset} templateConfig={templateConfig} qrCodeUrl={qrCodeDataUrl} />
      )
      // Render the React element to the container
      // Use ReactDOM.render or createRoot depending on React version
      // (Assume React 18+)
      import('react-dom/client').then(({ createRoot }) => {
        const root = createRoot(labelContainer)
        root.render(label)
        setTimeout(async () => {
          const canvas = await (
            await import('html2canvas')
          ).default(labelContainer, { backgroundColor: '#fff', scale: 2 })
          const imgData = canvas.toDataURL('image/png')
          // Save or upload imgData as needed (replace qrCodeDataUrl with imgData)
          // ... existing upload logic ...
          root.unmount()
          document.body.removeChild(labelContainer)
        }, 100)
      })

      // Track analytics if enabled
      if (settings.trackAnalytics) {
        await supabase.from('analytics_events').insert({
          event_type: 'qr_generated',
          asset_id: selectedAsset?.id,
          user_id: user.id,
          metadata: { asset_name: selectedAsset?.name },
          created_at: new Date().toISOString(),
        })
      }

      toast({
        title: 'QR Code Generated',
        description: 'QR code has been generated and saved successfully',
      })

      setGeneratedQR(qrCodeDataUrl)
      setAssetUrl(qrCodeDataUrl)
      onQRGenerated(assetId, qrCodeDataUrl)
    } catch (error) {
      console.error('QR generation error:', error)
      toast({
        title: 'Generation Failed',
        description: 'Failed to generate QR code',
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
      setGenerating(false)
      setSelectedAsset(null)
    }
  }

  const handleDownload = () => {
    if (!generatedQR) return

    const link = document.createElement('a')
    link.download = `qr-${selectedAssetId || customAssetId}.png`
    link.href = generatedQR
    link.click()
  }

  const handleCopyUrl = async () => {
    if (!assetUrl) return

    try {
      await navigator.clipboard.writeText(assetUrl)
      setCopied(true)
      setTimeout(() => setCopied(false), 2000)
    } catch (err) {
      console.error('Failed to copy URL:', err)
    }
  }

  // Show loading state while auth is loading
  if (authLoading) {
    return (
      <Card>
        <CardContent className="pt-6">
          <div className="flex items-center justify-center py-8">
            <RefreshCw className="h-6 w-6 animate-spin mr-2" />
            <span>Loading authentication...</span>
          </div>
        </CardContent>
      </Card>
    )
  }

  // Show login prompt if not authenticated
  if (!user) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <QrCode className="h-5 w-5 mr-2" />
            QR Code Generator
          </CardTitle>
          <CardDescription>Generate QR codes for your assets</CardDescription>
        </CardHeader>
        <CardContent>
          <Alert>
            <AlertTriangle className="h-4 w-4" />
