                ) : (
                  <div className="text-center">
                    <QrCode className="h-16 w-16 mx-auto text-gray-400 mb-2" />
                    <p className="text-gray-500">QR code will appear here</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

import { useState } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Upload, Image as ImageIcon, AlertCircle } from 'lucide-react'
import { createClient } from '@/lib/supabase/client'
import { useToast } from '@/hooks/use-toast'

interface QRImageUploadProps {
  onUploadComplete: (assetId: string, imageUrl: string) => void
}

export default function QRImageUpload({ onUploadComplete }: QRImageUploadProps) {
  const [uploading, setUploading] = useState(false)
  const [selectedFile, setSelectedFile] = useState<File | null>(null)
  const [preview, setPreview] = useState<string | null>(null)
  const { toast } = useToast()

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]
    if (file) {
      if (!file.type.includes('image/')) {
        toast({
          title: 'Invalid File Type',
          description: 'Please select an image file',
          variant: 'destructive',
        })
        return
      }

      setSelectedFile(file)
      const reader = new FileReader()
      reader.onloadend = () => {
        setPreview(reader.result as string)
      }
      reader.readAsDataURL(file)
    }
  }

  const handleUpload = async () => {
    if (!selectedFile) return

    setUploading(true)
    try {
      const supabase = createClient()

      // Generate unique filename
      const fileExt = selectedFile.name.split('.').pop()
      const fileName = `${Math.random().toString(36).substring(2)}.${fileExt}`
      const filePath = `qr-codes/${fileName}`

      // Upload to Supabase Storage
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('assets')
        .upload(filePath, selectedFile)

      if (uploadError) throw uploadError

      // Get public URL
      const {
        data: { publicUrl },
      } = supabase.storage.from('assets').getPublicUrl(filePath)

      toast({
        title: 'Upload Successful',
        description: 'QR code image has been uploaded',
      })

      onUploadComplete('custom', publicUrl)

      // Reset state
      setSelectedFile(null)
      setPreview(null)
    } catch (error) {
      console.error('Upload error:', error)
      toast({
        title: 'Upload Failed',
        description: 'Failed to upload QR code image',
        variant: 'destructive',
      })
    } finally {
      setUploading(false)
    }
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <ImageIcon className="h-5 w-5 mr-2" />
          Upload QR Code Image
        </CardTitle>
        <CardDescription>Upload an existing QR code image for an asset</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="border-2 border-dashed rounded-lg p-6 text-center">
          {preview ? (
            <div className="space-y-4">
              <img src={preview} alt="Preview" className="max-w-[200px] mx-auto" />
              <Button
                variant="outline"
                onClick={() => {
                  setSelectedFile(null)
                  setPreview(null)
                }}
              >
                Remove
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              <Upload className="h-12 w-12 mx-auto text-gray-400" />
              <div>
                <Button variant="outline" asChild>
                  <label className="cursor-pointer">
                    <input
                      type="file"
                      className="hidden"
                      accept="image/*"
                      onChange={handleFileSelect}
                    />
                    Select QR Code Image
                  </label>
                </Button>
              </div>
              <p className="text-sm text-gray-500">Supports: PNG, JPG, JPEG, GIF (max 5MB)</p>
            </div>
          )}
        </div>

        {selectedFile && (
          <Button className="w-full" onClick={handleUpload} disabled={uploading}>
            {uploading ? 'Uploading...' : 'Upload QR Code'}
          </Button>
        )}

        <div className="flex items-start space-x-2 text-sm text-amber-600">
          <AlertCircle className="h-4 w-4 mt-0.5" />
          <p>
            Make sure the QR code image is clear and readable. Poor quality images may not scan
            properly.
          </p>
        </div>
      </CardContent>
    </Card>
  )
}

import React from 'react'

interface QRLabelProps {
  asset: any
  templateConfig: any
  qrCodeUrl?: string // Optional: for batch PDF, pass the QR code image URL
}

export default function QRLabel({ asset, templateConfig, qrCodeUrl }: QRLabelProps) {
  const { logoUrl, fields, fontSize, labelPosition, qrSize } = templateConfig || {}
  const labelText = fields
    ?.map((f: string) => asset[f])
    .filter(Boolean)
    .join(' | ')

  return (
    <div
      style={{
        border: '1px solid #eee',
        borderRadius: 8,
        padding: 16,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        width: qrSize + 40,
        background: '#fff',
      }}
    >
      {labelPosition === 'above' && <div style={{ fontSize, marginBottom: 8 }}>{labelText}</div>}
      {logoUrl && <img src={logoUrl} alt="Logo" style={{ height: 32, marginBottom: 8 }} />}
      <div
        style={{
          width: qrSize,
          height: qrSize,
          background: '#f3f3f3',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          marginBottom: labelPosition === 'below' ? 8 : 0,
        }}
      >
        {qrCodeUrl ? (
          <img src={qrCodeUrl} alt="QR" style={{ width: qrSize, height: qrSize }} />
        ) : (
          <span style={{ color: '#bbb' }}>QR</span>
        )}
      </div>
      {labelPosition === 'below' && <div style={{ fontSize, marginTop: 8 }}>{labelText}</div>}
    </div>
  )
}

'use client'

import type React from 'react'

import { useEffect, useRef, useState } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Camera, Upload, X, Scan, AlertTriangle, CheckCircle } from 'lucide-react'
import { lookupAssetByQR } from '@/lib/qr-actions'
import { QRCodeScanner } from '@/lib/qr-code-utils'
import { useAuth } from '@/components/auth/auth-provider'

interface QRScannerProps {
  onScanSuccess?: (assetData: any) => void
  onScanError?: (error: string) => void
}

export default function QRScanner({ onScanSuccess, onScanError }: QRScannerProps) {
  const videoRef = useRef<HTMLVideoElement>(null)
  const canvasRef = useRef<HTMLCanvasElement>(null)
  const fileInputRef = useRef<HTMLInputElement>(null)

  const [isScanning, setIsScanning] = useState(false)
  const [hasCamera, setHasCamera] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [stream, setStream] = useState<MediaStream | null>(null)
  const [scanResult, setScanResult] = useState<any>(null)
  const [loading, setLoading] = useState(false)
  const { user } = useAuth()
  const [userRole, setUserRole] = useState<string>('user')
  const [scanAgain, setScanAgain] = useState(false)

  useEffect(() => {
    checkCameraAvailability()
    const fetchUserRole = async () => {
      if (!user) return
      try {
        const res = await fetch(`/api/profiles/${user.id}`)
        const data = await res.json()
        setUserRole(data.profile?.role || 'user')
      } catch {
        setUserRole('user')
      }
    }
    fetchUserRole()
    return () => {
      stopCamera()
    }
  }, [user])

  const checkCameraAvailability = async () => {
    try {
      const devices = await navigator.mediaDevices.enumerateDevices()
      const videoDevices = devices.filter(device => device.kind === 'videoinput')
      setHasCamera(videoDevices.length > 0)
    } catch (err) {
      setHasCamera(false)
    }
  }

  const startCamera = async () => {
    try {
      setError(null)
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'environment', // Use back camera if available
          width: { ideal: 640 },
          height: { ideal: 480 },
        },
      })

      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream
        videoRef.current.play()
      }

      setStream(mediaStream)
      setIsScanning(true)

      // Start scanning loop
      scanQRCode()
    } catch (err) {
      setError('Failed to access camera. Please check permissions.')
      setIsScanning(false)
    }
  }

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop())
      setStream(null)
    }
    setIsScanning(false)
  }

  const scanQRCode = async () => {
    if (!isScanning || !videoRef.current || !canvasRef.current) return

    const video = videoRef.current
    const canvas = canvasRef.current
    const context = canvas.getContext('2d')

    if (!context || video.readyState !== video.HAVE_ENOUGH_DATA) {
      setTimeout(scanQRCode, 100)
      return
    }

    canvas.width = video.videoWidth
    canvas.height = video.videoHeight
    context.drawImage(video, 0, 0, canvas.width, canvas.height)

    try {
      // In a real implementation, you would use a QR code library like jsQR
      // For this demo, we'll simulate QR detection
      const imageData = context.getImageData(0, 0, canvas.width, canvas.height)

      // Simulate QR code detection (replace with actual QR library)
      // const code = jsQR(imageData.data, imageData.width, imageData.height)

      // For demo purposes, we'll check if user clicks to simulate scan
      setTimeout(scanQRCode, 100)
    } catch (err) {
      setTimeout(scanQRCode, 100)
    }
  }

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]
    if (!file) return

    setLoading(true)
    setError(null)

    try {
      // Use the enhanced scanner utility
      const scanResult = await QRCodeScanner.scanFromFile(file)

      if (scanResult.success && scanResult.data) {
        // Convert the scanned data to the expected format
        const qrDataString = JSON.stringify({
          type: 'asset',
          id: scanResult.data.assetId,
          name: scanResult.data.name,
          category: scanResult.data.category,
          url: scanResult.data.url,
          timestamp: new Date().toISOString(),
        })

        await processQRData(qrDataString)
      } else {
        setError(scanResult.error || 'Failed to scan QR code from image')
      }
    } catch (err) {
      setError('Failed to process uploaded image')
    } finally {
      setLoading(false)
    }
  }

  const processQRData = async (qrData: string) => {
    setLoading(true)
    try {
      const result = await lookupAssetByQR(qrData)

      if (result.error) {
        setError(result.error)
        onScanError?.(result.error)
      } else {
        setScanResult(result)
        // Log scan event to analytics_events
        if (user && result.asset) {
          const supabase = (await import('@/lib/supabase/client')).createClient()
          await supabase.from('analytics_events').insert({
            event_type: 'asset_scanned',
            asset_id: result.asset.id,
            user_id: user.id,
            metadata: {
              asset_name: result.asset.name,
              category: result.asset.category,
              url: result.asset.url || undefined,
            },
            created_at: new Date().toISOString(),
          })
        }
        onScanSuccess?.(result)
      }
    } catch (err) {
      const errorMsg = 'Failed to lookup asset'
      setError(errorMsg)
      onScanError?.(errorMsg)
    } finally {
      setLoading(false)
    }
  }

  const simulateSuccessfulScan = async () => {
    // Demo function to simulate a successful QR scan
    const sampleQRData = JSON.stringify({
      type: 'asset',
      id: 'AST-001',
      name: 'MacBook Pro 16"',
      category: 'it-equipment',
      url: 'http://localhost:3000/asset/AST-001',
    })

    await processQRData(sampleQRData)
  }

  const handleAssignToMe = async () => {
    if (!user || !scanResult?.asset) return
    setLoading(true)
    setError(null)
    try {
      const res = await fetch('/api/assets/assign', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ assetId: scanResult.asset.id, assigneeId: user.id }),
      })
      const result = await res.json()
      if (result.success) {
        setScanResult(null)
        setScanAgain(true)
        if (window.navigator.vibrate) window.navigator.vibrate(100)
        // Optionally show a toast
      } else {
        setError(result.error || 'Assignment failed')
      }
    } catch (err) {
      setError('Assignment failed')
    } finally {
      setLoading(false)
    }
  }

  const handleCheckIn = async () => {
    if (!scanResult?.asset) return
    setLoading(true)
    setError(null)
    try {
      const res = await fetch('/api/assets/checkin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ assetId: scanResult.asset.id }),
      })
      const result = await res.json()
      if (result.success) {
        setScanResult(null)
        setScanAgain(true)
        if (window.navigator.vibrate) window.navigator.vibrate(100)
        // Optionally show a toast
      } else {
        setError(result.error || 'Check-in failed')
      }
    } catch (err) {
      setError('Check-in failed')
    } finally {
      setLoading(false)
    }
  }

  const handleScanAgain = () => {
    setScanResult(null)
    setScanAgain(false)
    setError(null)
    // Optionally restart camera
    if (!isScanning && hasCamera) startCamera()
  }

  return (
    <div className="space-y-4">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Scan className="h-5 w-5 mr-2" />
            QR Code Scanner
          </CardTitle>
          <CardDescription>Scan QR codes to quickly access asset information</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {error && (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          {scanResult && (
            <Alert className="border-green-200 bg-green-50">
              <CheckCircle className="h-4 w-4 text-green-600" />
              <AlertDescription className="text-green-800">
                <strong>Asset Found:</strong> {scanResult.asset?.name} ({scanResult.asset?.asset_id}
                )
              </AlertDescription>
            </Alert>
          )}

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Camera Scanner */}
            <div className="space-y-3">
              <h4 className="font-medium">Camera Scanner</h4>
              {hasCamera ? (
                <div className="space-y-3">
                  {!isScanning ? (
                    <Button onClick={startCamera} className="w-full">
                      <Camera className="h-4 w-4 mr-2" />
                      Start Camera
                    </Button>
                  ) : (
                    <Button onClick={stopCamera} variant="outline" className="w-full">
                      <X className="h-4 w-4 mr-2" />
                      Stop Camera
                    </Button>
                  )}

                  <div className="relative">
                    <video
                      ref={videoRef}
                      className={`w-full rounded-lg border ${isScanning ? 'block' : 'hidden'}`}
                      style={{ maxHeight: '300px' }}
                    />
                    {isScanning && (
                      <div className="absolute inset-0 border-2 border-blue-500 rounded-lg pointer-events-none">
                        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-32 h-32 border-2 border-blue-500 rounded-lg"></div>
                      </div>
                    )}
                  </div>
                </div>
              ) : (
                <Alert>
                  <AlertTriangle className="h-4 w-4" />
                  <AlertDescription>Camera not available or permission denied</AlertDescription>
                </Alert>
              )}
            </div>

            {/* File Upload */}
            <div className="space-y-3">
              <h4 className="font-medium">Upload QR Image</h4>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                onChange={handleFileUpload}
                className="hidden"
              />
              <Button
                onClick={() => fileInputRef.current?.click()}
                variant="outline"
                className="w-full"
                disabled={loading}
              >
                <Upload className="h-4 w-4 mr-2" />
                {loading ? 'Processing...' : 'Upload QR Image'}
              </Button>

              <div className="text-center">
                <Button
                  onClick={simulateSuccessfulScan}
                  variant="outline"
                  size="sm"
                  className="text-xs"
                >
                  Demo: Simulate Scan
                </Button>
              </div>
            </div>
          </div>

          {/* Hidden canvas for image processing */}
          <canvas ref={canvasRef} className="hidden" />

          {/* Scan Result Display */}
          {scanResult && scanResult.asset && (
            <Card className="border-green-200 bg-green-50">
              <CardHeader>
                <CardTitle className="text-green-800">Asset Details</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <div className="grid grid-cols-2 gap-2 text-sm">
                  <div>
                    <strong>Asset ID:</strong>
                  </div>
                  <div>{scanResult.asset.asset_id}</div>

                  <div>
                    <strong>Name:</strong>
                  </div>
                  <div>{scanResult.asset.name}</div>

                  <div>
                    <strong>Category:</strong>
                  </div>
                  <div className="capitalize">{scanResult.asset.category}</div>

                  <div>
                    <strong>Status:</strong>
                  </div>
                  <div className="capitalize">{scanResult.asset.status}</div>

                  <div>
                    <strong>Location:</strong>
                  </div>
                  <div>{scanResult.asset.location || 'Not set'}</div>

                  <div>
                    <strong>Assignee:</strong>
                  </div>
                  <div>{scanResult.asset.assignee?.full_name || 'Unassigned'}</div>
                </div>

                <div className="flex flex-col gap-2 mt-4">
                  <Button className="w-full" asChild>
                    <a
                      href={`/asset/${scanResult.asset.asset_id}`}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      View Full Asset Details
                    </a>
                  </Button>
                  {/* Quick Actions */}
                  {['admin', 'manager'].includes(userRole) &&
                    scanResult.asset.status === 'available' && (
                      <Button className="w-full" onClick={handleAssignToMe} disabled={loading}>
                        {loading ? 'Assigning...' : 'Check Out / Assign to Me'}
                      </Button>
                    )}
                  {['admin', 'manager'].includes(userRole) &&
                    scanResult.asset.status === 'checked_out' && (
                      <Button
                        className="w-full"
                        onClick={handleCheckIn}
                        disabled={loading}
                        variant="destructive"
                      >
                        {loading ? 'Checking In...' : 'Check In'}
                      </Button>
                    )}
                  <Button className="w-full" variant="outline" onClick={handleScanAgain}>
                    Scan Again
                  </Button>
                </div>
              </CardContent>
            </Card>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

import { useEffect, useState } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Input } from '@/components/ui/input'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'
import { Alert, AlertDescription } from '@/components/ui/alert'

const FIELD_OPTIONS = [
  { value: 'asset_id', label: 'Asset ID' },
  { value: 'name', label: 'Name' },
  { value: 'category', label: 'Category' },
  { value: 'status', label: 'Status' },
]

export default function QRTemplateDesigner() {
  const [templates, setTemplates] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [showForm, setShowForm] = useState(false)
  const [form, setForm] = useState({
    name: '',
    logoUrl: '',
    fields: ['asset_id', 'name'],
    fontSize: 14,
    labelPosition: 'below',
    qrSize: 120,
    is_default: false,
  })
  const [logoFile, setLogoFile] = useState<File | null>(null)
  const [previewUrl, setPreviewUrl] = useState<string>('')
  const [editId, setEditId] = useState<string | null>(null)

  useEffect(() => {
    fetchTemplates()
  }, [])

  const fetchTemplates = async () => {
    setLoading(true)
    setError(null)
    try {
      const res = await fetch('/api/qr-templates')
      const data = await res.json()
      if (data.error) throw new Error(data.error)
      setTemplates(data.templates)
    } catch (e: any) {
      setError(e.message)
    } finally {
      setLoading(false)
    }
  }

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  const handleFieldChange = (field: string) => {
    setForm(prev => ({
      ...prev,
      fields: prev.fields.includes(field)
        ? prev.fields.filter(f => f !== field)
        : [...prev.fields, field],
    }))
  }

  const handleLogoUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0]
    if (file) {
      setLogoFile(file)
      const reader = new FileReader()
      reader.onload = ev => {
        setForm(prev => ({ ...prev, logoUrl: ev.target?.result as string }))
        setPreviewUrl(ev.target?.result as string)
      }
      reader.readAsDataURL(file)
    }
  }

  const handleEdit = (tpl: any) => {
    setEditId(tpl.id)
    setShowForm(true)
    setForm({
      name: tpl.name,
      logoUrl: tpl.config.logoUrl || '',
      fields: tpl.config.fields || ['asset_id', 'name'],
      fontSize: tpl.config.fontSize || 14,
      labelPosition: tpl.config.labelPosition || 'below',
      qrSize: tpl.config.qrSize || 120,
      is_default: tpl.is_default || false,
    })
    setPreviewUrl(tpl.config.logoUrl || '')
  }

  const handleSetDefault = async (id: string) => {
    setError(null)
    try {
      // Set is_default true for this template, false for others
      const res = await fetch(`/api/qr-templates/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...form, is_default: true }),
      })
      const data = await res.json()
      if (data.error) throw new Error(data.error)
      fetchTemplates()
    } catch (e: any) {
      setError(e.message)
    }
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setError(null)
    try {
      const config = {
        logoUrl: form.logoUrl,
        fields: form.fields,
        fontSize: form.fontSize,
        labelPosition: form.labelPosition,
        qrSize: form.qrSize,
      }
      let res, data
      if (editId) {
        res = await fetch(`/api/qr-templates/${editId}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ name: form.name, config, is_default: form.is_default }),
        })
        data = await res.json()
      } else {
        res = await fetch('/api/qr-templates', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ name: form.name, config, is_default: form.is_default }),
        })
        data = await res.json()
      }
      if (data.error) throw new Error(data.error)
      setShowForm(false)
      setEditId(null)
      setForm({
        name: '',
        logoUrl: '',
        fields: ['asset_id', 'name'],
        fontSize: 14,
        labelPosition: 'below',
        qrSize: 120,
        is_default: false,
      })
      setLogoFile(null)
      setPreviewUrl('')
      fetchTemplates()
    } catch (e: any) {
      setError(e.message)
    }
  }

  const handleDelete = async (id: string) => {
    if (!confirm('Delete this template?')) return
    setError(null)
    try {
      const res = await fetch(`/api/qr-templates/${id}`, { method: 'DELETE' })
      const data = await res.json()
      if (data.error) throw new Error(data.error)
      fetchTemplates()
    } catch (e: any) {
      setError(e.message)
    }
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>QR Template Designer</CardTitle>
        </CardHeader>
        <CardContent>
          {error && (
            <Alert variant="destructive">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
          {loading ? (
            <div>Loading templates...</div>
          ) : (
            <>
              <div className="mb-4 flex justify-between items-center">
                <div className="font-medium">Existing Templates</div>
                <Button onClick={() => setShowForm(v => !v)}>
                  {showForm ? 'Cancel' : 'New Template'}
                </Button>
              </div>
              <div className="space-y-2">
                {templates.length === 0 && <div className="text-gray-500">No templates found.</div>}
                {templates.map(tpl => (
                  <div key={tpl.id} className="flex items-center gap-4 border p-2 rounded">
                    <div className="flex-1">
                      <div className="font-semibold">
                        {tpl.name}{' '}
                        {tpl.is_default && <span className="text-xs text-blue-600">(Default)</span>}
                      </div>
                      <div className="text-xs text-gray-500">
                        Fields: {tpl.config.fields?.join(', ')}
                      </div>
                    </div>
                    <Button size="sm" variant="outline" onClick={() => handleEdit(tpl)}>
                      Edit
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleSetDefault(tpl.id)}
                      disabled={tpl.is_default}
                    >
                      Set Default
                    </Button>
                    <Button size="sm" variant="outline" onClick={() => handleDelete(tpl.id)}>
                      Delete
                    </Button>
                  </div>
                ))}
              </div>
              {showForm && (
                <form className="mt-6 space-y-4" onSubmit={handleSubmit}>
                  <div>
                    <label className="block text-sm font-medium mb-1">Template Name</label>
                    <Input name="name" value={form.name} onChange={handleInputChange} required />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-1">Logo</label>
                    <Input type="file" accept="image/*" onChange={handleLogoUpload} />
                    {previewUrl && (
                      <img src={previewUrl} alt="Logo preview" className="mt-2 h-12" />
                    )}
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-1">Fields to Display</label>
                    <div className="flex gap-2 flex-wrap">
                      {FIELD_OPTIONS.map(opt => (
                        <label key={opt.value} className="flex items-center gap-1">
                          <input
                            type="checkbox"
                            checked={form.fields.includes(opt.value)}
                            onChange={() => handleFieldChange(opt.value)}
                          />
                          {opt.label}
                        </label>
                      ))}
                    </div>
                  </div>
                  <div className="flex gap-4">
                    <div>
                      <label className="block text-sm font-medium mb-1">Font Size</label>
                      <Input
                        type="number"
                        name="fontSize"
                        value={form.fontSize}
                        onChange={handleInputChange}
                        min={8}
                        max={32}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium mb-1">Label Position</label>
                      <Select
                        value={form.labelPosition}
                        onValueChange={v => setForm(f => ({ ...f, labelPosition: v }))}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="below">Below QR</SelectItem>
                          <SelectItem value="above">Above QR</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium mb-1">QR Size (px)</label>
                      <Input
                        type="number"
                        name="qrSize"
                        value={form.qrSize}
                        onChange={handleInputChange}
                        min={80}
                        max={300}
                      />
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    <input
                      type="checkbox"
                      checked={form.is_default}
                      onChange={e => setForm(f => ({ ...f, is_default: e.target.checked }))}
                    />
                    <span>Set as default</span>
                  </div>
                  <Button type="submit">{editId ? 'Save Changes' : 'Save Template'}</Button>
                </form>
              )}
              {/* Live Preview */}
              {showForm && (
                <div className="mt-6">
                  <div className="font-medium mb-2">Live Preview</div>
                  <div
                    style={{
                      border: '1px solid #eee',
                      borderRadius: 8,
                      padding: 16,
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      width: form.qrSize + 40,
                    }}
                  >
                    {form.labelPosition === 'above' && (
                      <div style={{ fontSize: form.fontSize, marginBottom: 8 }}>
                        {form.fields.map(f => f.toUpperCase()).join(' | ')}
                      </div>
                    )}
                    {form.logoUrl && (
                      <img src={form.logoUrl} alt="Logo" style={{ height: 32, marginBottom: 8 }} />
                    )}
                    <div
                      style={{
                        width: form.qrSize,
                        height: form.qrSize,
                        background: '#f3f3f3',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginBottom: form.labelPosition === 'below' ? 8 : 0,
                      }}
                    >
                      <span style={{ color: '#bbb' }}>QR</span>
                    </div>
                    {form.labelPosition === 'below' && (
                      <div style={{ fontSize: form.fontSize, marginTop: 8 }}>
                        {form.fields.map(f => f.toUpperCase()).join(' | ')}
                      </div>
                    )}
                  </div>
                </div>
              )}
            </>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

