source:
'use client'

// =====================================================
// DEPARTMENT MANAGEMENT COMPONENT
// =====================================================
// Component for managing organizational departments

import { useState, useEffect } from 'react'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import {
  Plus,
  Search,
  MoreVertical,
  Users,
  Building,
  Settings,
  Trash2,
  Edit,
  Eye,
  UserPlus,
  AlertTriangle,
  TreePine,
} from 'lucide-react'
import { Department, DepartmentHierarchyNode } from '@/lib/types/rbac'

interface DepartmentManagementProps {
  tenantId: string
  canManageDepartments?: boolean
}

export function DepartmentManagement({
  tenantId,
  canManageDepartments = false,
}: DepartmentManagementProps) {
  const [departments, setDepartments] = useState<Department[]>([])
  const [departmentHierarchy, setDepartmentHierarchy] = useState<DepartmentHierarchyNode[]>([])
  const [selectedDepartment, setSelectedDepartment] = useState<Department | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [searchTerm, setSearchTerm] = useState('')
  const [viewMode, setViewMode] = useState<'list' | 'hierarchy'>('list')

  // Dialog states
  const [_showCreateDialog, _setShowCreateDialog] = useState(false)
  const [_showEditDialog, _setShowEditDialog] = useState(false)
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false)
  const [_showAssignUsersDialog, _setShowAssignUsersDialog] = useState(false)

  const loadDepartments = async () => {
    try {
      setLoading(true)
      const response = await fetch(`/api/departments?tenant_id=${tenantId}`)
      const data = await response.json()

      if (data.error) {
        throw new Error(data.error)
      }

      setDepartments(data.departments || [])
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to load departments')
    } finally {
      setLoading(false)
    }
  }

  const loadDepartmentHierarchy = async () => {
    try {
      const response = await fetch(`/api/departments/hierarchy?tenant_id=${tenantId}`)
      const data = await response.json()

      if (data.error) {
        throw new Error(data.error)
      }

      setDepartmentHierarchy(data.hierarchy || [])
    } catch (err: unknown) {
      console.error(
        'Error loading department hierarchy:',
        err instanceof Error ? err.message : 'Unknown error'
      )
    }
  }

  useEffect(() => {
    loadDepartments()
    loadDepartmentHierarchy()
  }, [tenantId, loadDepartments, loadDepartmentHierarchy])

  const _handleCreateDepartment = async (departmentData: Record<string, unknown>) => {
    try {
      const response = await fetch('/api/departments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...departmentData, tenant_id: tenantId }),
      })

      const data = await response.json()

      if (data.error) {
        throw new Error(data.error)
      }

      await loadDepartments()
      await loadDepartmentHierarchy()
      // setShowCreateDialog(false)
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to create department')
    }
  }

  const _handleUpdateDepartment = async (
    departmentId: string,
    updates: Record<string, unknown>
  ) => {
    try {
      const response = await fetch(`/api/departments/${departmentId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...updates, tenant_id: tenantId }),
      })

      const data = await response.json()

      if (data.error) {
        throw new Error(data.error)
      }

      await loadDepartments()
      await loadDepartmentHierarchy()
      // setShowEditDialog(false)
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to update department')
    }
  }

  const handleDeleteDepartment = async (departmentId: string) => {
    try {
      const response = await fetch(`/api/departments/${departmentId}?tenant_id=${tenantId}`, {
        method: 'DELETE',
      })

      const data = await response.json()

      if (data.error) {
        throw new Error(data.error)
      }

      await loadDepartments()
      await loadDepartmentHierarchy()
      setSelectedDepartment(null)
      setShowDeleteConfirm(false)
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to delete department')
    }
  }

  const filteredDepartments = departments.filter(
    dept =>
      dept.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      dept.display_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (dept.code && dept.code.toLowerCase().includes(searchTerm.toLowerCase()))
  )

  const renderDepartmentHierarchy = (nodes: DepartmentHierarchyNode[], level = 0) => {
    return nodes.map(node => (
      <div
        key={node.department.id}
        className={`${level > 0 ? 'ml-6 border-l-2 border-gray-200 pl-4' : ''}`}
      >
        <Card className="mb-2 hover:shadow-md transition-shadow">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3 flex-1">
                <Building
                  className={`h-5 w-5 ${
                    node.department.department_type === 'operational'
                      ? 'text-blue-600'
                      : node.department.department_type === 'administrative'
                        ? 'text-green-600'
                        : node.department.department_type === 'technical'
                          ? 'text-purple-600'
                          : node.department.department_type === 'financial'
                            ? 'text-yellow-600'
                            : 'text-red-600'
                  }`}
                />

                <div className="flex-1">
                  <div className="flex items-center space-x-2">
                    <h3 className="font-semibold text-gray-900">{node.department.display_name}</h3>
                    {node.department.code && (
                      <Badge variant="outline" className="text-xs">
                        {node.department.code}
                      </Badge>
                    )}
                  </div>

                  {node.department.description && (
                    <p className="text-sm text-gray-600 mt-1">{node.department.description}</p>
                  )}

                  <div className="flex items-center space-x-4 mt-2">
                    <div className="flex items-center space-x-1 text-sm text-gray-500">
                      <Users className="h-4 w-4" />
                      <span>{node.users?.length || 0} users</span>
                    </div>
                    <div className="flex items-center space-x-1 text-sm text-gray-500">
                      <Settings className="h-4 w-4" />
                      <span>{node.roles?.length || 0} roles</span>
                    </div>
                  </div>
                </div>

                <div className="flex flex-wrap gap-1">
                  <Badge variant="outline" className="text-xs capitalize">
                    {node.department.department_type}
                  </Badge>
                  {!node.department.is_active && (
                    <Badge variant="destructive" className="text-xs">
                      Inactive
                    </Badge>
                  )}
                  <Badge variant="outline" className="text-xs">
                    Level {node.department.level}
                  </Badge>
                </div>

                {canManageDepartments && (
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" size="sm">
                        <MoreVertical className="h-4 w-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      <DropdownMenuItem onClick={() => setSelectedDepartment(node.department)}>
                        <Eye className="h-4 w-4 mr-2" />
                        View Details
                      </DropdownMenuItem>
                      <DropdownMenuItem
                        onClick={() => {
                          setSelectedDepartment(node.department)
                          setShowEditDialog(true)
                        }}
                      >
                        <Edit className="h-4 w-4 mr-2" />
                        Edit Department
                      </DropdownMenuItem>
                      <DropdownMenuItem
                        onClick={() => {
                          setSelectedDepartment(node.department)
                          setShowAssignUsersDialog(true)
                        }}
                      >
                        <UserPlus className="h-4 w-4 mr-2" />
                        Manage Users
                      </DropdownMenuItem>
                      <DropdownMenuItem
                        onClick={() => {
                          setSelectedDepartment(node.department)
                          setShowDeleteConfirm(true)
                        }}
                        className="text-red-600"
                      >
                        <Trash2 className="h-4 w-4 mr-2" />
                        Delete Department
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        {node.children.length > 0 && (
          <div className="mt-2">{renderDepartmentHierarchy(node.children, level + 1)}</div>
        )}
      </div>
    ))
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Department Management</h1>
          <p className="text-gray-600">Manage organizational structure and user assignments</p>
        </div>
        <div className="flex items-center space-x-2">
          <Button
            variant={viewMode === 'list' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setViewMode('list')}
          >
            List View
          </Button>
          <Button
            variant={viewMode === 'hierarchy' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setViewMode('hierarchy')}
          >
            <TreePine className="h-4 w-4 mr-2" />
            Hierarchy
          </Button>
          {canManageDepartments && (
            <Button onClick={() => setShowCreateDialog(true)}>
              <Plus className="h-4 w-4 mr-2" />
              Create Department
            </Button>
          )}
        </div>
      </div>

      {error && (
        <Alert variant="destructive">
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Building className="h-5 w-5 text-blue-600" />
              <div>
                <p className="text-sm text-gray-600">Total Departments</p>
                <p className="text-2xl font-bold">{departments.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Users className="h-5 w-5 text-green-600" />
              <div>
                <p className="text-sm text-gray-600">Active Departments</p>
                <p className="text-2xl font-bold">{departments.filter(d => d.is_active).length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <TreePine className="h-5 w-5 text-purple-600" />
              <div>
                <p className="text-sm text-gray-600">Root Departments</p>
                <p className="text-2xl font-bold">{departmentHierarchy.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Settings className="h-5 w-5 text-orange-600" />
              <div>
                <p className="text-sm text-gray-600">Max Depth</p>
                <p className="text-2xl font-bold">
                  {departments.length > 0 ? Math.max(...departments.map(d => d.level)) : 0}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Search and Filters */}
      <div className="flex items-center space-x-4">
        <div className="relative flex-1 max-w-md">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
          <Input
            placeholder="Search departments..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            className="pl-10"
          />
        </div>
      </div>

      {/* Department Content */}
      {viewMode === 'hierarchy' ? (
        <div className="space-y-4">
          <h3 className="text-lg font-semibold">Department Hierarchy</h3>
          {departmentHierarchy.length > 0 ? (
            <div className="space-y-2">{renderDepartmentHierarchy(departmentHierarchy)}</div>
          ) : (
            <div className="text-center py-8">
              <Building className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600">No departments found.</p>
            </div>
          )}
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredDepartments.map(department => (
            <Card key={department.id} className="hover:shadow-md transition-shadow">
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <Building
                      className={`h-5 w-5 ${
                        department.department_type === 'operational'
                          ? 'text-blue-600'
                          : department.department_type === 'administrative'
                            ? 'text-green-600'
                            : department.department_type === 'technical'
                              ? 'text-purple-600'
                              : department.department_type === 'financial'
                                ? 'text-yellow-600'
                                : 'text-red-600'
                      }`}
                    />
                    <CardTitle className="text-lg">{department.display_name}</CardTitle>
                  </div>
                  {canManageDepartments && (
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" size="sm">
                          <MoreVertical className="h-4 w-4" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuItem onClick={() => setSelectedDepartment(department)}>
                          <Eye className="h-4 w-4 mr-2" />
                          View Details
                        </DropdownMenuItem>
                        <DropdownMenuItem
                          onClick={() => {
                            setSelectedDepartment(department)
                            setShowEditDialog(true)
                          }}
                        >
                          <Edit className="h-4 w-4 mr-2" />
                          Edit Department
                        </DropdownMenuItem>
                        <DropdownMenuItem
                          onClick={() => {
                            setSelectedDepartment(department)
                            setShowAssignUsersDialog(true)
                          }}
                        >
                          <UserPlus className="h-4 w-4 mr-2" />
                          Manage Users
                        </DropdownMenuItem>
                        <DropdownMenuItem
                          onClick={() => {
                            setSelectedDepartment(department)
                            setShowDeleteConfirm(true)
                          }}
                          className="text-red-600"
                        >
                          <Trash2 className="h-4 w-4 mr-2" />
                          Delete Department
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  )}
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  <p className="text-sm text-gray-600">
                    {department.description || 'No description'}
                  </p>

                  <div className="flex flex-wrap gap-1">
                    <Badge variant="outline" className="text-xs capitalize">
                      {department.department_type}
                    </Badge>
                    {department.code && (
                      <Badge variant="outline" className="text-xs">
                        {department.code}
                      </Badge>
                    )}
                    {!department.is_active && (
                      <Badge variant="destructive" className="text-xs">
                        Inactive
                      </Badge>
                    )}
                    <Badge variant="outline" className="text-xs">
                      Level {department.level}
                    </Badge>
                  </div>

                  <div className="flex items-center justify-between text-sm text-gray-500">
                    <span>Created: {new Date(department.created_at).toLocaleDateString()}</span>
                    {department.budget_limit && (
                      <span>Budget: ${department.budget_limit.toLocaleString()}</span>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {filteredDepartments.length === 0 && viewMode === 'list' && (
        <div className="text-center py-8">
          <Building className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <p className="text-gray-600">No departments found matching your search.</p>
        </div>
      )}

      {/* Delete Confirmation Dialog */}
      {selectedDepartment && (
        <Dialog open={showDeleteConfirm} onOpenChange={setShowDeleteConfirm}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Delete Department</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <p>
                Are you sure you want to delete the department &quot;
                {selectedDepartment.display_name}&quot;? This action cannot be undone.
              </p>
              <div className="flex justify-end space-x-2">
                <Button variant="outline" onClick={() => setShowDeleteConfirm(false)}>
                  Cancel
                </Button>
                <Button
                  variant="destructive"
                  onClick={() => handleDeleteDepartment(selectedDepartment.id)}
                >
                  Delete Department
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  )
}

'use client'

import React, { useState, useEffect } from 'react'
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { Switch } from '@/components/ui/switch'
import { Role } from '@/lib/types/rbac'

interface EditRoleDialogProps {
  open: boolean
  onOpenChange: (open: boolean) => void
  role: Role
  onUpdateRole: (roleId: string, updates: any) => Promise<void>
  existingRoles: Role[]
}

export function EditRoleDialog({
  open,
  onOpenChange,
  role,
  onUpdateRole,
  existingRoles,
}: EditRoleDialogProps) {
  const [formData, setFormData] = useState({
    name: '',
    display_name: '',
    description: '',
    is_active: true,
    max_users: '',
  })
  const [isLoading, setIsLoading] = useState(false)

  useEffect(() => {
    if (role) {
      setFormData({
        name: role.name || '',
        display_name: role.display_name || '',
        description: role.description || '',
        is_active: role.is_active ?? true,
        max_users: role.max_users?.toString() || '',
      })
    }
  }, [role])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setIsLoading(true)

    try {
      const updates = {
        ...formData,
        max_users: formData.max_users ? parseInt(formData.max_users) : null,
      }

      await onUpdateRole(role.id, updates)
      onOpenChange(false)
    } catch (error) {
      console.error('Error updating role:', error)
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Edit Role</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="name">Role Name</Label>
            <Input
              id="name"
              value={formData.name}
              onChange={e => setFormData(prev => ({ ...prev, name: e.target.value }))}
              placeholder="Enter role name"
              required
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="display_name">Display Name</Label>
            <Input
              id="display_name"
              value={formData.display_name}
              onChange={e => setFormData(prev => ({ ...prev, display_name: e.target.value }))}
              placeholder="Enter display name"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="description">Description</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={e => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Enter role description"
              rows={3}
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="max_users">Max Users (optional)</Label>
            <Input
              id="max_users"
              type="number"
              value={formData.max_users}
              onChange={e => setFormData(prev => ({ ...prev, max_users: e.target.value }))}
              placeholder="Leave empty for unlimited"
              min="1"
            />
          </div>

          <div className="flex items-center space-x-2">
            <Switch
              id="is_active"
              checked={formData.is_active}
              onCheckedChange={checked => setFormData(prev => ({ ...prev, is_active: checked }))}
            />
            <Label htmlFor="is_active">Active</Label>
          </div>

          <div className="flex justify-end space-x-2 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              disabled={isLoading}
            >
              Cancel
            </Button>
            <Button type="submit" disabled={isLoading}>
              {isLoading ? 'Updating...' : 'Update Role'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  )
}

'use client'

// =====================================================
// PERMISSION GUARD COMPONENT
// =====================================================
// Component for protecting UI elements based on user permissions

import { ReactNode, useEffect, useState, useCallback } from 'react'
import { createClient } from '@/lib/supabase/client'
import { PermissionService } from '@/lib/services/permission-service'
import { PermissionCheckRequest } from '@/lib/types/rbac'

interface PermissionGuardProps {
  children: ReactNode
  permissions: string | string[]
  requireAll?: boolean // true = AND, false = OR (default)
  resourceId?: string
  context?: Record<string, unknown>
  fallback?: ReactNode
  loading?: ReactNode
  onUnauthorized?: () => void
  tenantId?: string
  userId?: string
}

export function PermissionGuard({
  children,
  permissions,
  requireAll = false,
  resourceId,
  context,
  fallback = null,
  loading = null,
  onUnauthorized,
  tenantId,
  userId,
}: PermissionGuardProps): JSX.Element {
  const [hasPermission, setHasPermission] = useState<boolean | null>(null)
  const [isLoading, setIsLoading] = useState(true)
  const [userContext, setUserContext] = useState<{ tenantId: string; userId: string } | null>(null)

  const permissionService = new PermissionService()

  const loadUserContext = useCallback(async () => {
    if (tenantId && userId) {
      setUserContext({ tenantId, userId })
      return
    }

    try {
      const response = await fetch('/api/auth/me')
      const data = await response.json()

      if (data.user && data.tenant) {
        setUserContext({
          tenantId: data.tenant.id,
          userId: data.user.id,
        })
      }
    } catch (error) {
      console.error('Failed to load user context:', error)
    }
  }, [tenantId, userId])

  const checkPermissions = useCallback(async () => {
    if (!userContext && !(tenantId && userId)) {
      setHasPermission(false)
      setIsLoading(false)
      return
    }

    const currentTenantId = tenantId || userContext?.tenantId
    const currentUserId = userId || userContext?.userId

    if (!currentTenantId || !currentUserId) {
      setHasPermission(false)
      setIsLoading(false)
      return
    }

    try {
      setIsLoading(true)

      const permissionArray = Array.isArray(permissions) ? permissions : [permissions]
      const results = await Promise.all(
        permissionArray.map(permission =>
          permissionService.checkPermission(currentUserId, resourceId || 'default', permission)
        )
      )

      const hasAccess = requireAll
        ? results.every(result => result)
        : results.some(result => result)

      setHasPermission(hasAccess)

      if (!hasAccess && onUnauthorized) {
        onUnauthorized()
      }
    } catch (error) {
      console.error('Permission check failed:', error)
      setHasPermission(false)
    } finally {
      setIsLoading(false)
    }
  }, [
    userContext,
    permissions,
    resourceId,
    context,
    tenantId,
    userId,
    requireAll,
    onUnauthorized,
    permissionService,
  ])

  useEffect(() => {
    loadUserContext()
  }, [loadUserContext])

  useEffect(() => {
    if (userContext || (tenantId && userId)) {
      checkPermissions()
    }
  }, [userContext, tenantId, userId, checkPermissions])

  if (isLoading) {
    return loading || <div className="animate-pulse bg-gray-200 rounded h-4 w-16"></div>
  }

  if (hasPermission === false) {
    return <>{fallback}</>
  }

  if (hasPermission === true) {
    return <>{children}</>
  }

  return <></>
}

// =====================================================
// HOOK FOR PERMISSION CHECKING
// =====================================================

export function usePermissions(
  permissions: string | string[],
  options: {
    requireAll?: boolean
    resourceId?: string
    context?: Record<string, unknown>
    tenantId?: string
    userId?: string
  } = {}
) {
  const [hasPermission, setHasPermission] = useState<boolean | null>(null)
  const [isLoading, setIsLoading] = useState(true)
  const [userContext, setUserContext] = useState<{ tenantId: string; userId: string } | null>(null)

  const permissionService = new PermissionService()

  const loadUserContext = useCallback(async () => {
    if (options.tenantId && options.userId) {
      setUserContext({ tenantId: options.tenantId, userId: options.userId })
      return
    }

    try {
      const supabase = createClient()
      const {
        data: { user },
        error,
      } = await supabase.auth.getUser()

      if (error || !user) {
        setHasPermission(false)
        setIsLoading(false)
        return
      }

      const { data: profile } = await supabase
        .from('profiles')
        .select('tenant_id')
        .eq('id', user.id)
        .single()

      if (!profile) {
        setHasPermission(false)
        setIsLoading(false)
        return
      }

      setUserContext({
        tenantId: profile.tenant_id,
        userId: user.id,
      })
    } catch (error) {
      console.error('Error loading user context:', error)
      setHasPermission(false)
      setIsLoading(false)
    }
  }, [options.tenantId, options.userId])

  const checkPermissions = useCallback(async () => {
    if (!userContext && !(options.tenantId && options.userId)) {
      setHasPermission(false)
      setIsLoading(false)
      return
    }

    const currentTenantId = options.tenantId || userContext!.tenantId
    const currentUserId = options.userId || userContext!.userId

    try {
      setIsLoading(true)

      const permissionList = Array.isArray(permissions) ? permissions : [permissions]

      if (permissionList.length === 0) {
        setHasPermission(true)
        setIsLoading(false)
        return
      }

      const requests: PermissionCheckRequest[] = permissionList.map(permission => ({
        permission_name: permission,
        resource_id: options.resourceId,
        context: {
          ...options.context,
          hook: 'usePermissions',
          timestamp: new Date().toISOString(),
        },
      }))

      const results = await permissionService.checkMultiplePermissions(
        currentTenantId,
        currentUserId,
        requests
      )

      const hasRequiredPermissions = options.requireAll
        ? permissionList.every(permission => results[permission]?.granted)
        : permissionList.some(permission => results[permission]?.granted)

      setHasPermission(hasRequiredPermissions)
    } catch (error) {
      console.error('Error checking permissions:', error)
      setHasPermission(false)
    } finally {
      setIsLoading(false)
    }
  }, [userContext, permissions, options, permissionService])

  useEffect(() => {
    loadUserContext()
  }, [loadUserContext])

  useEffect(() => {
    if (userContext || (options.tenantId && options.userId)) {
      checkPermissions()
    }
  }, [userContext, options.tenantId, options.userId, checkPermissions])

  const refetch = () => {
    checkPermissions()
  }

  return {
    hasPermission,
    isLoading,
    refetch,
  }
}

// =====================================================
// HIGHER-ORDER COMPONENT FOR PERMISSION PROTECTION
// =====================================================

export function withPermissions<P extends object>(
  Component: React.ComponentType<P>,
  permissions: string | string[],
  options: {
    requireAll?: boolean
    fallback?: ReactNode
    loading?: ReactNode
  } = {}
) {
  return function PermissionProtectedComponent(props: P) {
    return (
      <PermissionGuard
        permissions={permissions}
        requireAll={options.requireAll}
        fallback={options.fallback}
        loading={options.loading}
      >
        <Component {...props} />
      </PermissionGuard>
    )
  }
}

// =====================================================
// CONDITIONAL RENDERING HELPERS
// =====================================================

interface ConditionalRenderProps {
  children: ReactNode
  condition: boolean
  fallback?: ReactNode
}

export function ConditionalRender({
  children,
  condition,
  fallback = null,
}: ConditionalRenderProps) {
  return condition ? <>{children}</> : <>{fallback}</>
}

// =====================================================
// PERMISSION-BASED BUTTON COMPONENT
// =====================================================

interface PermissionButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  permissions: string | string[]
  requireAll?: boolean
  resourceId?: string
  context?: Record<string, unknown>
  fallback?: ReactNode
  children: ReactNode
  className?: string
}

export function PermissionButton({
  permissions,
  requireAll = false,
  resourceId,
  context,
  fallback = null,
  children,
  className = '',
  ...buttonProps
}: PermissionButtonProps) {
  const { hasPermission, isLoading } = usePermissions(permissions, {
    requireAll,
    resourceId,
    context,
  })

  if (isLoading) {
    return (
      <button {...buttonProps} disabled className={`${className} opacity-50 cursor-not-allowed`}>
        <div className="animate-pulse">Loading...</div>
      </button>
    )
  }

  if (!hasPermission) {
    return <>{fallback}</>
  }

  return (
    <button {...buttonProps} className={className}>
      {children}
    </button>
  )
}

// =====================================================
// PERMISSION-BASED LINK COMPONENT
// =====================================================

interface PermissionLinkProps {
  permissions: string | string[]
  requireAll?: boolean
  resourceId?: string
  context?: Record<string, unknown>
  fallback?: ReactNode
  children: ReactNode
  href: string
  className?: string
}

export function PermissionLink({
  permissions,
  requireAll = false,
  resourceId,
  context,
  fallback = null,
  children,
  href,
  className = '',
}: PermissionLinkProps) {
  const { hasPermission, isLoading } = usePermissions(permissions, {
    requireAll,
    resourceId,
    context,
  })

  if (isLoading) {
    return (
      <span className={`${className} opacity-50`}>
        <div className="animate-pulse">Loading...</div>
      </span>
    )
  }

  if (!hasPermission) {
    return <>{fallback}</>
  }

  return (
    <a href={href} className={className}>
      {children}
    </a>
  )
}

// =====================================================
// PERMISSION CONSTANTS FOR EASY REFERENCE
// =====================================================

export const PERMISSIONS = {
  ASSETS: {
    CREATE: 'create:asset',
    READ: 'read:asset',
    UPDATE: 'update:asset',
    DELETE: 'delete:asset',
    ASSIGN: 'assign:asset',
