          </div>

          {filteredRoles.length === 0 && (
            <div className="text-center py-8">
              <Shield className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600">No roles found matching your search.</p>
            </div>
          )}
        </TabsContent>

        <TabsContent value="hierarchy">
          <Card>
            <CardHeader>
              <CardTitle>Role Hierarchy</CardTitle>
              <p className="text-sm text-gray-600">
                Visual representation of role inheritance and relationships
              </p>
            </CardHeader>
            <CardContent>
              <RoleHierarchyTree
                hierarchy={roleHierarchy}
                onRoleSelect={loadRoleDetails}
                canManageRoles={canManageRoles}
              />
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="permissions">
          <PermissionMatrix tenantId={tenantId} roles={roles} canManageRoles={canManageRoles} />
        </TabsContent>

        <TabsContent value="analytics">
          <RoleAnalytics tenantId={tenantId} roles={roles} />
        </TabsContent>
      </Tabs>

      {/* Dialogs */}
      <CreateRoleDialog
        open={showCreateDialog}
        onOpenChange={setShowCreateDialog}
        onCreateRole={handleCreateRole}
        tenantId={tenantId}
        existingRoles={roles}
      />

      {selectedRole && (
        <>
          <EditRoleDialog
            open={showEditDialog}
            onOpenChange={setShowEditDialog}
            role={selectedRole}
            onUpdateRole={handleUpdateRole}
            existingRoles={roles}
          />

          <AssignRoleDialog
            open={showAssignDialog}
            onOpenChange={setShowAssignDialog}
            role={selectedRole}
            tenantId={tenantId}
            onAssignComplete={() => {
              setShowAssignDialog(false)
              loadRoleDetails(selectedRole.id)
            }}
          />

          <Dialog open={showDeleteConfirm} onOpenChange={setShowDeleteConfirm}>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Delete Role</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <p>
                  Are you sure you want to delete the role &quot;{selectedRole.display_name}&quot;?
                  This action cannot be undone.
                </p>
                <div className="flex justify-end space-x-2">
                  <Button variant="outline" onClick={() => setShowDeleteConfirm(false)}>
                    Cancel
                  </Button>
                  <Button variant="destructive" onClick={() => handleDeleteRole(selectedRole.id)}>
                    Delete Role
                  </Button>
                </div>
              </div>
            </DialogContent>
          </Dialog>
        </>
      )}
    </div>
  )
}

import { useEffect, useState } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'

const PERMISSION_OPTIONS = [
  { value: 'manage:assets', label: 'Manage Assets' },
  { value: 'manage:geofences', label: 'Manage Geofences' },
  { value: 'manage:reports', label: 'Manage Reports' },
  { value: 'manage:users', label: 'Manage Users' },
  { value: 'view:reports', label: 'View Reports' },
  { value: 'view:audit', label: 'View Audit Log' },
  { value: 'manage:settings', label: 'Manage Settings' },
]

export default function RoleManagement() {
  const [roles, setRoles] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [editRole, setEditRole] = useState<any>(null)
  const [editPerms, setEditPerms] = useState<string[]>([])
  const [saving, setSaving] = useState(false)

  useEffect(() => {
    fetchRoles()
  }, [])

  const fetchRoles = async () => {
    setLoading(true)
    setError(null)
    try {
      const res = await fetch('/api/roles')
      const data = await res.json()
      if (data.error) throw new Error(data.error)
      setRoles(data.roles)
    } catch (e: any) {
      setError(e.message)
    } finally {
      setLoading(false)
    }
  }

  const handleEdit = (role: any) => {
    setEditRole(role)
    setEditPerms(role.permissions || [])
  }

  const handlePermChange = (perm: string) => {
    setEditPerms(prev => (prev.includes(perm) ? prev.filter(p => p !== perm) : [...prev, perm]))
  }

  const handleSave = async () => {
    if (!editRole) return
    setSaving(true)
    setError(null)
    try {
      const res = await fetch(`/api/roles/${editRole.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ permissions: editPerms }),
      })
      const data = await res.json()
      if (data.error) throw new Error(data.error)
      setEditRole(null)
      setEditPerms([])
      fetchRoles()
    } catch (e: any) {
      setError(e.message)
    } finally {
      setSaving(false)
    }
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Role Management</CardTitle>
        </CardHeader>
        <CardContent>
          {error && (
            <Alert variant="destructive">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
          {loading ? (
            <div>Loading roles...</div>
          ) : (
            <>
              <div className="mb-4 font-medium">Roles</div>
              <div className="space-y-2">
                {roles.length === 0 && <div className="text-gray-500">No roles found.</div>}
                {roles.map(role => (
                  <div key={role.id} className="flex items-center gap-4 border p-2 rounded">
                    <div className="flex-1">
                      <div className="font-semibold">{role.name}</div>
                      <div className="text-xs text-gray-500">
                        Permissions: {role.permissions?.join(', ')}
                      </div>
                    </div>
                    <Button size="sm" variant="outline" onClick={() => handleEdit(role)}>
                      Edit
                    </Button>
                  </div>
                ))}
              </div>
              {editRole && (
                <div className="mt-6 border rounded p-4 bg-gray-50">
                  <div className="font-medium mb-2">Edit Permissions for {editRole.name}</div>
                  <div className="flex gap-2 flex-wrap mb-4">
                    {PERMISSION_OPTIONS.map(opt => (
                      <label key={opt.value} className="flex items-center gap-1">
                        <input
                          type="checkbox"
                          checked={editPerms.includes(opt.value)}
                          onChange={() => handlePermChange(opt.value)}
                        />
                        {opt.label}
                      </label>
                    ))}
                  </div>
                  <div className="flex gap-2">
                    <Button onClick={handleSave} disabled={saving}>
                      {saving ? 'Saving...' : 'Save'}
                    </Button>
                    <Button
                      variant="outline"
                      onClick={() => {
                        setEditRole(null)
                        setEditPerms([])
                      }}
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              )}
            </>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { createClient } from '@/lib/supabase/client'
import { SUPABASE_CONFIG } from '@/lib/supabase/config'
import { Button } from '@/components/ui/button'
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from '@/components/ui/card'
import { CheckCircle2, XCircle, RefreshCw } from 'lucide-react'

export function SupabaseConnectionTest() {
  const [status, setStatus] = useState<'loading' | 'success' | 'error'>('loading')
  const [message, setMessage] = useState<string>('')
  const [projectRef, setProjectRef] = useState<string>(SUPABASE_CONFIG.projectId)

  const testConnection = async () => {
    setStatus('loading')
    setMessage('Testing connection to Supabase...')

    try {
      const supabase = createClient()

      // Simple query to test connection
      const { data, error } = await supabase.from('profiles').select('id').limit(1)

      if (error) {
        console.error('Supabase connection error:', error)
        setStatus('error')
        setMessage(`Connection failed: ${error.message}`)
        return
      }

      setStatus('success')
      setMessage('Successfully connected to Supabase!')
    } catch (err) {
      console.error('Unexpected error:', err)
      setStatus('error')
      setMessage(`Unexpected error: ${err instanceof Error ? err.message : String(err)}`)
    }
  }

  useEffect(() => {
    testConnection()
  }, [])

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          Supabase Connection Status
          {status === 'loading' && <RefreshCw className="h-4 w-4 animate-spin" />}
          {status === 'success' && <CheckCircle2 className="h-5 w-5 text-green-500" />}
          {status === 'error' && <XCircle className="h-5 w-5 text-red-500" />}
        </CardTitle>
        <CardDescription>Testing connection to project: {projectRef}</CardDescription>
      </CardHeader>
      <CardContent>
        <div
          className={`p-4 rounded-md ${
            status === 'loading'
              ? 'bg-blue-50 text-blue-700'
              : status === 'success'
                ? 'bg-green-50 text-green-700'
                : 'bg-red-50 text-red-700'
          }`}
        >
          {message}
        </div>
      </CardContent>
      <CardFooter>
        <Button
          onClick={testConnection}
          disabled={status === 'loading'}
          variant={status === 'error' ? 'destructive' : 'default'}
        >
          {status === 'loading' ? (
            <>
              <RefreshCw className="mr-2 h-4 w-4 animate-spin" />
              Testing...
            </>
          ) : (
            <>
              <RefreshCw className="mr-2 h-4 w-4" />
              Test Connection Again
            </>
          )}
        </Button>
      </CardFooter>
    </Card>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Button } from '@/components/ui/button'
import { Alert, AlertDescription } from '@/components/ui/alert'

interface DebugInfo {
  supabaseUrl: string
  hasAnonKey: boolean
  clientCreated: boolean
  connectionTest: {
    success: boolean
    error?: string
  }
  environmentVariables: {
    [key: string]: string | undefined
  }
}

export function SupabaseDebug() {
  const [debugInfo, setDebugInfo] = useState<DebugInfo | null>(null)
  const [loading, setLoading] = useState(true)

  const runDiagnostics = async () => {
    setLoading(true)
    try {
      const { createClient, getConfig, checkSupabaseConnection } = await import(
        '@/lib/supabase/client'
      )
      const { ENV, validateEnvironment } = await import('@/lib/env')

      const config = getConfig()
      const validation = validateEnvironment()

      let connectionTest = { success: false, error: 'Not tested' }
      let clientCreated = false

      try {
        const client = createClient()
        clientCreated = true
        connectionTest = await checkSupabaseConnection()
      } catch (error) {
        connectionTest = {
          success: false,
          error: error instanceof Error ? error.message : 'Unknown error',
        }
      }

      setDebugInfo({
        supabaseUrl: config?.url || 'Not configured',
        hasAnonKey: !!config?.anonKey,
        clientCreated,
        connectionTest,
        environmentVariables: {
          NEXT_PUBLIC_SUPABASE_URL: ENV.SUPABASE_URL,
          NEXT_PUBLIC_APP_URL: ENV.APP_URL,
          NODE_ENV: ENV.NODE_ENV,
          hasAnonKey: ENV.SUPABASE_ANON_KEY ? ' Present' : ' Missing',
          validationStatus: validation.valid
            ? ' Valid'
            : ` Invalid: ${validation.errors.join(', ')}`,
        },
      })
    } catch (error) {
      console.error('Debug diagnostics failed:', error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    runDiagnostics()
  }, [])

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Running Diagnostics...</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="animate-pulse space-y-2">
            <div className="h-4 bg-gray-200 rounded w-3/4"></div>
            <div className="h-4 bg-gray-200 rounded w-1/2"></div>
            <div className="h-4 bg-gray-200 rounded w-2/3"></div>
          </div>
        </CardContent>
      </Card>
    )
  }

  if (!debugInfo) {
    return (
      <Alert>
        <AlertDescription>
          Failed to run diagnostics. Please check the console for errors.
        </AlertDescription>
      </Alert>
    )
  }

  return (
    <div className="space-y-4">
      <Card>
        <CardHeader>
          <CardTitle>Supabase Configuration Debug</CardTitle>
          <CardDescription>
            Diagnostic information for troubleshooting Supabase connection issues
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <h4 className="font-medium mb-2">Connection Status</h4>
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <span>Client Created:</span>
                  <Badge variant={debugInfo.clientCreated ? 'default' : 'destructive'}>
                    {debugInfo.clientCreated ? ' Success' : ' Failed'}
                  </Badge>
                </div>
                <div className="flex items-center justify-between">
                  <span>Connection Test:</span>
                  <Badge variant={debugInfo.connectionTest.success ? 'default' : 'destructive'}>
                    {debugInfo.connectionTest.success ? ' Connected' : ' Failed'}
                  </Badge>
                </div>
                {debugInfo.connectionTest.error && (
                  <p className="text-sm text-red-600 mt-1">
                    Error: {debugInfo.connectionTest.error}
                  </p>
                )}
              </div>
            </div>

            <div>
              <h4 className="font-medium mb-2">Configuration</h4>
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <span>Supabase URL:</span>
                  <Badge
                    variant={debugInfo.supabaseUrl !== 'Not configured' ? 'default' : 'destructive'}
                  >
                    {debugInfo.supabaseUrl !== 'Not configured' ? ' Set' : ' Missing'}
                  </Badge>
                </div>
                <div className="flex items-center justify-between">
                  <span>Anon Key:</span>
                  <Badge variant={debugInfo.hasAnonKey ? 'default' : 'destructive'}>
                    {debugInfo.hasAnonKey ? ' Present' : ' Missing'}
                  </Badge>
                </div>
              </div>
            </div>
          </div>

          <div>
            <h4 className="font-medium mb-2">Environment Variables</h4>
            <div className="bg-gray-50 p-3 rounded-lg space-y-1 text-sm font-mono">
              {Object.entries(debugInfo.environmentVariables).map(([key, value]) => (
                <div key={key} className="flex justify-between">
                  <span className="text-gray-600">{key}:</span>
                  <span className="text-gray-900">{value || 'undefined'}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="pt-4 border-t">
            <Button onClick={runDiagnostics} className="w-full">
              Re-run Diagnostics
            </Button>
          </div>
        </CardContent>
      </Card>

      {debugInfo.supabaseUrl !== 'Not configured' && (
        <Card>
          <CardHeader>
            <CardTitle>Configuration Details</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2 text-sm">
              <div>
                <strong>Supabase URL:</strong>
                <code className="ml-2 bg-gray-100 px-2 py-1 rounded text-xs">
                  {debugInfo.supabaseUrl}
                </code>
              </div>
              <div>
                <strong>Project ID:</strong>
                <code className="ml-2 bg-gray-100 px-2 py-1 rounded text-xs">
                  {debugInfo.supabaseUrl.includes('supabase.co')
                    ? debugInfo.supabaseUrl.split('//')[1]?.split('.')[0]
                    : 'Unknown'}
                </code>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  )
}

