'use client'

import { useEffect, useState } from 'react'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { AlertTriangle, CheckCircle, RefreshCw, Database } from 'lucide-react'

export default function SupabaseStatus() {
  const [status, setStatus] = useState<'checking' | 'connected' | 'error' | 'offline'>('checking')
  const [error, setError] = useState<string | null>(null)
  const [retryCount, setRetryCount] = useState(0)

  const checkSupabaseConnection = async () => {
    try {
      setStatus('checking')
      setError(null)

      // Check if we have the required environment variables
      const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL
      const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY

      if (!supabaseUrl || !supabaseAnonKey) {
        setError('Supabase environment variables are missing')
        setStatus('error')
        return
      }

      // Try to make a simple request to Supabase
      const response = await fetch(`${supabaseUrl}/rest/v1/`, {
        method: 'HEAD',
        headers: {
          apikey: supabaseAnonKey,
          Authorization: `Bearer ${supabaseAnonKey}`,
        },
      })

      if (response.ok) {
        setStatus('connected')
      } else {
        throw new Error(`Supabase responded with status: ${response.status}`)
      }
    } catch (err) {
      console.error('Supabase connection error:', err)
      setError(err instanceof Error ? err.message : 'Failed to connect to Supabase')
      setStatus('error')
    }
  }

  const handleRetry = () => {
    setRetryCount(prev => prev + 1)
    checkSupabaseConnection()
  }

  useEffect(() => {
    checkSupabaseConnection()
  }, [])

  if (status === 'checking') {
    return (
      <Alert>
        <Database className="h-4 w-4 animate-pulse" />
        <AlertDescription>Checking database connection...</AlertDescription>
      </Alert>
    )
  }

  if (status === 'connected') {
    return (
      <Alert className="border-green-200 bg-green-50">
        <CheckCircle className="h-4 w-4 text-green-600" />
        <AlertDescription className="text-green-800">
           Database connection is working properly
        </AlertDescription>
      </Alert>
    )
  }

  return (
    <Card className="border-red-200 bg-red-50">
      <CardHeader>
        <CardTitle className="text-red-800 flex items-center">
          <AlertTriangle className="h-5 w-5 mr-2" />
          Database Connection Issue
        </CardTitle>
        <CardDescription className="text-red-700">
          Unable to connect to the database. This may be a temporary network issue.
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <Alert variant="destructive">
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>
            <strong>Error:</strong> {error}
          </AlertDescription>
        </Alert>

        <div className="space-y-3">
          <h4 className="font-medium text-red-800">Possible Solutions:</h4>
          <ul className="text-sm text-red-700 space-y-1 list-disc list-inside">
            <li>Check your internet connection</li>
            <li>Try refreshing the page</li>
            <li>Wait a moment and try again</li>
            <li>The v0 preview environment may have network restrictions</li>
          </ul>
        </div>

        <div className="flex space-x-2">
          <Button onClick={handleRetry} variant="outline" size="sm">
            <RefreshCw className="h-4 w-4 mr-2" />
            Retry Connection ({retryCount})
          </Button>
          <Button onClick={() => window.location.reload()} size="sm">
            Refresh Page
          </Button>
        </div>

        <div className="bg-blue-50 p-3 rounded border border-blue-200">
          <p className="text-sm text-blue-800">
            <strong>Note:</strong> If you're in the v0 preview environment, some network requests
            may be restricted. Consider downloading the code and running it locally for full
            functionality.
          </p>
        </div>
      </CardContent>
    </Card>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { createClient } from '@/lib/supabase/client'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table'
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'
import { User, Mail, Loader2, Trash2, Plus } from 'lucide-react'
import Link from 'next/link'

const ROLES = ['admin', 'manager', 'user']

export default function TeamManagement() {
  const [members, setMembers] = useState<any[]>([])
  const [invitations, setInvitations] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [inviteDialog, setInviteDialog] = useState(false)
  const [inviteEmail, setInviteEmail] = useState('')
  const [inviteRole, setInviteRole] = useState('user')
  const [inviteLoading, setInviteLoading] = useState(false)
  const [teamId, setTeamId] = useState<string | null>(null)

  // Fetch teamId for current user
  useEffect(() => {
    async function fetchTeamId() {
      setLoading(true)
      setError(null)
      try {
        const supabase = createClient()
        const { data: profile } = await supabase.from('profiles').select('team_id').single()
        if (profile?.team_id) setTeamId(profile.team_id)
      } catch (e: any) {
        setError('Failed to load team info')
      } finally {
        setLoading(false)
      }
    }
    fetchTeamId()
  }, [])

  // Fetch members and invitations
  useEffect(() => {
    if (!teamId) return
    setLoading(true)
    setError(null)
    async function fetchData() {
      try {
        const supabase = createClient()
        const [membersRes, invitesRes] = await Promise.all([
          supabase.rpc('get_team_members', { team_id: teamId }),
          fetch(`/api/teams/${teamId}/invitations`).then(r => r.json()),
        ])
        setMembers(membersRes.data || [])
        setInvitations(invitesRes.invitations || [])
      } catch (e: any) {
        setError('Failed to load team data')
      } finally {
        setLoading(false)
      }
    }
    fetchData()
  }, [teamId])

  async function handleInvite() {
    setInviteLoading(true)
    setError(null)
    try {
      const res = await fetch('/api/teams/invite', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: inviteEmail, role: inviteRole, team_id: teamId }),
      })
      if (!res.ok) throw new Error((await res.json()).error || 'Failed to invite')
      setInviteDialog(false)
      setInviteEmail('')
      setInviteRole('user')
      // Refresh
      const invitesRes = await fetch(`/api/teams/${teamId}/invitations`).then(r => r.json())
      setInvitations(invitesRes.invitations || [])
    } catch (e: any) {
      setError(e.message)
    } finally {
      setInviteLoading(false)
    }
  }

  async function handleRemoveMember(userId: string) {
    if (!window.confirm('Remove this member from the team?')) return
    setLoading(true)
    setError(null)
    try {
      const res = await fetch(`/api/teams/${teamId}/members/${userId}`, { method: 'DELETE' })
      if (!res.ok) throw new Error((await res.json()).error || 'Failed to remove member')
      setMembers(members.filter(m => m.user_id !== userId))
    } catch (e: any) {
      setError(e.message)
    } finally {
      setLoading(false)
    }
  }

  async function handleChangeRole(userId: string, newRole: string) {
    setLoading(true)
    setError(null)
    try {
      const res = await fetch(`/api/teams/${teamId}/members/${userId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ role: newRole }),
      })
      if (!res.ok) throw new Error((await res.json()).error || 'Failed to change role')
      setMembers(members.map(m => (m.user_id === userId ? { ...m, role: newRole } : m)))
    } catch (e: any) {
      setError(e.message)
    } finally {
      setLoading(false)
    }
  }

  async function handleCancelInvite(inviteId: string) {
    if (!window.confirm('Cancel this invitation?')) return
    setLoading(true)
    setError(null)
    try {
      const res = await fetch(`/api/teams/invitations/${inviteId}`, { method: 'DELETE' })
      if (!res.ok) throw new Error((await res.json()).error || 'Failed to cancel invitation')
      setInvitations(invitations.filter(i => i.id !== inviteId))
    } catch (e: any) {
      setError(e.message)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div>
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Team Analytics</CardTitle>
          <CardDescription>View analytics and reports for your team.</CardDescription>
        </CardHeader>
        <CardContent>
          <Button asChild variant="outline">
            <Link href="/settings/team/analytics">View Team Analytics</Link>
          </Button>
        </CardContent>
      </Card>
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Team Members</CardTitle>
          <CardDescription>Manage your team members and their roles.</CardDescription>
        </CardHeader>
        <CardContent>
          <Button onClick={() => setInviteDialog(true)} className="mb-4" size="sm">
            <Plus className="w-4 h-4 mr-2" /> Invite Member
          </Button>
          {loading ? (
            <div className="flex items-center gap-2">
              <Loader2 className="animate-spin" /> Loading...
            </div>
          ) : error ? (
            <div className="text-red-500">{error}</div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead>Role</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {members.map(member => (
                  <TableRow key={member.user_id}>
                    <TableCell>
                      {member.profiles?.full_name || <span className="text-gray-400">-</span>}
                    </TableCell>
                    <TableCell>
                      {member.profiles?.email || <span className="text-gray-400">-</span>}
                    </TableCell>
                    <TableCell>
                      <Select
                        value={member.role}
                        onValueChange={val => handleChangeRole(member.user_id, val)}
                      >
                        <SelectTrigger className="w-28">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          {ROLES.map(role => (
                            <SelectItem key={role} value={role}>
                              {role}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="destructive"
                        size="icon"
                        onClick={() => handleRemoveMember(member.user_id)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Pending Invitations</CardTitle>
          <CardDescription>Invitations that have not yet been accepted.</CardDescription>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex items-center gap-2">
              <Loader2 className="animate-spin" /> Loading...
            </div>
          ) : error ? (
            <div className="text-red-500">{error}</div>
          ) : invitations.length === 0 ? (
            <div className="text-gray-500">No pending invitations.</div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Email</TableHead>
                  <TableHead>Role</TableHead>
                  <TableHead>Invited By</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {invitations.map(invite => (
                  <TableRow key={invite.id}>
                    <TableCell>{invite.email}</TableCell>
                    <TableCell>{invite.role}</TableCell>
                    <TableCell>
                      {invite.invited_by || <span className="text-gray-400">-</span>}
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="destructive"
                        size="icon"
                        onClick={() => handleCancelInvite(invite.id)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
      <Dialog open={inviteDialog} onOpenChange={setInviteDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Invite New Member</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <Input
              placeholder="Email address"
              value={inviteEmail}
              onChange={e => setInviteEmail(e.target.value)}
              type="email"
              required
            />
            <Select value={inviteRole} onValueChange={setInviteRole}>
              <SelectTrigger className="w-32">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {ROLES.map(role => (
                  <SelectItem key={role} value={role}>
                    {role}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <DialogFooter>
            <Button onClick={handleInvite} disabled={inviteLoading}>
              {inviteLoading ? (
                <Loader2 className="animate-spin w-4 h-4 mr-2" />
              ) : (
                <Mail className="w-4 h-4 mr-2" />
              )}{' '}
              Send Invite
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

'use client'

import * as React from 'react'
import * as RechartsPrimitive from 'recharts'

import { cn } from '@/lib/utils'

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: '', dark: '.dark' } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error('useChart must be used within a <ChartContainer />')
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<'div'> & {
    config: ChartConfig
    children: React.ComponentProps<typeof RechartsPrimitive.ResponsiveContainer>['children']
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, '')}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>{children}</RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = 'Chart'

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(([_, config]) => config.theme || config.color)

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color = itemConfig.theme?.[theme as keyof typeof itemConfig.theme] || itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join('\n')}
}
`
          )
          .join('\n'),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<'div'> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: 'line' | 'dot' | 'dashed'
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = 'dot',
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item.dataKey || item.name || 'value'}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === 'string'
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn('font-medium', labelClassName)}>{labelFormatter(value, payload)}</div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn('font-medium', labelClassName)}>{value}</div>
    }, [label, labelFormatter, payload, hideLabel, labelClassName, config, labelKey])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== 'dot'

    return (
      <div
        ref={ref}
        className={cn(
          'grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl',
