// Inspired by react-hot-toast library
import * as React from 'react'

import type { ToastActionElement, ToastProps } from '@/components/ui/toast'

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: 'ADD_TOAST',
  UPDATE_TOAST: 'UPDATE_TOAST',
  DISMISS_TOAST: 'DISMISS_TOAST',
  REMOVE_TOAST: 'REMOVE_TOAST',
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType['ADD_TOAST']
      toast: ToasterToast
    }
  | {
      type: ActionType['UPDATE_TOAST']
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType['DISMISS_TOAST']
      toastId?: ToasterToast['id']
    }
  | {
      type: ActionType['REMOVE_TOAST']
      toastId?: ToasterToast['id']
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: 'REMOVE_TOAST',
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case 'ADD_TOAST':
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case 'UPDATE_TOAST':
      return {
        ...state,
        toasts: state.toasts.map(t => (t.id === action.toast.id ? { ...t, ...action.toast } : t)),
      }

    case 'DISMISS_TOAST': {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach(toast => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map(t =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case 'REMOVE_TOAST':
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter(t => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach(listener => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, 'id'>

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: 'UPDATE_TOAST',
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: 'DISMISS_TOAST', toastId: id })

  dispatch({
    type: 'ADD_TOAST',
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: open => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: 'DISMISS_TOAST', toastId }),
  }
}

export { useToast, toast }

import { useState, useEffect } from 'react'

export interface AnalyticsData {
  overview: {
    totalAssets: number
    activeAssets: number
    assetsCreatedToday: number
    assetsCreatedThisWeek: number
    assetsCreatedThisMonth: number
    totalValue: number
    qrCoverage: number
    lastUpdated: string
  }
  categories: Array<{
    category: string
    count: number
    percentage: number
  }>
  status: Array<{
    status: string
    count: number
    percentage: number
  }>
  locations: Array<{
    location: string
    count: number
    percentage: number
  }>
  timeSeries: Array<{
    date: string
    assets: number
    scans: number
    users: number
  }>
  scanData: Array<{
    hour: number
    scans: number
    timestamp: string
  }>
  recentActivity: Array<{
    id: string
    name: string
    created_at: string
    updated_at: string
    status: string
  }>
  userActivity: {
    totalUsers: number
    activeUsers: number
    newUsersThisMonth: number
  }
}

export function useAnalytics() {
  const [data, setData] = useState<AnalyticsData | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  const fetchAnalytics = async () => {
    try {
      setLoading(true)
      setError(null)

      const response = await fetch('/api/analytics')
      if (!response.ok) {
        throw new Error('Failed to fetch analytics data')
      }

      const result = await response.json()
      setData(result.analytics)
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred')
      console.error('Analytics fetch error:', err)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchAnalytics()
  }, [])

  const refresh = () => {
    fetchAnalytics()
  }

  return {
    data,
    loading,
    error,
    refresh,
  }
}

export function useAnalyticsWithInterval(intervalMs: number = 30000) {
  const analytics = useAnalytics()

  useEffect(() => {
    const interval = setInterval(() => {
      analytics.refresh()
    }, intervalMs)

    return () => clearInterval(interval)
  }, [intervalMs, analytics.refresh])

  return analytics
}

'use client'

import { useState, useEffect, useCallback } from 'react'
import { realtimeAnalytics, type RealtimeEvent, type AnalyticsEvent } from '@/lib/realtime-client'

export interface RealtimeMetrics {
  totalAssets: number
  activeAssets: number
  assetsCreatedToday: number
  totalUsers: number
  scansThisWeek: number
  recentScans: any[]
  lastUpdated: string
}

export interface ActivityFeed {
  id: string
  type: 'asset_created' | 'asset_updated' | 'asset_scanned' | 'user_login' | 'qr_generated'
  title: string
  description: string
  timestamp: string
  metadata?: Record<string, any>
}

export function useRealtimeMetrics() {
  const [metrics, setMetrics] = useState<RealtimeMetrics>({
    totalAssets: 0,
    activeAssets: 0,
    assetsCreatedToday: 0,
    totalUsers: 0,
    scansThisWeek: 0,
    recentScans: [],
    lastUpdated: new Date().toISOString(),
  })
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  const updateMetrics = useCallback(async () => {
    try {
      const newMetrics = await realtimeAnalytics.getRealtimeMetrics()
      setMetrics({
        ...newMetrics,
        lastUpdated: new Date().toISOString(),
      })
      setError(null)
    } catch (err) {
      setError('Failed to update metrics')
      console.error('Metrics update error:', err)
    }
  }, [])

  useEffect(() => {
    // Initial load
    updateMetrics().finally(() => setLoading(false))

    // Subscribe to real-time updates
    const handleAssetChange = (event: RealtimeEvent) => {
      if (event.table === 'assets') {
        updateMetrics()
      }
    }

    const handleAnalyticsEvent = (event: AnalyticsEvent) => {
      updateMetrics()
    }

    const assetChannelId = realtimeAnalytics.subscribeToTable('assets', handleAssetChange)
    realtimeAnalytics.subscribeToAnalytics(handleAnalyticsEvent)

    // Cleanup on unmount
    return () => {
      realtimeAnalytics.unsubscribe(assetChannelId, handleAssetChange)
    }
  }, [updateMetrics])

  return { metrics, loading, error, refresh: updateMetrics }
}

export function useActivityFeed(limit = 20) {
  const [activities, setActivities] = useState<ActivityFeed[]>([])
  const [loading, setLoading] = useState(true)

  const addActivity = useCallback(
    (event: AnalyticsEvent) => {
      const activity: ActivityFeed = {
        id: event.id,
        type: event.event_type,
        title: getActivityTitle(event.event_type),
        description: getActivityDescription(event),
        timestamp: event.timestamp,
        metadata: event.metadata,
      }

      setActivities(prev => [activity, ...prev.slice(0, limit - 1)])
    },
    [limit]
  )

  useEffect(() => {
    // Subscribe to analytics events
    realtimeAnalytics.subscribeToAnalytics(addActivity)

    // Load initial activities (mock data for demo)
    const mockActivities: ActivityFeed[] = [
      {
        id: '1',
        type: 'asset_created',
        title: 'New Asset Created',
        description: 'MacBook Pro 16" was added to inventory',
        timestamp: new Date(Date.now() - 5 * 60 * 1000).toISOString(),
      },
      {
        id: '2',
        type: 'asset_scanned',
        title: 'Asset Scanned',
        description: 'Office Chair QR code was scanned',
        timestamp: new Date(Date.now() - 15 * 60 * 1000).toISOString(),
      },
      {
        id: '3',
        type: 'qr_generated',
        title: 'QR Code Generated',
        description: 'QR code created for Projector',
        timestamp: new Date(Date.now() - 30 * 60 * 1000).toISOString(),
      },
    ]

    setActivities(mockActivities)
    setLoading(false)

    return () => {
      // Cleanup handled by the analytics client
    }
  }, [addActivity])

  return { activities, loading }
}

export function useRealtimeAssetStatus(assetId?: string) {
  const [assetStatus, setAssetStatus] = useState<any>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    if (!assetId) {
      setLoading(false)
      return
    }

    const handleAssetUpdate = (event: RealtimeEvent) => {
      if (event.type === 'UPDATE' && event.record) {
        setAssetStatus(event.record)
      }
    }

    const channelId = realtimeAnalytics.subscribeToTable('assets', handleAssetUpdate, {
      column: 'asset_id',
      value: assetId,
    })

    // Load initial status
    realtimeAnalytics.supabase
      .from('assets')
      .select('*')
      .eq('asset_id', assetId)
      .single()
      .then(({ data }) => {
        setAssetStatus(data)
        setLoading(false)
      })

    return () => {
      realtimeAnalytics.unsubscribe(channelId, handleAssetUpdate)
    }
  }, [assetId])

  return { assetStatus, loading }
}

// Helper functions
function getActivityTitle(eventType: string): string {
  switch (eventType) {
    case 'asset_created':
      return 'New Asset Created'
    case 'asset_updated':
      return 'Asset Updated'
    case 'asset_scanned':
      return 'Asset Scanned'
    case 'user_login':
      return 'User Login'
    case 'qr_generated':
      return 'QR Code Generated'
    default:
      return 'Activity'
  }
}

function getActivityDescription(event: AnalyticsEvent): string {
  switch (event.event_type) {
    case 'asset_created':
      return `Asset ${event.asset_id} was added to inventory`
    case 'asset_updated':
      return `Asset ${event.asset_id} was modified`
    case 'asset_scanned':
      return `Asset ${event.asset_id} QR code was scanned`
    case 'user_login':
      return 'User logged into the system'
    case 'qr_generated':
      return `QR code generated for asset ${event.asset_id}`
    default:
      return 'System activity occurred'
  }
}

