export async function getAssetStats() {
  const supabase = await createClient()

  try {
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser()

    if (userError || !user) {
      return { error: 'Unauthorized' }
    }

    const { data: assets } = await supabase
      .from('assets')
      .select('status, category, purchase_value, created_at')
      .eq('created_by', user.id)

    if (!assets) {
      return { data: { total: 0, byStatus: {}, byCategory: {}, totalValue: 0 } }
    }

    const stats = {
      total: assets.length,
      byStatus: {} as Record<string, number>,
      byCategory: {} as Record<string, number>,
      totalValue: 0,
      recentAdditions: 0,
    }

    const thirtyDaysAgo = new Date()
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30)

    assets.forEach(
      (asset: {
        status: string
        category: string
        purchase_value?: number
        created_at?: string
      }) => {
        // Status counts
        stats.byStatus[asset.status] = (stats.byStatus[asset.status] || 0) + 1

        // Category counts
        stats.byCategory[asset.category] = (stats.byCategory[asset.category] || 0) + 1

        // Total value
        if (asset.purchase_value) {
          stats.totalValue += asset.purchase_value
        }

        // Recent additions
        if (asset.created_at && new Date(asset.created_at) > thirtyDaysAgo) {
          stats.recentAdditions++
        }
      }
    )

    return { data: stats }
  } catch (error) {
    console.error('Error in getAssetStats:', error)
    return { error: 'Failed to fetch asset statistics' }
  }
}

export async function generateAssetId(category?: string) {
  const prefix = category ? category.toUpperCase().slice(0, 3) : 'AST'
  const timestamp = Date.now().toString().slice(-6)
  const randomNum = Math.floor(Math.random() * 100)
    .toString()
    .padStart(2, '0')
  return `${prefix}-${timestamp}-${randomNum}`
}

export async function checkAssetIdExists(assetId: string) {
  const supabase = await createClient()

  try {
    const { data, error } = await supabase
      .from('assets')
      .select('id')
      .eq('asset_id', assetId)
      .single()

    if (error && error.code !== 'PGRST116') {
      console.error('Error checking asset ID:', error)
      return { error: error.message }
    }

    return { exists: !!data }
  } catch (error) {
    console.error('Error in checkAssetIdExists:', error)
    return { error: 'Failed to check asset ID' }
  }
}

import { createClient } from '@/lib/supabase/server'

export type AuditLogEvent = {
  user_id: string
  action: string
  entity: string
  entity_id?: string
  details?: Record<string, any>
  tenant_id?: string
  ip_address?: string
  before?: Record<string, any>
  after?: Record<string, any>
  user_agent?: string
}

/**
 * Logs an audit event to the audit_logs table.
 * @param event AuditLogEvent
 */
export async function logAuditEvent(event: AuditLogEvent) {
  const supabase = await createClient()
  try {
    const { error } = await supabase.from('audit_logs').insert({
      user_id: event.user_id,
      action: event.action,
      entity: event.entity,
      entity_id: event.entity_id || null,
      details: event.details || {},
      tenant_id: event.tenant_id || null,
      ip_address: event.ip_address || null,
      before: event.before || null,
      after: event.after || null,
      user_agent: event.user_agent || null,
      created_at: new Date().toISOString(),
    })
    if (error) {
      console.error('Failed to log audit event:', error)
    }
  } catch (err) {
    console.error('Audit log error:', err)
  }
}

'use server'

import { createClient } from '@/lib/supabase/server'
import { redirect } from 'next/navigation'
import { getAuthRedirectUrl } from '@/lib/supabase/config'

const getURL = () => {
  let url = process.env.NEXT_PUBLIC_APP_URL ?? 'https://cloudeleavepro.vercel.app'
  // Make sure to include `https://` when not localhost.
  url = url.includes('http') ? url : `https://${url}`
  // Make sure to include a trailing `/`.
  url = url.charAt(url.length - 1) === '/' ? url : `${url}/`
  return url
}

export async function signInWithEmail(email: string, password: string) {
  const supabase = await createClient()

  const { error } = await supabase.auth.signInWithPassword({
    email,
    password,
  })

  if (error) {
    console.error('Sign in error:', error)
    return { error: error.message }
  }

  return { success: true }
}

export async function signUpWithEmail(
  email: string,
  password: string,
  fullName: string,
  orgName?: string
) {
  const supabase = await createClient()

  const redirectUrl = getAuthRedirectUrl()
  console.log('Signup redirect URL:', redirectUrl)

  const { data, error } = await supabase.auth.signUp({
    email,
    password,
    options: {
      emailRedirectTo: redirectUrl,
      data: {
        full_name: fullName,
        org_name: orgName,
      },
    },
  })

  if (error) {
    console.error('Sign up error:', error)
    return { error: error.message }
  }

  // Check if user needs email confirmation
  if (data.user && !data.user.email_confirmed_at) {
    console.log('User created, email confirmation required:', data.user.id)
    return {
      success: true,
      needsConfirmation: true,
      message: 'Please check your email for a confirmation link to complete your registration.',
    }
  }

  // User is already confirmed (auto-confirm is enabled)
  if (data.user && data.user.email_confirmed_at) {
    console.log('User created and auto-confirmed:', data.user.id)
    return {
      success: true,
      needsConfirmation: false,
      message: 'Account created successfully! You can now sign in.',
    }
  }

  return { success: true }
}

export async function signInWithGitHub() {
  const supabase = await createClient()

  const redirectUrl = getAuthRedirectUrl()
  console.log('GitHub OAuth redirect URL:', redirectUrl)

  const { data, error } = await supabase.auth.signInWithOAuth({
    provider: 'github',
    options: {
      redirectTo: redirectUrl,
      queryParams: {
        access_type: 'offline',
        prompt: 'consent',
      },
    },
  })

  if (error) {
    console.error('GitHub OAuth error:', error)
    return { error: error.message }
  }

  if (data.url) {
    console.log('Redirecting to GitHub OAuth:', data.url)
    redirect(data.url)
  }

  return { success: true }
}

export async function signOut() {
  const supabase = await createClient()
  const { error } = await supabase.auth.signOut()

  if (error) {
    console.error('Sign out error:', error)
  }

