import Stripe from 'stripe'
import { createClient } from '@/lib/supabase/client'
import { Tenant } from '@/lib/rbac/types'

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY || '')

const PLAN_PRICES = {
  free: 'price_free', // Free plan - no Stripe price ID needed
  basic: process.env.STRIPE_BASIC_PRICE_ID || 'price_basic',
  pro: process.env.STRIPE_PRO_PRICE_ID || 'price_pro',
  enterprise: process.env.STRIPE_ENTERPRISE_PRICE_ID || 'price_enterprise',
}

export async function createCustomer(email: string, tenantId: string): Promise<string> {
  try {
    const customer = await stripe.customers.create({
      email,
      metadata: {
        tenantId,
      },
    })

    const supabase = createClient()
    await supabase.from('billing').insert({
      tenant_id: tenantId,
      stripe_customer_id: customer.id,
      plan: 'free',
      amount: 0,
      status: 'active',
    })

    return customer.id
  } catch (error) {
    console.error('Error creating customer:', error)
    throw error
  }
}

export async function createSubscription(
  customerId: string,
  priceId: string,
  tenantId: string
): Promise<string> {
  try {
    const subscription = await stripe.subscriptions.create({
      customer: customerId,
      items: [{ price: priceId }],
      metadata: {
        tenantId,
      },
    })

    const supabase = createClient()
    // If subscription is a Response<Subscription>, use .data; otherwise, use as Subscription
    const sub = (subscription as any).data ? (subscription as any).data : subscription
    await supabase
      .from('billing')
      .update({
        stripe_subscription_id: sub.id,
        plan: sub.items.data[0].price.nickname || 'basic',
        amount: sub.items.data[0].price.unit_amount || 0,
        status: sub.status,
        current_period_start: new Date(sub.current_period_start * 1000).toISOString(),
        current_period_end: new Date(sub.current_period_end * 1000).toISOString(),
        updated_at: new Date().toISOString(),
      })
      .eq('tenant_id', tenantId)

    return subscription.id
  } catch (error) {
    console.error('Error creating subscription:', error)
    throw error
  }
}

export async function updateSubscription(
  subscriptionId: string,
  newPriceId: string
): Promise<void> {
  try {
    const subscription = await stripe.subscriptions.retrieve(subscriptionId)
    await stripe.subscriptions.update(subscriptionId, {
      items: [
        {
          id: subscription.items.data[0].id,
          price: newPriceId,
        },
      ],
    })
  } catch (error) {
    console.error('Error updating subscription:', error)
    throw error
  }
}

export async function cancelSubscription(subscriptionId: string): Promise<void> {
  try {
    await stripe.subscriptions.cancel(subscriptionId)
  } catch (error) {
    console.error('Error canceling subscription:', error)
    throw error
  }
}

export async function createCheckoutSession(
  customerId: string,
  priceId: string,
  tenantId: string
): Promise<string> {
  try {
    const session = await stripe.checkout.sessions.create({
      customer: customerId,
      line_items: [
        {
          price: priceId,
          quantity: 1,
        },
      ],
      mode: 'subscription',
      success_url: `${process.env.NEXT_PUBLIC_APP_URL}/billing/success?session_id={CHECKOUT_SESSION_ID}`,
      cancel_url: `${process.env.NEXT_PUBLIC_APP_URL}/billing/cancel`,
      metadata: {
        tenantId,
      },
    })

    return session.url || ''
  } catch (error) {
    console.error('Error creating checkout session:', error)
    throw error
  }
}

export async function handleSubscriptionChange(
  subscriptionId: string,
  status: string
): Promise<void> {
  const supabase = createClient()

  try {
    const subscription = await stripe.subscriptions.retrieve(subscriptionId)
    const { tenantId } = subscription.metadata

    await supabase
      .from('billing')
      .update({
        status,
        updated_at: new Date().toISOString(),
      })
      .eq('tenant_id', tenantId)

    // Update tenant features based on plan
    const plan = subscription.items.data[0].price.nickname || 'basic'
    await updateTenantFeatures(tenantId, plan as Tenant['plan'])
  } catch (error) {
    console.error('Error handling subscription change:', error)
    throw error
  }
}

export async function updateTenantFeatures(tenantId: string, plan: Tenant['plan']): Promise<void> {
  const planFeatures = {
    free: {
      maxUsers: 5,
      maxAssets: 100,
      features: {
        qrCodes: true,
        analytics: false,
        api: false,
        customBranding: false,
        multipleLocations: false,
        advancedReports: false,
      },
    },
    basic: {
      maxUsers: 10,
      maxAssets: 500,
      features: {
        qrCodes: true,
        analytics: true,
        api: false,
        customBranding: false,
        multipleLocations: false,
        advancedReports: false,
      },
    },
    pro: {
      maxUsers: 50,
      maxAssets: 5000,
      features: {
        qrCodes: true,
        analytics: true,
        api: true,
        customBranding: true,
        multipleLocations: true,
        advancedReports: false,
      },
    },
    enterprise: {
      maxUsers: 1000,
      maxAssets: 100000,
      features: {
        qrCodes: true,
        analytics: true,
        api: true,
        customBranding: true,
        multipleLocations: true,
        advancedReports: true,
      },
    },
  }

  const supabase = createClient()
  await supabase
    .from('tenants')
    .update({
      plan,
      max_users: planFeatures[plan].maxUsers,
      max_assets: planFeatures[plan].maxAssets,
      features: planFeatures[plan].features,
      updated_at: new Date().toISOString(),
    })
    .eq('id', tenantId)
}

import { createClient } from '@/lib/supabase/server'

export async function sendIntegrationNotification({
  tenant_id,
  message,
  event,
}: {
  tenant_id: string
  message: string
  event?: string
}) {
  const supabase = await createClient()
  // Fetch active integrations for this tenant
  const { data: integrations, error } = await supabase
    .from('integrations')
    .select('*')
    .eq('tenant_id', tenant_id)
    .eq('status', 'active')
  if (error || !integrations) return
  for (const integration of integrations) {
    if (!integration.webhook_url) continue
    // Optionally filter by event type if needed
    try {
      await fetch(integration.webhook_url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: message }), // Slack/Teams expect { text }
      })
      // Optionally: log delivery success
    } catch (err) {
      // Optionally: log delivery failure, retry, etc.
    }
  }
}

// =====================================================
// API KEY AUTHENTICATION MIDDLEWARE
// =====================================================
// Middleware for validating API keys and enforcing permissions

import { NextRequest, NextResponse } from 'next/server'
import { apiKeyService } from '@/lib/services/api-key-service'

export interface ApiKeyAuthOptions {
  requiredPermission?: string
  requiredScope?: string
  skipRateLimit?: boolean
}

export interface ApiKeyContext {
  apiKey: any
  tenantId: string
  userId: string
}

/**
 * Middleware to authenticate and validate API keys
 */
export async function withApiKeyAuth(
  request: NextRequest,
  options: ApiKeyAuthOptions = {}
): Promise<{ success: boolean; context?: ApiKeyContext; response?: NextResponse }> {
  try {
    // Extract API key from Authorization header
    const authHeader = request.headers.get('authorization')
    const apiKey = extractApiKeyFromHeader(authHeader)

    if (!apiKey) {
      return {
        success: false,
        response: NextResponse.json({ error: 'API key required' }, { status: 401 }),
      }
    }

    // Get client IP address
    const ipAddress = getClientIpAddress(request)

    // Validate API key
    const validation = await apiKeyService.validateApiKey(
      apiKey,
      options.requiredPermission,
      options.requiredScope,
      ipAddress
    )

    if (!validation.valid) {
      const status = validation.rateLimitExceeded ? 429 : 401
      return {
        success: false,
        response: NextResponse.json({ error: validation.error }, { status }),
      }
    }

    // Log API usage (unless skipped)
    if (!options.skipRateLimit) {
      const url = new URL(request.url)
      await apiKeyService.logApiKeyUsage(
        validation.apiKey!,
        url.pathname,
        request.method,
        200, // Will be updated with actual status code later
        {
          ipAddress,
          userAgent: request.headers.get('user-agent') || undefined,
        }
      )
    }

    return {
      success: true,
      context: {
        apiKey: validation.apiKey!,
        tenantId: validation.apiKey!.tenant_id,
        userId: validation.apiKey!.user_id,
      },
    }
  } catch (error) {
    console.error('API key authentication error:', error)
    return {
      success: false,
      response: NextResponse.json({ error: 'Authentication failed' }, { status: 500 }),
    }
  }
}

/**
 * Higher-order function to wrap API routes with API key authentication
 */
export function requireApiKey(
  handler: (request: NextRequest, context: ApiKeyContext) => Promise<NextResponse>,
  options: ApiKeyAuthOptions = {}
) {
  return async (request: NextRequest): Promise<NextResponse> => {
    const auth = await withApiKeyAuth(request, options)

    if (!auth.success) {
      return auth.response!
    }

    try {
      const response = await handler(request, auth.context!)

      // Update usage log with actual response status
      if (!options.skipRateLimit) {
        const url = new URL(request.url)
        await apiKeyService.logApiKeyUsage(
          auth.context!.apiKey,
          url.pathname,
          request.method,
          response.status,
          {
            ipAddress: getClientIpAddress(request),
            userAgent: request.headers.get('user-agent') || undefined,
          }
        )
      }

      return response
    } catch (error) {
      console.error('API route error:', error)

      // Log error in usage
      if (!options.skipRateLimit) {
        const url = new URL(request.url)
        await apiKeyService.logApiKeyUsage(
          auth.context!.apiKey,
          url.pathname,
          request.method,
          500,
          {
            ipAddress: getClientIpAddress(request),
            userAgent: request.headers.get('user-agent') || undefined,
          }
        )
      }

      return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
    }
  }
}

/**
 * Extract API key from Authorization header
 */
function extractApiKeyFromHeader(authHeader: string | null): string | null {
  if (!authHeader) return null

  // Support both "Bearer" and "ApiKey" prefixes
  const bearerMatch = authHeader.match(/^Bearer\s+(.+)$/i)
  if (bearerMatch) return bearerMatch[1]

  const apiKeyMatch = authHeader.match(/^ApiKey\s+(.+)$/i)
  if (apiKeyMatch) return apiKeyMatch[1]

  // Support direct API key without prefix
  if (authHeader.startsWith('ak_')) return authHeader

  return null
}

/**
 * Get client IP address from request
 */
function getClientIpAddress(request: NextRequest): string {
  // Check various headers for the real IP address
  const forwardedFor = request.headers.get('x-forwarded-for')
  if (forwardedFor) {
    return forwardedFor.split(',')[0].trim()
  }

  const realIp = request.headers.get('x-real-ip')
  if (realIp) {
    return realIp
  }

  const cfConnectingIp = request.headers.get('cf-connecting-ip')
  if (cfConnectingIp) {
    return cfConnectingIp
  }

  // Fallback to localhost since NextRequest doesn't have ip property
  return '127.0.0.1'
}

/**
 * Middleware to add rate limiting headers to response
 */
export function addRateLimitHeaders(
  response: NextResponse,
  rateLimitStatus: {
    allowed: boolean
    remaining: number
    resetTime: Date
    limit: number
  }
): NextResponse {
  response.headers.set('X-RateLimit-Limit', rateLimitStatus.limit.toString())
  response.headers.set('X-RateLimit-Remaining', rateLimitStatus.remaining.toString())
  response.headers.set(
    'X-RateLimit-Reset',
    Math.ceil(rateLimitStatus.resetTime.getTime() / 1000).toString()
  )

  if (!rateLimitStatus.allowed) {
    response.headers.set(
      'Retry-After',
      Math.ceil((rateLimitStatus.resetTime.getTime() - Date.now()) / 1000).toString()
    )
  }

  return response
}

/**
 * Create a standardized API error response
 */
export function createApiErrorResponse(
  error: string,
  status: number = 400,
  details?: Record<string, any>
): NextResponse {
  const body: any = { error }

  if (details) {
    body.details = details
  }

  return NextResponse.json(body, { status })
}

/**
 * Create a standardized API success response
 */
export function createApiSuccessResponse(data: any, status: number = 200): NextResponse {
  return NextResponse.json(
    {
      success: true,
      data,
    },
    { status }
  )
}

