  async getActiveGuestAccess(tenantId: string): Promise<GuestAccess[]> {
    try {
      const supabase = await this.getSupabase()

      const { data: guestAccess, error } = await supabase
        .from('guest_access')
        .select(
          `
          *,
          inviter:profiles!guest_access_invited_by_fkey (
            id,
            full_name,
            email
          )
        `
        )
        .eq('tenant_id', tenantId)
        .eq('is_active', true)
        .gte('expires_at', new Date().toISOString())
        .order('expires_at', { ascending: true })

      if (error) {
        throw new Error(`Failed to get active guest access: ${error.message}`)
      }

      return guestAccess || []
    } catch (error) {
      console.error('Error getting active guest access:', error)
      throw error
    }
  }

  // =====================================================
  // CLEANUP AND MAINTENANCE
  // =====================================================

  async cleanupExpiredDelegations(tenantId?: string): Promise<number> {
    try {
      const supabase = await this.getSupabase()

      let query = supabase
        .from('permission_delegations')
        .update({ status: 'expired' })
        .eq('status', 'active')
        .lt('expires_at', new Date().toISOString())

      if (tenantId) {
        query = query.eq('tenant_id', tenantId)
      }

      const { error, count } = await query

      if (error) {
        throw new Error(`Failed to cleanup expired delegations: ${error.message}`)
      }

      return count || 0
    } catch (error) {
      console.error('Error cleaning up expired delegations:', error)
      throw error
    }
  }

  async cleanupExpiredGuestAccess(tenantId?: string): Promise<number> {
    try {
      const supabase = await this.getSupabase()

      let query = supabase
        .from('guest_access')
        .update({ is_active: false })
        .eq('is_active', true)
        .lt('expires_at', new Date().toISOString())

      if (tenantId) {
        query = query.eq('tenant_id', tenantId)
      }

      const { error, count } = await query

      if (error) {
        throw new Error(`Failed to cleanup expired guest access: ${error.message}`)
      }

      return count || 0
    } catch (error) {
      console.error('Error cleaning up expired guest access:', error)
      throw error
    }
  }

  // =====================================================
  // VALIDATION HELPERS
  // =====================================================

  private async validateDelegatorPermissions(
    _tenantId: string,
    _delegatorId: string,
    _permissionNames: string[]
  ): Promise<boolean> {
    try {
      // This would integrate with the permission service to check if the delegator has all the permissions
      // For now, we'll assume they do (this should be implemented with actual permission checking)
      return true
    } catch (error) {
      console.error('Error validating delegator permissions:', error)
      return false
    }
  }

  private async validateDelegatorRole(
    tenantId: string,
    delegatorId: string,
    roleId: string
  ): Promise<boolean> {
    try {
      const supabase = await this.getSupabase()

      const { data: userRole } = await supabase
        .from('user_roles')
        .select('id')
        .eq('tenant_id', tenantId)
        .eq('user_id', delegatorId)
        .eq('role_id', roleId)
        .eq('is_active', true)
        .single()

      return !!userRole
    } catch (error) {
      console.error('Error validating delegator role:', error)
      return false
    }
  }

  // =====================================================
  // ANALYTICS AND REPORTING
  // =====================================================

  async getDelegationStats(
    tenantId: string,
    days = 30
  ): Promise<{
    total_delegations: number
    active_delegations: number
    expired_delegations: number
    revoked_delegations: number
    guest_access_count: number
    most_delegated_permissions: { permission_name: string; count: number }[]
  }> {
    try {
      const supabase = await this.getSupabase()
      const since = new Date(Date.now() - days * 24 * 60 * 60 * 1000).toISOString()

      // Get delegation counts
      const { data: delegations } = await supabase
        .from('permission_delegations')
        .select('status, permission_ids')
        .eq('tenant_id', tenantId)
        .gte('created_at', since)

      const totalDelegations = delegations?.length || 0
      const activeDelegations = delegations?.filter((d: any) => d.status === 'active').length || 0
      const expiredDelegations = delegations?.filter((d: any) => d.status === 'expired').length || 0
      const revokedDelegations = delegations?.filter((d: any) => d.status === 'revoked').length || 0

      // Get guest access count
      const { data: guestAccess } = await supabase
        .from('guest_access')
        .select('id')
        .eq('tenant_id', tenantId)
        .eq('is_active', true)

      const guestAccessCount = guestAccess?.length || 0

      // Get most delegated permissions (placeholder)
      const mostDelegatedPermissions = [
        { permission_name: 'read:asset', count: 15 },
        { permission_name: 'update:asset', count: 8 },
        { permission_name: 'create:report', count: 5 },
      ]

      return {
        total_delegations: totalDelegations,
        active_delegations: activeDelegations,
        expired_delegations: expiredDelegations,
        revoked_delegations: revokedDelegations,
        guest_access_count: guestAccessCount,
        most_delegated_permissions: mostDelegatedPermissions,
      }
    } catch (error) {
      console.error('Error getting delegation stats:', error)
      throw error
    }
  }
}

// =====================================================
// DEPARTMENT SERVICE
// =====================================================
// Service for managing organizational departments and hierarchy

import { createClient } from '@/lib/supabase/server'
import {
  Department,
  DepartmentInsert,
  DepartmentUpdate,
  DepartmentWithHierarchy,
  UserDepartment,
  DepartmentRole,
  DepartmentRoleInsert,
  DepartmentHierarchyNode,
  DepartmentAnalytics,
} from '@/lib/types/rbac'

export class DepartmentService {
  private async getSupabase() {
    return createClient()
  }

  // =====================================================
  // DEPARTMENT MANAGEMENT
  // =====================================================

  async createDepartment(
    tenantId: string,
    departmentData: Omit<DepartmentInsert, 'tenant_id' | 'created_by'>,
    createdBy: string
  ): Promise<Department> {
    try {
      const supabase = await this.getSupabase()

      // Validate department name uniqueness
      const { data: existingDept } = await supabase
        .from('departments')
        .select('id')
        .eq('tenant_id', tenantId)
        .eq('name', departmentData.name)
        .single()

      if (existingDept) {
        throw new Error(`Department with name '${departmentData.name}' already exists`)
      }

      // Validate department code uniqueness if provided
      if (departmentData.code) {
        const { data: existingCode } = await supabase
          .from('departments')
          .select('id')
          .eq('tenant_id', tenantId)
          .eq('code', departmentData.code)
          .single()

        if (existingCode) {
          throw new Error(`Department with code '${departmentData.code}' already exists`)
        }
      }

      // Validate parent department exists if specified
      if (departmentData.parent_department_id) {
        const { data: parentDept } = await supabase
          .from('departments')
          .select('id, level')
          .eq('id', departmentData.parent_department_id)
          .eq('tenant_id', tenantId)
          .single()

        if (!parentDept) {
          throw new Error('Parent department not found')
        }

        // Check hierarchy depth limit
        if (parentDept.level >= 10) {
          throw new Error('Maximum department hierarchy depth exceeded')
        }
      }

      // Create department
      const { data: department, error } = await supabase
        .from('departments')
        .insert({
          ...departmentData,
          tenant_id: tenantId,
          created_by: createdBy,
        })
        .select()
        .single()

      if (error) {
        throw new Error(`Failed to create department: ${error.message}`)
      }

      return department
    } catch (error) {
      console.error('Error creating department:', error)
      throw error
    }
  }

  async updateDepartment(
    tenantId: string,
    departmentId: string,
    updates: DepartmentUpdate
  ): Promise<Department> {
    try {
      const supabase = await this.getSupabase()

      // Check if department exists
      const { data: existingDept } = await supabase
        .from('departments')
        .select('id, name, code')
        .eq('id', departmentId)
        .eq('tenant_id', tenantId)
        .single()

      if (!existingDept) {
        throw new Error('Department not found')
      }

      // Validate name uniqueness if being updated
      if (updates.name && updates.name !== existingDept.name) {
        const { data: nameConflict } = await supabase
          .from('departments')
          .select('id')
          .eq('tenant_id', tenantId)
          .eq('name', updates.name)
          .neq('id', departmentId)
          .single()

        if (nameConflict) {
          throw new Error(`Department with name '${updates.name}' already exists`)
        }
      }

      // Validate code uniqueness if being updated
      if (updates.code && updates.code !== existingDept.code) {
        const { data: codeConflict } = await supabase
          .from('departments')
          .select('id')
          .eq('tenant_id', tenantId)
          .eq('code', updates.code)
          .neq('id', departmentId)
          .single()

        if (codeConflict) {
          throw new Error(`Department with code '${updates.code}' already exists`)
        }
      }

      // Validate parent department if being updated
      if (updates.parent_department_id) {
        const { data: parentDept } = await supabase
          .from('departments')
          .select('id, level, hierarchy_path')
          .eq('id', updates.parent_department_id)
          .eq('tenant_id', tenantId)
          .single()

        if (!parentDept) {
          throw new Error('Parent department not found')
        }

        // Prevent circular references
        if (parentDept.hierarchy_path?.includes(departmentId)) {
          throw new Error('Circular reference detected in department hierarchy')
        }
      }

      const { data: department, error } = await supabase
        .from('departments')
        .update({
          ...updates,
          updated_at: new Date().toISOString(),
        })
        .eq('id', departmentId)
        .eq('tenant_id', tenantId)
        .select()
        .single()

      if (error) {
        throw new Error(`Failed to update department: ${error.message}`)
      }

      return department
    } catch (error) {
      console.error('Error updating department:', error)
      throw error
    }
  }

  async deleteDepartment(tenantId: string, departmentId: string): Promise<boolean> {
    try {
      const supabase = await this.getSupabase()

      // Check if department exists
      const { data: department } = await supabase
        .from('departments')
        .select('id, name')
        .eq('id', departmentId)
        .eq('tenant_id', tenantId)
        .single()

      if (!department) {
        throw new Error('Department not found')
      }

      // Check if department has users assigned
      const { data: userDepts } = await supabase
        .from('user_departments')
        .select('id')
        .eq('department_id', departmentId)
        .eq('tenant_id', tenantId)
        .limit(1)

      if (userDepts && userDepts.length > 0) {
        throw new Error('Cannot delete department with assigned users')
      }

      // Check if department has child departments
      const { data: childDepts } = await supabase
        .from('departments')
        .select('id')
        .eq('parent_department_id', departmentId)
        .eq('tenant_id', tenantId)
        .limit(1)

      if (childDepts && childDepts.length > 0) {
        throw new Error('Cannot delete department with child departments')
      }

      // Delete department roles first
      await supabase
        .from('department_roles')
        .delete()
        .eq('department_id', departmentId)
        .eq('tenant_id', tenantId)

      // Delete the department
      const { error } = await supabase
        .from('departments')
        .delete()
        .eq('id', departmentId)
        .eq('tenant_id', tenantId)

      if (error) {
        throw new Error(`Failed to delete department: ${error.message}`)
      }

      return true
    } catch (error) {
      console.error('Error deleting department:', error)
      throw error
    }
  }

  async getDepartment(
    tenantId: string,
    departmentId: string
  ): Promise<DepartmentWithHierarchy | null> {
    try {
      const supabase = await this.getSupabase()

      const { data: department } = await supabase
        .from('departments')
        .select(
          `
          *,
          manager:profiles!departments_manager_id_fkey (
            id,
            full_name,
            email
          )
        `
        )
        .eq('id', departmentId)
        .eq('tenant_id', tenantId)
        .single()

      if (!department) {
        return null
      }

      // Get user count
      const { data: userCount } = await supabase
        .from('user_departments')
        .select('id')
        .eq('department_id', departmentId)
        .eq('tenant_id', tenantId)

      // Get department roles
      const { data: deptRoles } = await supabase
        .from('department_roles')
        .select(
          `
          *,
          roles (
            id,
            name,
            display_name,
            description
          )
        `
        )
        .eq('department_id', departmentId)
        .eq('tenant_id', tenantId)

      return {
        ...department,
        user_count: userCount?.length || 0,
        roles: deptRoles?.map(dr => dr.roles).filter(Boolean) || [],
      }
    } catch (error) {
      console.error('Error getting department:', error)
      throw error
    }
  }

  async getDepartments(tenantId: string, includeInactive = false): Promise<Department[]> {
    try {
      const supabase = await this.getSupabase()

      let query = supabase
        .from('departments')
        .select('*')
        .eq('tenant_id', tenantId)
        .order('level', { ascending: true })
        .order('name', { ascending: true })

      if (!includeInactive) {
        query = query.eq('is_active', true)
      }

      const { data: departments, error } = await query

      if (error) {
        throw new Error(`Failed to get departments: ${error.message}`)
      }

      return departments || []
    } catch (error) {
      console.error('Error getting departments:', error)
      throw error
    }
  }

  async getDepartmentHierarchy(tenantId: string): Promise<DepartmentHierarchyNode[]> {
    try {
      const supabase = await this.getSupabase()
      const departments = await this.getDepartments(tenantId)

      // Get user counts for each department
      const { data: userCounts } = await supabase
        .from('user_departments')
        .select('department_id')
        .eq('tenant_id', tenantId)

      const userCountMap =
        userCounts?.reduce(
          (acc, ud) => {
            acc[ud.department_id] = (acc[ud.department_id] || 0) + 1
            return acc
          },
          {} as Record<string, number>
        ) || {}

      // Get roles for each department
      const { data: deptRoles } = await supabase
        .from('department_roles')
        .select(
          `
          department_id,
          roles (
            id,
            name,
            display_name,
            description
          )
        `
        )
        .eq('tenant_id', tenantId)

      const roleMap =
        deptRoles?.reduce(
          (acc, dr) => {
            if (!acc[dr.department_id]) {
              acc[dr.department_id] = []
            }
            if (dr.roles) {
              acc[dr.department_id].push(dr.roles)
            }
            return acc
          },
          {} as Record<string, any[]>
        ) || {}

      // Build hierarchy
      const deptMap = new Map<string, DepartmentHierarchyNode>()
      const rootNodes: DepartmentHierarchyNode[] = []

      // Create nodes
      departments.forEach(dept => {
        const node: DepartmentHierarchyNode = {
          department: dept,
          children: [],
          users: [], // Will be populated if needed
          roles: roleMap[dept.id] || [],
        }
        deptMap.set(dept.id, node)
      })

      // Build hierarchy
      departments.forEach(dept => {
        const node = deptMap.get(dept.id)!
        if (dept.parent_department_id) {
          const parent = deptMap.get(dept.parent_department_id)
          if (parent) {
            parent.children.push(node)
          }
        } else {
          rootNodes.push(node)
        }
      })

      return rootNodes
    } catch (error) {
      console.error('Error getting department hierarchy:', error)
      throw error
    }
  }

  // =====================================================
  // USER-DEPARTMENT ASSIGNMENTS
  // =====================================================

  async assignUserToDepartment(
    tenantId: string,
    userId: string,
    departmentId: string,
    isPrimary = false,
    roleInDepartment?: string,
    assignedBy?: string
  ): Promise<UserDepartment> {
    try {
      const supabase = await this.getSupabase()

      // Validate department exists
      const { data: department } = await supabase
        .from('departments')
        .select('id, is_active')
        .eq('id', departmentId)
        .eq('tenant_id', tenantId)
        .single()

      if (!department) {
        throw new Error('Department not found')
      }

      if (!department.is_active) {
        throw new Error('Cannot assign user to inactive department')
      }

      // Validate user exists in tenant
      const { data: user } = await supabase
        .from('profiles')
        .select('id')
        .eq('id', userId)
        .eq('tenant_id', tenantId)
        .single()

      if (!user) {
        throw new Error('User not found in tenant')
      }

      // If setting as primary, unset other primary assignments
      if (isPrimary) {
        await supabase
          .from('user_departments')
          .update({ is_primary: false })
          .eq('user_id', userId)
          .eq('tenant_id', tenantId)
      }

      const { data: assignment, error } = await supabase
        .from('user_departments')
        .insert({
          tenant_id: tenantId,
          user_id: userId,
          department_id: departmentId,
          is_primary: isPrimary,
          role_in_department: roleInDepartment,
          assigned_by: assignedBy || userId,
        })
        .select()
        .single()

      if (error) {
        if (error.code === '23505') {
          // Unique constraint violation
          // Update existing assignment
          const { data: updated, error: updateError } = await supabase
            .from('user_departments')
            .update({
              is_primary: isPrimary,
              role_in_department: roleInDepartment,
              assigned_by: assignedBy || userId,
              assigned_at: new Date().toISOString(),
            })
            .eq('tenant_id', tenantId)
            .eq('user_id', userId)
            .eq('department_id', departmentId)
            .select()
            .single()

          if (updateError) {
            throw new Error(`Failed to update department assignment: ${updateError.message}`)
          }

          return updated
        }

        throw new Error(`Failed to assign user to department: ${error.message}`)
      }

      return assignment
    } catch (error) {
      console.error('Error assigning user to department:', error)
      throw error
    }
  }

  async removeUserFromDepartment(
    tenantId: string,
    userId: string,
    departmentId: string
  ): Promise<boolean> {
    try {
      const supabase = await this.getSupabase()

      const { error } = await supabase
        .from('user_departments')
        .delete()
        .eq('tenant_id', tenantId)
        .eq('user_id', userId)
        .eq('department_id', departmentId)

      if (error) {
        throw new Error(`Failed to remove user from department: ${error.message}`)
      }

      return true
    } catch (error) {
      console.error('Error removing user from department:', error)
      throw error
    }
  }

  async getUserDepartments(
    tenantId: string,
    userId: string
  ): Promise<(Department & { is_primary: boolean; role_in_department?: string })[]> {
    try {
      const supabase = await this.getSupabase()

      const { data: userDepts, error } = await supabase
        .from('user_departments')
        .select(
          `
          is_primary,
          role_in_department,
          departments (
            id,
            name,
            display_name,
            description,
            code,
            department_type,
            level,
            hierarchy_path,
            is_active
          )
        `
        )
        .eq('tenant_id', tenantId)
        .eq('user_id', userId)
        .order('is_primary', { ascending: false })

      if (error) {
        throw new Error(`Failed to get user departments: ${error.message}`)
      }

      return (
        userDepts?.map((ud: any) => ({
          ...ud.departments,
          is_primary: ud.is_primary,
          role_in_department: ud.role_in_department,
        })) || []
      )
    } catch (error) {
      console.error('Error getting user departments:', error)
      throw error
    }
  }

  async getDepartmentUsers(tenantId: string, departmentId: string): Promise<any[]> {
    try {
      const supabase = await this.getSupabase()

      const { data: deptUsers, error } = await supabase
        .from('user_departments')
        .select(
          `
          is_primary,
          role_in_department,
          assigned_at,
          profiles (
            id,
            email,
            full_name,
            avatar_url,
            job_title
          )
        `
        )
        .eq('tenant_id', tenantId)
        .eq('department_id', departmentId)
        .order('is_primary', { ascending: false })
        .order('assigned_at', { ascending: false })

      if (error) {
        throw new Error(`Failed to get department users: ${error.message}`)
      }

      return (
        deptUsers?.map((du: any) => ({
          ...du.profiles,
          is_primary: du.is_primary,
          role_in_department: du.role_in_department,
          assigned_at: du.assigned_at,
        })) || []
      )
    } catch (error) {
      console.error('Error getting department users:', error)
      throw error
    }
  }

  // =====================================================
  // DEPARTMENT ROLES
  // =====================================================

  async assignRoleToDepartment(
    tenantId: string,
    departmentId: string,
    roleId: string,
    isDefaultRole = false,
    maxUsers?: number,
    createdBy?: string
  ): Promise<DepartmentRole> {
    try {
      const supabase = await this.getSupabase()

      const { data: deptRole, error } = await supabase
        .from('department_roles')
        .insert({
          tenant_id: tenantId,
          department_id: departmentId,
          role_id: roleId,
          is_default_role: isDefaultRole,
          max_users: maxUsers,
          created_by: createdBy || 'system',
        })
        .select()
        .single()

      if (error) {
        if (error.code === '23505') {
          throw new Error('Role is already assigned to this department')
        }
        throw new Error(`Failed to assign role to department: ${error.message}`)
      }

      return deptRole
    } catch (error) {
      console.error('Error assigning role to department:', error)
      throw error
    }
  }

  async removeRoleFromDepartment(
    tenantId: string,
    departmentId: string,
    roleId: string
  ): Promise<boolean> {
    try {
      const supabase = await this.getSupabase()

      const { error } = await supabase
        .from('department_roles')
        .delete()
        .eq('tenant_id', tenantId)
        .eq('department_id', departmentId)
        .eq('role_id', roleId)

      if (error) {
        throw new Error(`Failed to remove role from department: ${error.message}`)
      }

      return true
    } catch (error) {
      console.error('Error removing role from department:', error)
      throw error
    }
  }

  // =====================================================
  // ANALYTICS AND REPORTING
  // =====================================================

  async getDepartmentAnalytics(
    tenantId: string,
    departmentId: string,
    days = 30
  ): Promise<DepartmentAnalytics> {
    try {
      const supabase = await this.getSupabase()
      const department = await this.getDepartment(tenantId, departmentId)
      if (!department) {
        throw new Error('Department not found')
      }

      // Get user count
      const users = await this.getDepartmentUsers(tenantId, departmentId)
      const userCount = users.length

      // Get role distribution
      const { data: roleDistribution } = await supabase
        .from('user_roles')
        .select(
          `
          role_id,
          roles (name, display_name)
        `
        )
        .eq('tenant_id', tenantId)
        .eq('is_active', true)
        .in(
          'user_id',
          users.map(u => u.id)
        )

      const roleDistMap =
        roleDistribution?.reduce(
          (acc: Record<string, number>, ur: any) => {
            const roleName = ur.roles?.display_name || ur.roles?.name || 'Unknown'
            acc[roleName] = (acc[roleName] || 0) + 1
            return acc
          },
          {} as Record<string, number>
        ) || {}

      const roleDistributionArray = Object.entries(roleDistMap).map(([role_name, user_count]) => ({
        role_name,
        user_count,
      }))

      // Get permission usage (placeholder - would need actual implementation)
      const permissionUsage = [
        { permission_name: 'read:asset', usage_count: 150 },
        { permission_name: 'create:asset', usage_count: 45 },
        { permission_name: 'update:asset', usage_count: 89 },
      ]

      return {
        department_id: departmentId,
        department_name: department.name,
        user_count: userCount,
        role_distribution: roleDistributionArray,
        permission_usage: permissionUsage,
      }
    } catch (error) {
      console.error('Error getting department analytics:', error)
      throw error
    }
  }

  // =====================================================
  // UTILITY METHODS
  // =====================================================

  async validateDepartmentHierarchy(tenantId: string): Promise<boolean> {
    try {
      const supabase = await this.getSupabase()

      // Check for circular references
      const { data: departments } = await supabase
        .from('departments')
        .select('id, parent_department_id, hierarchy_path')
        .eq('tenant_id', tenantId)

      if (!departments) return true

      for (const dept of departments) {
        if (dept.parent_department_id && dept.hierarchy_path?.includes(dept.id)) {
          throw new Error(`Circular reference detected for department ${dept.id}`)
        }
      }

      return true
    } catch (error) {
      console.error('Error validating department hierarchy:', error)
      throw error
    }
  }

  async searchDepartments(tenantId: string, query: string, limit = 10): Promise<Department[]> {
    try {
      const supabase = await this.getSupabase()

      const { data: departments, error } = await supabase
        .from('departments')
        .select('*')
        .eq('tenant_id', tenantId)
        .eq('is_active', true)
        .or(`name.ilike.%${query}%,display_name.ilike.%${query}%,code.ilike.%${query}%`)
        .limit(limit)
        .order('name')

      if (error) {
        throw new Error(`Failed to search departments: ${error.message}`)
      }

      return departments || []
    } catch (error) {
      console.error('Error searching departments:', error)
      throw error
    }
  }
}

/**
 * Enhanced Redis Service for AssetTrackerPro
 * Provides advanced Redis operations with fallback mechanisms
 */

import { redis, hasRedis, redisUtils } from '@/lib/config/redis'
import { Ratelimit } from '@upstash/ratelimit'

export class EnhancedRedisService {
  private static instance: EnhancedRedisService

  private constructor() {}

  public static getInstance(): EnhancedRedisService {
    if (!EnhancedRedisService.instance) {
      EnhancedRedisService.instance = new EnhancedRedisService()
    }
    return EnhancedRedisService.instance
  }

  /**
   * Check if Redis is available
   */
  public isAvailable(): boolean {
    return hasRedis && redis !== null
  }

  /**
   * Cache management
   */
  public async cache<T>(
    key: string,
    fetchFunction: () => Promise<T>,
    ttlSeconds: number = 300
  ): Promise<T> {
    if (!this.isAvailable()) {
      return await fetchFunction()
    }

    try {
      // Try to get from cache first
      const cached = await redisUtils.get(key)
      if (cached !== null) {
        return typeof cached === 'string' ? JSON.parse(cached) : cached
      }

      // Fetch fresh data
      const freshData = await fetchFunction()

      // Cache the result
      await redisUtils.set(key, JSON.stringify(freshData), ttlSeconds)

      return freshData
    } catch (error) {
      console.error('Cache operation failed:', error)
      return await fetchFunction()
    }
  }

  /**
   * Session management
   */
  public async setSession(
    sessionId: string,
    data: any,
    ttlSeconds: number = 3600
  ): Promise<boolean> {
    const key = `session:${sessionId}`
    return await redisUtils.set(key, JSON.stringify(data), ttlSeconds)
  }

  public async getSession(sessionId: string): Promise<any> {
    const key = `session:${sessionId}`
    const data = await redisUtils.get(key)
    return data ? JSON.parse(data) : null
  }

  public async deleteSession(sessionId: string): Promise<boolean> {
    const key = `session:${sessionId}`
    return await redisUtils.del(key)
  }

  /**
   * Rate limiting
   */
  public async checkRateLimit(
    identifier: string,
    limit: number = 60,
    windowSeconds: number = 60
  ): Promise<{ allowed: boolean; remaining: number; resetTime: number }> {
    if (!this.isAvailable()) {
      return { allowed: true, remaining: limit - 1, resetTime: Date.now() + windowSeconds * 1000 }
    }

    try {
      const ratelimit = new Ratelimit({
        redis: redis!,
        limiter: Ratelimit.fixedWindow(limit, `${windowSeconds}s`),
        analytics: true,
      })

      const result = await ratelimit.limit(identifier)

      return {
        allowed: result.success,
        remaining: result.remaining,
        resetTime: result.reset,
      }
    } catch (error) {
      console.error('Rate limit check failed:', error)
      return { allowed: true, remaining: limit - 1, resetTime: Date.now() + windowSeconds * 1000 }
    }
  }

  /**
   * Distributed locking
   */
  public async acquireLock(
    lockKey: string,
    ttlSeconds: number = 30,
    retryAttempts: number = 3
  ): Promise<string | null> {
    if (!this.isAvailable()) {
      return `fallback-lock-${Date.now()}`
    }

    const lockValue = `lock:${Date.now()}:${Math.random()}`
    const key = `lock:${lockKey}`

    for (let attempt = 0; attempt < retryAttempts; attempt++) {
      try {
        const result = await redis!.set(key, lockValue, { ex: ttlSeconds, nx: true })
        if (result === 'OK') {
          return lockValue
        }

        // Wait before retry
        await new Promise(resolve => setTimeout(resolve, 100 * (attempt + 1)))
      } catch (error) {
        console.error(`Lock acquisition attempt ${attempt + 1} failed:`, error)
      }
    }

    return null
  }

  public async releaseLock(lockKey: string, lockValue: string): Promise<boolean> {
    if (!this.isAvailable()) {
      return true // Fallback always succeeds
    }

    try {
      const key = `lock:${lockKey}`
      const script = `
        if redis.call("get", KEYS[1]) == ARGV[1] then
          return redis.call("del", KEYS[1])
        else
          return 0
        end
      `

      const result = await redis!.eval(script, [key], [lockValue])
      return result === 1
    } catch (error) {
      console.error('Lock release failed:', error)
      return false
    }
  }

  /**
   * Pub/Sub messaging
   */
  public async publish(channel: string, message: any): Promise<boolean> {
    if (!this.isAvailable()) {
      console.warn('Redis not available, message not published:', { channel, message })
      return false
    }

    try {
      await redis!.publish(channel, JSON.stringify(message))
      return true
    } catch (error) {
      console.error('Publish failed:', error)
      return false
    }
  }

  /**
   * Analytics and metrics
   */
  public async incrementCounter(
    key: string,
    increment: number = 1,
    ttlSeconds?: number
  ): Promise<number> {
    if (!this.isAvailable()) {
      return increment
    }

    try {
      const result = await redis!.incrby(key, increment)
      if (ttlSeconds && result === increment) {
        await redis!.expire(key, ttlSeconds)
      }
      return result
    } catch (error) {
      console.error('Counter increment failed:', error)
      return increment
    }
  }

  public async getCounter(key: string): Promise<number> {
    const value = await redisUtils.get(key, 0)
    return typeof value === 'number' ? value : parseInt(value) || 0
  }

  /**
   * Health check
   */
  public async healthCheck(): Promise<{
    status: 'healthy' | 'unhealthy' | 'unavailable'
    latency?: number
    error?: string
  }> {
    if (!this.isAvailable()) {
      return { status: 'unavailable' }
    }

    try {
      const start = Date.now()
      await redis!.ping()
      const latency = Date.now() - start

      return {
        status: 'healthy',
        latency,
      }
    } catch (error) {
      return {
        status: 'unhealthy',
        error: error instanceof Error ? error.message : 'Unknown error',
      }
    }
  }

  /**
   * Cleanup expired keys (maintenance)
   */
  public async cleanup(pattern: string = '*'): Promise<number> {
    if (!this.isAvailable()) {
      return 0
    }

    try {
      // Note: SCAN is more efficient than KEYS for large datasets
      const keys = await redis!.keys(pattern)
      if (keys.length === 0) return 0

      await redis!.del(...keys)
      return keys.length
    } catch (error) {
      console.error('Cleanup failed:', error)
      return 0
    }
  }
}

// Export singleton instance
export const enhancedRedisService = EnhancedRedisService.getInstance()
export default enhancedRedisService

// =====================================================
// MULTI-FACTOR AUTHENTICATION SERVICE
// =====================================================
// Service for managing MFA methods, verification, and security

import { createClient } from '@/lib/supabase/server'
import {
  MfaMethod,
  MfaMethodInsert,
  MfaMethodUpdate,
  MfaVerificationAttempt,
  SecurityEventInsert,
} from '@/lib/types/database'
import { authenticator } from 'otplib'
import crypto from 'crypto'

export interface MfaSetupResult {
  success: boolean
  method?: MfaMethod
  secret?: string
  qrCode?: string
  backupCodes?: string[]
  error?: string
}

export interface MfaVerificationResult {
  success: boolean
  method?: MfaMethod
  requiresBackup?: boolean
  error?: string
}

export interface MfaStatusResult {
  isEnabled: boolean
  methods: MfaMethod[]
  primaryMethod?: MfaMethod
  backupCodesRemaining: number
}

export class MfaService {
  private async getSupabase() {
    return createClient()
  }

  // =====================================================
  // MFA SETUP AND MANAGEMENT
  // =====================================================

  /**
   * Set up TOTP (Time-based One-Time Password) authentication
   */
  async setupTOTP(
    tenantId: string,
    userId: string,
    methodName: string,
    userEmail: string
  ): Promise<MfaSetupResult> {
    try {
      const supabase = await this.getSupabase()

      // Generate TOTP secret
      const secret = authenticator.generateSecret()
      const serviceName = 'AssetTracker Pro'
      const otpAuthUrl = authenticator.keyuri(userEmail, serviceName, secret)

      // Encrypt the secret
      const encryptedSecret = this.encryptSecret(secret)

      // Generate backup codes
      const backupCodes = this.generateBackupCodes()
      const encryptedBackupCodes = backupCodes.map(code => this.encryptSecret(code))

      // Create MFA method record
      const methodData: MfaMethodInsert = {
        tenant_id: tenantId,
        user_id: userId,
        method_type: 'totp',
        method_name: methodName,
        secret_encrypted: encryptedSecret,
        backup_codes: encryptedBackupCodes,
        is_verified: false,
        is_primary: false,
      }

      const { data: method, error } = await supabase
        .from('mfa_methods')
        .insert(methodData)
        .select()
        .single()

      if (error) {
        console.error('Error creating TOTP method:', error)
        return { success: false, error: error.message }
      }

      // Generate QR code data URL
      const qrCode = await this.generateQRCode(otpAuthUrl)

      return {
        success: true,
